INSERT INTO `manual_contents_desc` (`desc_id`, `manual_contents_id`, `title`, `content`, `language`) VALUES
(37, 36, 'FORM-AUTHORIZATION TO RELEASE PERSONAL INFORMATION', '<p>\r\n	I, ______________________ [Employee&rsquo;s Name], hereby authorize this residence to release personal information listed</p>\r\n<p>\r\n	below to ______________________ [Third Party].</p>\r\n<p>\r\n	Choose whichever apply:</p>\r\n<ul>\r\n	<li>\r\n		Confirmation of employment and salary information (usually used for banking/lending purposes).</li>\r\n	<li>\r\n		&nbsp;All employment information in company&rsquo;s possession, including dates of hire, positions held, information relating to performance and attendance (both objective and subjective), salary information and all related matters (usually used for reference checking purposes).</li>\r\n	<li>\r\n		Any medical information in the company&rsquo;s possession (including absences, medical notes or reports (usually used for sick leave, disability benefits or health insurance).</li>\r\n	<li>\r\n		Other (specify information to be released):</li>\r\n</ul>\r\n<p>\r\n	<br />\r\n	&nbsp;</p>\r\n<p>\r\n	I hereby release and discharge the residence from any claim whatsoever relating to the residence acting in accordance with this Authorization.</p>\r\n<p>\r\n	Date</p>\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td class=\"employee-sign\">\r\n				<br />\r\n				<p>\r\n					Employee&rsquo;s Signature</p>\r\n			</td>\r\n			<td class=\"employee-sign1\">\r\n				&nbsp;<br />\r\n				<p>\r\n					Date</p>\r\n			</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', 'en'),
(38, 37, 'FORM-ADMISSION AGREEMENT [YOUR ORGANIZATION] & THE APPLICANT', '<ol>\r\n	<li>\r\n		To furnish room, provide linens, bedding, towels, facecloths and such personal items as may be required for the health, safety, and well-being of the resident.</li>\r\n	<li>\r\n		To provide food, cable television service and cleaning services for each resident.</li>\r\n	<li>\r\n		To inform the responsible party of the need to obtain the services of a licensed physician and/or Extra Mural Services as deemed necessary. Also, to ensure all necessary medications are ordered and received as per the physicians order.</li>\r\n	<li>\r\n		To arrange for transfer of the Resident, at their cost, to the hospital when this is deemed necessary, or ordered by the attending physician, and to immediately notify the responsible party of such transfer.</li>\r\n</ol>\r\n<p>\r\n	<strong>AGREEMENT OF APPLICANT OR RESPONSIBLE PERSON</strong></p>\r\n<ol>\r\n	<li>\r\n		To provide such personal clothing and effects as needed or desired by the Resident.</li>\r\n	<li>\r\n		To be responsible for the drug bills for medications not covered by the Prescription Drug Program and other treatments or aids ordered by the physician.</li>\r\n	<li>\r\n		To be responsible for hospital charges if hospitalization of the Resident becomes necessary.</li>\r\n	<li>\r\n		To pay the basic rate agreed upon with the Home at specific intervals. The basic rate is presently $80 per day. The monthly total of rent will be due on the first of every month, in advance, and post dated cheques are to be provided for a minimum of three months at a time. The Home has the right, from time to time, to increase the basic rate upon written notification to the Resident and Responsible Person.</li>\r\n	<li>\r\n		The Responsible Person acknowledges that if the Resident is unable to pay the basic rate, and other charges which may be incurred by or on behalf of the Resident to the Home, then the Responsible Person is obligated to pay such charges or arrange for alternate payment. Failure of the Responsible Person to pay such charges or make alternate arrangements may mean that the Home may commence action against the Responsible Person to recover the basic rate and other charges and in addition may require the Resident to vacate the home.</li>\r\n	<li>\r\n		To conform to all the rules and regulations of the Home as from time to time are issued.</li>\r\n	<br />\r\n</ol>\r\n<p style=\"text-align: center;\">\r\n	<strong>STANDARD ADMISSION WAIVER</strong></p>\r\n<ol>\r\n	<li>\r\n		The Management of this Home has agreed to exercise such reasonable care toward this person as his or her known condition may require, however, this Home is in no sense an insurer of his or her safety or welfare and assumes no liability as such.</li>\r\n	<li>\r\n		The Management of this Home will not be responsible for any valuables or money left in the possession of this person while he or she is a resident of this Home.</li>\r\n	<li>\r\n		The Management of this Home will not be responsible for Residents when absent from the Home for whatever reason.</li>\r\n	<li>\r\n		The Management of this Home will not force any Resident to remain in the Home against his or her will for any length of time.<br />\r\n		<hr />\r\n		<p>\r\n			&nbsp;</p>\r\n	</li>\r\n</ol>\r\n<p style=\"text-align: center;\">\r\n	<strong>DURATION OF AGREEMENT</strong></p>\r\n<p>\r\n	Either party may terminate this agreement on twenty (20) days written notice. Otherwise it will remain in effect until a different agreement is executed.</p>\r\n<p>\r\n	<br />\r\n	(Signature of Operator)</p>\r\n<p class=\"date\">\r\n	Date</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<br />\r\n	(Signature of Resident)</p>\r\n<p>\r\n	Date</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<br />\r\n	(Signature of Responsible Person) Date<br />\r\n	<br />\r\n	**Note: Application not valid unless signed.<br />\r\n	&nbsp;</p>\r\n', 'en'),
(39, 38, 'FORM-ORIENTATION CHECK CHART', '<table border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:100%; border-collapse:collapse;\">\r\n	<tbody>\r\n		<tr>\r\n			<td colspan=\"4\" style=\"text-align: center;\">\r\n				<strong>ATTATCHMENT A</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"4\" style=\"text-align: center;\">\r\n				<strong>ORIENTATION CHECK CHART</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Employee:</td>\r\n			<td class=\"forms\">\r\n				&nbsp;</td>\r\n			<td>\r\n				Date:</td>\r\n			<td class=\"forms\">\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"4\">\r\n				<strong>DAY 1</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"4\">\r\n				VERIFY AND/OR RECORD THE FOLLOWING EMPLOYEE DATA</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td colspan=\"2\">\r\n				Full name, address, telephone number, birth date, SIN, marital status</td>\r\n			<td style=\"text-align: right;\">\r\n				<input type=\"checkbox\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td colspan=\"2\">\r\n				Who to notify in emergencies and telephone number</td>\r\n			<td style=\"text-align: right;\">\r\n				<input type=\"checkbox\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"3\">\r\n				EXPLAIN AND OBTAIN COMPLETED FORMS FOR THE FOLLOWING (if appropriate)</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td colspan=\"2\">\r\n				TD1 Form (Income Tax)</td>\r\n			<td style=\"text-align: right;\">\r\n				<input type=\"checkbox\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td colspan=\"2\">\r\n				Benefit programs enrolment forms (Group Life, AD&amp;D, Health, Dental, etc.)</td>\r\n			<td style=\"text-align: right;\">\r\n				<input type=\"checkbox\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"3\">\r\n				REVIEW, ISSUE, AND/OR DESCRIBE</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td colspan=\"2\">\r\n				Method of paying wages (when and where)</td>\r\n			<td style=\"text-align: right;\">\r\n				<input type=\"checkbox\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td colspan=\"2\">\r\n				Vacation policies</td>\r\n			<td style=\"text-align: right;\">\r\n				<input type=\"checkbox\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td colspan=\"2\">\r\n				Holidays observed</td>\r\n			<td style=\"text-align: right;\">\r\n				<input type=\"checkbox\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td colspan=\"2\">\r\n				Health and safety policy and procedures</td>\r\n			<td style=\"text-align: right;\">\r\n				<input type=\"checkbox\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td colspan=\"2\">\r\n				Review/provide safety rules, WHIMIS requirements, personal protective safety devices</td>\r\n			<td style=\"text-align: right;\">\r\n				<input type=\"checkbox\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td colspan=\"2\">\r\n				Conduct and behaviour standards</td>\r\n			<td style=\"text-align: right;\">\r\n				<input type=\"checkbox\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td colspan=\"2\">\r\n				Complaint procedures</td>\r\n			<td style=\"text-align: right;\">\r\n				<input type=\"checkbox\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td colspan=\"2\">\r\n				Confidentiality agreement and/or other agreements</td>\r\n			<td style=\"text-align: right;\">\r\n				<input type=\"checkbox\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"3\">\r\n				PROVIDE THE FOLLOWING ADDITIONAL INFORMATION</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td colspan=\"2\">\r\n				Job responsibility record and job performance standards</td>\r\n			<td style=\"text-align: right;\">\r\n				<input type=\"checkbox\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td colspan=\"2\">\r\n				Tour of facilities and department</td>\r\n			<td style=\"text-align: right;\">\r\n				<input type=\"checkbox\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td colspan=\"2\">\r\n				Show location of First Aid stations, lunchrooms, restrooms, time clocks (if any), bulletin boards, etc.</td>\r\n			<td style=\"text-align: right;\">\r\n				<input type=\"checkbox\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td colspan=\"2\">\r\n				Review timing of and procedures for lunch and rest periods</td>\r\n			<td style=\"text-align: right;\">\r\n				<input type=\"checkbox\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td colspan=\"2\">\r\n				Issue and/or review: telephone lists, plant/office layouts, organization charts, etc.</td>\r\n			<td style=\"text-align: right;\">\r\n				<input type=\"checkbox\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td colspan=\"2\">\r\n				Provide list of co-workers&rsquo; names and job titles; introduce to co-workers</td>\r\n			<td style=\"text-align: right;\">\r\n				<input type=\"checkbox\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td colspan=\"2\">\r\n				Notify reception of name and department location of new hire</td>\r\n			<td style=\"text-align: right;\">\r\n				<input type=\"checkbox\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"3\">\r\n				<strong>DAY 2 ONWARDS</strong></td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td colspan=\"2\">\r\n				Review important company policy/practices, quality standards, job performance standards, complaint procedures, accident reporting, training an development opportunities, special departmental procedures, if any</td>\r\n			<td style=\"text-align: right;\">\r\n				<input type=\"checkbox\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td colspan=\"2\">\r\n				Explain employee benefit program details, enrolment, effective dates</td>\r\n			<td style=\"text-align: right;\">\r\n				<input type=\"checkbox\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"3\">\r\n				<strong>DAILY</strong></td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td colspan=\"2\">\r\n				Briefly review employee&rsquo;s progress</td>\r\n			<td style=\"text-align: right;\">\r\n				<input type=\"checkbox\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td colspan=\"2\">\r\n				Discuss and resolve any employee concerns</td>\r\n			<td style=\"text-align: right;\">\r\n				<input type=\"checkbox\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td colspan=\"2\">\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Completed by:</td>\r\n			<td class=\"forms\">\r\n				&nbsp;</td>\r\n			<td>\r\n				Date:</td>\r\n			<td class=\"forms\">\r\n				&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', 'en'),
(40, 39, 'FORM-JOB DESCRIPTION FORM — Page 1 of 4', '<table align=\"center\" border=\"1\" cellpadding=\"1\" cellspacing=\"1\" class=\"job-form jod-desc\">\r\n	<tbody>\r\n		<tr>\r\n			<td colspan=\"2\" style=\"text-align: center;\">\r\n				<strong>JOB DESCRIPTION FORM &mdash; Page 1 of 4</strong></td>\r\n		</tr>\r\n		<tr class=\"border\">\r\n			<td>\r\n				Job Title:</td>\r\n			<td>\r\n				Department:</td>\r\n		</tr>\r\n		<tr class=\"border\">\r\n			<td>\r\n				Date Prepared:</td>\r\n			<td>\r\n				Prepared by:</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p style=\"text-align: center;\">\r\n	<strong>Organizational Relationship</strong><br />\r\n	(Show the organizational position of this job by indicating the title of the job to which this job reports and<br />\r\n	the titles of any jobs reporting directly to this job.)</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<table align=\"center\" border=\"1\" cellpadding=\"1\" cellspacing=\"1\" class=\"job-form1\" style=\"width: 250px; height: 100px\">\r\n	<tbody>\r\n		<tr>\r\n			<td class=\"title-mgr\" style=\"text-align: center; vertical-align: top;\">\r\n				Title of manager</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table align=\"center\" border=\"1\" cellpadding=\"1\" cellspacing=\"1\" class=\"job-form2\" style=\"width: 250px; height: 100px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"text-align: center; vertical-align: top;\">\r\n				Title of this job</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table align=\"center\" border=\"1\" cellpadding=\"1\" cellspacing=\"1\" class=\"job-form1\" style=\"width: 250px; height: 100px\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"text-align: center; vertical-align: top;\">\r\n				List titles of direct reports (if any)</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', 'en'),
(41, 40, 'FORM-JOB DESCRIPTION FORM — Page 2 of 4', '<p>\r\n	&nbsp;</p>\r\n<table border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"border-collapse: collapse;width: 600px; \">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width: 70%;\">\r\n				Job Title:</td>\r\n			<td>\r\n				Department:</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\" style=\"text-align: center;\">\r\n				<strong>Essential Duties and Responsibilities</strong><br />\r\n				<br />\r\n				List, in order of importance, the essential duties and responsibilities of this job and<br />\r\n				the approximate percentage of time spent per week on each duty and/or responsibility.</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height: 50px; text-align: left; vertical-align: top; width: 80%;\">\r\n				1.</td>\r\n			<td style=\"text-align: center; vertical-align: top;\">\r\n				% of time</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height: 50px; text-align: left; vertical-align: top;\">\r\n				2.</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height: 50px; text-align: left; vertical-align: top;\">\r\n				3.</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height: 50px; text-align: left; vertical-align: top;\">\r\n				4.</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height: 50px; text-align: left; vertical-align: top;\">\r\n				5.</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height: 50px; text-align: left; vertical-align: top;\">\r\n				6.</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height: 50px; text-align: left; vertical-align: top;\">\r\n				7.</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height: 50px; text-align: left; vertical-align: top;\">\r\n				8.</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', 'en'),
(42, 41, 'FORM-JOB DESCRIPTION FORM — Page 3 of 4', '<table border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"border-collapse:collapse;width: 100%; height: 200px\">\r\n	<tbody>\r\n		<tr>\r\n			<td colspan=\"2\" style=\"text-align: center; height: 80px;\">\r\n				<strong>JOB DESCRIPTION FORM &mdash; Page 3 of 4</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width: 70%;\">\r\n				Job Title:</td>\r\n			<td>\r\n				Department:</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\" style=\"text-align: center;\">\r\n				<strong>Job Specifications</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\" style=\"text-align: left; vertical-align: top; height: 80px;\">\r\n				<strong>EDUCATION/EXPERIENCE</strong> &mdash; Describe required academic, technical, business education required and previous work experience necessary to be able to competently accomplish duties and responsibilities.</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\" style=\"text-align: left; vertical-align: top; height: 80px;\">\r\n				<strong>COMPLEXITY/ANALYSES </strong>&mdash; Describe the difficulty of job duties, type of work, ingenuity, creativity, analyses, problem identification, problem solving, and/or interpretation required.</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\" style=\"text-align: left; vertical-align: top; height: 80px;\">\r\n				<strong>DIRECTION RECEIVED</strong> &mdash; Describe amount of supervision expected or required from superior or others, and the extent of use of written instructions and/or procedures.</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\" style=\"text-align: left; vertical-align: top; height: 80px;\">\r\n				<strong>DECISION MAKING</strong> &mdash; Describe judgement exercised when making decisions and impact of decisions on financial, organization, customer relations effectiveness, precedent decisions.</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\" style=\"text-align: left; vertical-align: top; height: 80px;\">\r\n				<strong>ACCOUNTABILITY</strong> &mdash; Describe in terms of prime, shared, or minimum accountability for actions or decisions related to cost/revenue, implications due to error, responsibility for inspection and/or verification of work of others.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', 'en'),
(43, 42, 'FORM-JOB DESCRIPTION FORM — Page 4 of 4', '<table border=\"1\" cellpadding=\"1\" cellspacing=\"1\" style=\"border-collapse:collapse;width: 100%; height: 200px\">\r\n	<tbody>\r\n		<tr>\r\n			<td colspan=\"2\" style=\"text-align: center; height: 80px;\">\r\n				<strong>JOB DESCRIPTION FORM &mdash; Page 4 of 4</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width: 70%;\">\r\n				Job Title:</td>\r\n			<td>\r\n				Department:</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\" style=\"text-align: center;\">\r\n				<strong>Job Specifications</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\" style=\"text-align: left; vertical-align: top; height: 80px;\">\r\n				<strong>RELATIONSHIPS</strong> &mdash; Describe frequency and type of interactions with others, both within and outside the organization and the typical position of people most frequently contacted.</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\" style=\"text-align: left; vertical-align: top; height: 80px;\">\r\n				<strong>COORDINATION</strong> &mdash; Describe type of assistance or coordination given to others and/or the extent of scheduling work of others.</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\" style=\"text-align: left; vertical-align: top; height: 70px;\">\r\n				<strong>FINANCIAL IMPLICATIONS</strong> &mdash; Describe size of budget, responsibility for expense, capital, revenue control, responsibility for assets and effect on product/service pricing.</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\" style=\"text-align: left; vertical-align: top; height: 80px;\">\r\n				<strong>SUPERVISION/MANAGEMENT</strong> &mdash; Describe the extent to which policies and/or guidelines are developed, or interpreted, number of people and types of skills supervised or managed.</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\" style=\"text-align: left; vertical-align: top; height: 80px;\">\r\n				<strong>WORKING CONDITIONS</strong> &mdash; Describe the work environment, physical characteristics of the work environment such as heat, cold, dust, use of chemicals, travel, and if 24-hour on-call duty is involved.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', 'en'),
(44, 43, 'FORM-REFERENCE CHECK QUESTIONS', '<table border=\"1\" cellpadding=\"1\" cellspacing=\"1\" class=\"border-collapse:collapse;\" style=\"width: 100%\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"height: 70px; text-align: left; vertical-align: top;\">\r\n				1.Can you confirm that candidate worked for your organization from [<strong>date</strong>] to [<strong>date</strong>], in the capacity of <strong>[job title</strong>]?</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: left; vertical-align: top; height: 70px;\">\r\n				2.What were candidate&rsquo;s job responsibilities?</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: left; vertical-align: top; height: 70px;\">\r\n				3. What were candidate&rsquo;s accomplishments while in that position?</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: left; vertical-align: top; height: 70px;\">\r\n				4.Did the candidate hold any other positions while in the employ of your organization? Can you describe them and the major accomplishments achieved while in those positions?</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: left; vertical-align: top; height: 70px;\">\r\n				5.How would you describe candidate&rsquo;s work ethic? Was attendance regular and punctual?</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: left; vertical-align: top; height: 70px;\">\r\n				6.In your opinion, what are candidate&rsquo;s strengths and what areas needed improvement?</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: left; vertical-align: top; height: 70px;\">\r\n				7.How would you describe candidate&rsquo;s motivation and initiative? Can you give an example?</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: left; vertical-align: top; height: 70px;\">\r\n				8.Did candidate show leadership in the organization? Can you give an example?</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: left; vertical-align: top; height: 70px;\">\r\n				9.How did the candidate work with colleagues? Was s/he a team player? Examples?</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: left; vertical-align: top; height: 70px;\">\r\n				10.How would you describe the candidate&rsquo;s communication skills? Examples?</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: left; vertical-align: top; height: 70px;\">\r\n				11.Why did the candidate leave your organization?</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: left; vertical-align: top; height: 70px;\">\r\n				12. Would you rehire the candidate and in what capacity? Why or why not?</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"text-align: left; vertical-align: top; height: 70px;\">\r\n				13. What was the relationship of the person providing the reference to the candidate?</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', 'en'),
(87, 86, 'Form-ORIENTATION GUIDE (PSW) - MEDICATIONS', '<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" style=\"width: 500px\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				Employee Name:</td>\r\n			<td class=\"employee0\">\r\n				&nbsp;</td>\r\n			<td>\r\n				Training Date:</td>\r\n			<td class=\"employee01\">\r\n				&nbsp;</td>\r\n			<td>\r\n				20</td>\r\n			<td class=\"emp02\">\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Name of Trainer:</td>\r\n			<td class=\"employee03\">\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"1\" cellpadding=\"1\" cellspacing=\"1\" class=\"table-medication\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				Employee</td>\r\n			<td>\r\n				Trainer</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Only PSW Medications staff can administer medications</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Follow the policies and procedures for administering medications</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<span avenir=\"\" data-doc-id=\"1067983000000026001\" data-doc-type=\"writer\" font-size:=\"\" lt=\"\" next=\"\" style=\"font-family: \">Distribution of the medications with the bubble packs</span></td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Medications must be prescribed by a doctor</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Administering medications-Oral (mouth)</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Administering medication- Nasal (nose)</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<