INSERT INTO `manual_policies_desc` (`desc_id`, `manual_policies_id`, `title`, `content`, `language`) VALUES
(5, 4, 'Oath of Confidentiality', '<ul class=\"title\">\r\n	<li>\r\n		All employees and volunteers are required to sign the oath of confidentiality.</li>\r\n	<li>\r\n		Violations of this oath can include discipline, up to and including termination.</li>\r\n	<li>\r\n		A copy of the form is found on the next page.</li>\r\n	<li>\r\n		This form will become part of the employee file</li>\r\n</ul>\r\n', 'en'),
(6, 5, 'Reporting an Incident', '<p>\r\n	Any incident involving a resident will be reported to their next-of-kin or legal representative, their Case Manager and the Coordinator.</p>\r\n<p>\r\n	Operator or his designate reports the following incident <strong>immediately</strong>, and an Incident Report will be sent to the Case Manager with a copy to the ARF Coordinator<strong> </strong>within 24 hours</p>\r\n<ul>\r\n	<li>\r\n		Suspected physical abuse, sexual abuse, mental cruelty or neglect</li>\r\n	<li>\r\n		Missing resident (elopement/wandered)</li>\r\n	<li>\r\n		Disasters</li>\r\n	<li>\r\n		Suspicious death*</li>\r\n	<li>\r\n		Suicide</li>\r\n	<li>\r\n		Attempted suicide</li>\r\n</ul>\r\n<p class=\"reporting-incident\">\r\n	*Suspicious death is a death that is sudden and unexpected and is from any cause other than disease or natural causes.<br />\r\n	&nbsp;</p>\r\n<p>\r\n	For the following incidents, an Incident Report must be sent to the Case Manager by the Operator, or his designate, with a copy to the ARF Coordinator <strong>within 24 hours:</strong></p>\r\n<ul>\r\n	<li>\r\n		Injury/illness requiring medical attention</li>\r\n	<li>\r\n		Behaviour necessitating the use of physical restraints</li>\r\n	<li>\r\n		Medication mismanagement</li>\r\n	<li>\r\n		Admission to hospital</li>\r\n	<li>\r\n		Falls</li>\r\n	<li>\r\n		Incident involving conduct and attitude</li>\r\n</ul>\r\n<p>\r\n	<br />\r\n	Operator will:</p>\r\n<ul>\r\n	<li>\r\n		place a copy of the incident report in the file of the resident affected</li>\r\n	<li>\r\n		provide a copy of the incident report to the client on request</li>\r\n</ul>\r\n', 'en'),
(7, 6, 'Reporting a Death', '<p>\r\n	In case of death, the operator will notify immediately:</p>\r\n<ul>\r\n	<li>\r\n		police, if the death is suspicious or untimely</li>\r\n	<li>\r\n		physician or 911, when the death occurs on the premises</li>\r\n	<li>\r\n		next-of-kin or legal representative</li>\r\n	<li>\r\n		Extra Mural Program, if the resident is a client</li>\r\n	<li>\r\n		Case Manager and the Adult Residential Facility Coordinator</li>\r\n</ul>\r\n<p>\r\n	Operator will forward a Report of Death Form to the Adult Residential Facility Coordinator within <strong>forty-eight (48) hours</strong> of the death of the resident whether the resident died at home or in hospital.</p>\r\n', 'en'),
(8, 7, 'Reporting a Missing Resident', '<p>\r\n	The safety and security of our residents is our highest priority. If a resident is found to be missing, complete the following steps:</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"report-missing\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				1. Search all areas of the care home including locked areas<br />\r\n				2. Search the grounds of the care home.<br />\r\n				3. Immediately report all absences without notification to:</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"report-missing1\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				a. Local police by call 911<br />\r\n				b. Operator<br />\r\n				c. Case Manager<br />\r\n				d. Coordinator,<br />\r\n				e. and the next of kin/contact person</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"report-missing\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				4. Provide a description of the resident and last known clothing to search parties<br />\r\n				5. Full cooperate with any search and rescue efforts</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', 'en'),
(9, 8, 'Discharge or temporary absence', '<p>\r\n	<strong>Right to Discharge</strong><br />\r\n	<br />\r\n	The operators reserve the right to discharge a resident anytime the resident is a safety threat to themselves or other individuals in the facility. The resident, the next of kin, the coordinator and case manager will be notified at least fifteen (30) days prior to the date of discharge.</p>\r\n<p>\r\n	<strong>Discharge Process</strong></p>\r\n<p>\r\n	Upon discharge the operator will ensure:</p>\r\n<ol>\r\n	<li>\r\n		The Personal Record of Resident Form #76-5234 (5/08) is forwarded to Social Development;</li>\r\n	<li>\r\n		The Resident&#39;s Financial Record Form #76-5228 (5/08) and Medication Record Form #76-5236 (0/08) are forwarded to the new operator;</li>\r\n	<li>\r\n		The personal monies of residents are transferred to the appropriate person, for example, resident, case manager, new operator, or family member;</li>\r\n	<li>\r\n		Residents take all of their belongings and medication with them.</li>\r\n</ol>\r\n<p>\r\n	<strong>Temporary Leave</strong></p>\r\n<p>\r\n	Temporary leaves of absence due to hospitalization to a medical or psychiatric facility or to visit family and friends will be granted up to 30 days with no changes in funding from Social Development.</p>\r\n<p>\r\n	Leaves of absence longer than 30 days will be discussed with the Coordinator and Case Manager who may grant extensions beyond 30 days in exceptional situations, that is, longer stay in hospitals with intent to return to the facility.</p>\r\n<p>\r\n	Monthly fees are still required during a temporary absence.</p>\r\n', 'en'),
(10, 9, 'Resident and/or Family Concerns', '<p>\r\n	Concerns of residents and/or family will be heard by any staff member and be referred via the organizational chart based on level of concern and action required.</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"family\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				For example, if a resident has a concern that their room is cold in temperature, then the employee can adjust the temperature as per the thermostat on the wall. The employee would check back with the resident to ensure the intervention was effective. If the resident still found the room cold, then the concern would be escalated to the supervisor for further direction and follow-up.</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	The process for making a formal complaint is detailed below. This process is available for any staff, resident or family member.</p>\r\n<p>\r\n	Resident and family complaints are taken seriously. All complaints will follow the process below:</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"family-concerns\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				1.Complaint is documented (this may be done verbally from a resident, written is preferred from family)&nbsp;<br />\r\n				2.Notice of complaint logged in the daybook for staff reference<br />\r\n				3.Complaint is referred to the operator for investigation<br />\r\n				4.Operator conducts an investigation which may include:&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"family-concerns1\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				a. Interviewing the resident<br />\r\n				b. Interviewing the staff members involved<br />\r\n				c. Interviewing family members<br />\r\n				d. Viewing video surveillance (if applicable)<br />\r\n				e. Review of relevant documentation&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"family-concerns\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				5. Operator reserves the right to utilize an outside investigator if the incident is of a serious nature.<br />\r\n				6. Operator concludes investigation and follows up with all parties involved.<br />\r\n				7. Operator notes in daybook for staff reference the outcome of the investigation if applicable.<br />\r\n				8. Operator reports findings to ARF coordinator as indicated in ARF standards</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	Note: Staff remain informed of the process for complaints on hire and at staff meetings.</p>\r\n', 'en'),
(11, 10, 'Application and Approval', '<p>\r\n	<strong>ADMISSIONS</strong></p>\r\n<p>\r\n	This residence will admit residents on the needs of the applicant and at the discretion of the operator, as to whether the residence will be able to successfully meet their needs.</p>\r\n<p>\r\n	Residents can be admitted through two procedures. One is the Single Point Entry (SEP) provided by the Department of Social Development 1-888-441-7337. Second is the Private Pay Admission process which relies on the resident&rsquo;s ability to pay the total cost of care for a period of 12 months (doesn&rsquo;t require government subsidy). Through a series of forms being completed by the attending physicians, and subsequent approval from the Department of Social Development, such resident can move in within 10 days of application.</p>\r\n<p>\r\n	An<u> Admission Form and Agreement</u> must be completed prior to admission.</p>\r\n<p>\r\n	This residence relies on the department&rsquo;s financial assessor to determine a resident&rsquo;s eligibility for subsidization. In the absence of a complete financial assessment, the resident and/or sponsor is responsible for the entire cost of care while at the residence.</p>\r\n<p>\r\n	<strong>DISCHARGE</strong></p>\r\n<p>\r\n	The special care home, under the DSD Standards, is obliged to protect its residents and staff. If a resident presents a threat to the other residents, to themselves, or to the staff, the care home has the authority to discharge a resident within fifteen (15) days written notice, or if the situation warrants, immediately. The same holds for non-payment on accounts over thirty (30) days.</p>\r\n<div>\r\n	A resident cannot be held against their will in a special care home; therefore a resident who is mentally competent may leave at anytime.<br />\r\n	<br />\r\n	<strong>SPONSOR</strong><br />\r\n	<br />\r\n	The purpose of a resident&rsquo;s representative is to provide the special care home with a contact person who has the authority to make decisions for the resident in the event that the resident is unable to make decisions for him/ her. If a resident in unable to look after their own finances, it is the responsibility of the sponsor to see that all bills pertaining to the residence are paid in full.<br />\r\n	<br />\r\n	If there is a Power of Attorney, a copy of the document is required for our files.<br />\r\n	<br />\r\n	<strong>ACCOMODATION</strong></div>\r\n<div>\r\n	The residence consists of one semi-private and 10 private rooms. It may be necessary for the home to relocate a resident to another room. The family or sponsor will be consulted and notified of any need for change.</div>\r\n<div>\r\n	<br />\r\n	<strong>Furniture</strong></div>\r\n<div>\r\n	The resident and family are encouraged to personalize the resident&rsquo;s room. The safety of the residents and staff must be considered, and some pieces of furniture may not be suitable due to the size of the room.</div>\r\n<div>\r\n	Oversized recliner chairs can cause damage to the walls and may not be suitable. Glider rockers are an appropriate alternative, but other chairs may be just fine.</div>\r\n<p>\r\n	<strong>Telephone</strong><br />\r\n	The resident or sponsor is responsible for making arrangements with the telephone company&nbsp; should they wish their own phone. The resident is responsible for all costs, including costs to transfer the phone to another location if it was their request to move.</p>\r\n<div>\r\n	<strong>Television</strong><br />\r\n	Cable television hook up is included&nbsp; the resident. The lodge does not supply televisions.</div>\r\n<p>\r\n	<strong>Electrical Appliance</strong><br />\r\n	Electrical items must be CSA approved and inspected by the operator. Electric blankets or electric heating pads are not permitted and due to food safety issues, a small fridge is not allowed. Any extension cord usage needs to be approved by the operator. Power surge bars are permitted.</p>\r\n<p>\r\n	<strong>NURSING SERVICES</strong><br />\r\n	This residence works closely with Extra Mural Services in providing necessary nursing care to their residents.Upon approval by the resident&rsquo;s physician nursing services will be provided free&nbsp; of charge as per the physicians directions.Examples of services are blood tests, serious wound care, physiotherapists, occupational therapists etc. Day to day care is given by the residence staff consisting of health care aids, personal support workers, human service councilors, personal care attendants, and resident attendants.</p>\r\n<div>\r\n	<strong>Medications</strong><br />\r\n	The care home has a contract with a local pharmacy to fill prescriptions of all residents.Medications are not to be kept in resident&rsquo;s room unless permission is obtained from the physician.&nbsp;</div>\r\n<p>\r\n	All residents who have a private prescription plan must provide their plan number. Residents who do not have private drug plans will be directed on how to register with the NB Prescription&nbsp; Drug Program and will be issued a drug card. Medication not covered under the NB drug program will be the responsibility of the resident.<br />\r\n	All medications related to the resident will require billing arrangements to be made with[Pharmacy name, address, and contact].&nbsp;</p>\r\n<p>\r\n	As per the DSD Standards, our staff are not permitted to cut toenails. A professional foot care person is available bi-monthly for residents at a cost of ______$$. If families choose not to utilize this service they will be responsible for foot care for their loved one. Staff can provide simple foot management (filing, corn pads, cream, etc.) at no cost to the resident.</p>\r\n<p>\r\n	<strong>DIETARY SERVICES</strong><br />\r\n	&nbsp;This residence provides optimal nutritional care and quality food services for residents.Residents requiring specific dietary restrictions or texture modifications are assessed and accommodated.Families are encouraged to utilize our kitchen when visiting their loved one.Making a pot of tea or enjoying the cookie of the day with your loved one helps to make your visit more intimate, and we hope you will take advantage of this invitation. Arrangements for suppers to be provided for family members will involve a small fee.</p>\r\n<p>\r\n	<strong>SUPPORT SERVICES</strong></p>\r\n<p>\r\n	<strong>Laundry</strong></p>\r\n<p>\r\n	This residence will do all resident&rsquo;s laundry unless the family chooses to take laundry home and do it.The special care home cannot be held responsible for personal clothing items lost or damaged. It is recommended that all clients clothing should be labelled upon admission.</p>\r\n<p>\r\n	<strong>Environment</strong></p>\r\n<p>\r\n	Every effort will be made to keep rooms and other areas as clean and sanitary as possible.Please remember that too much clutter in a room can be a safety issue. Storage space is at a minimum, so it would be greatly appreciated if seasonal items (ex. clothing and decorations) could be packed and stored at home. We have a great staff at the residence, but accidents do happen. If an item gets lost or damaged, the special care home will not be held responsible;herefore if an item has great sentimental value, it may be best if you keep it at home.</p>\r\n<p>\r\n	<strong>Maintenance</strong></p>\r\n<p>\r\n	Any maintenance concerns should be relayed to the staff and they will let the operator know.</p>\r\n<p>\r\n	<strong>ACTIVITIES SERVICES</strong></p>\r\n<p>\r\n	The residence provides services to each resident who wishes to participate in planned activities. Special events take place during the year, and daily events are planned each month.Families are welcome to attend any program.</p>\r\n<p>\r\n	<strong>Pastoral Services</strong></p>\r\n<p>\r\n	Through volunteers, this residence has pastoral care services to residents on a regular basis.Monthly communion and prayer services are available to residents if they wish.Hymn sings are incorporated into the activity calendar and arrangements may be possible for transportation to church for residents that can&rsquo;t go with their families.</p>\r\n<p>\r\n	<strong>Volunteer Services</strong></p>\r\n<p>\r\n	We encourage family participation and welcome those who express and interest in helping in any way.Please talk with the staff if you would like to volunteer.</p>\r\n<p>\r\n	<strong>FINANCIAL SERVICES</strong></p>\r\n<p>\r\n	<strong>Resident&rsquo;s Account</strong></p>\r\n<p>\r\n	The daily rate (per diem) is set by the operator. Room charges will commence on the day of admission and will continue until the resident&rsquo;s belongings are removed from the room or after discharge or death. It is the family&rsquo;s responsibility for removal of the belongings.</p>\r\n<p>\r\n	Should the resident decide to move to another address, transfer to nursing home or passes away, one full month payment will be expected from date of notice.</p>\r\n<p>\r\n	Post-dated cheques dated for the first of each month will be supplied to the residence from the resident&rsquo;s sponsor. Cheques should be addressed to the residence.</p>\r\n<p>\r\n	Non payment on account after 30 days may warrant a discharge notice of 15 days.</p>\r\n<p>\r\n	<strong>Comfort and Clothing Allowance</strong></p>\r\n<p>\r\n	Some homes that can operate for the government rate ($####/mth) give a comfort and clothing allowance to residents of $###/mo. Due to our operational costs, special programs and payroll costs our rates are higher than the government rate. The residence does not handle any part of the comfort and clothing factor and has a set rate for lodging and care of $####/mth.&nbsp;</p>\r\n<p>\r\n	<strong>MISCELLANEOUS POLICIES AND SERVICES</strong></p>\r\n<p>\r\n	<strong>Valuables</strong><br />\r\n	This residence cannot be responsible for any valuables. If a resident or family member insists on having valuables, they are responsible to protect them.</p>\r\n<p>\r\n	<strong>Scent Policy</strong><br />\r\n	Scented products are harmful to those with allergies, environmental sensitivities, chronic heart or lung disease. In support of its commitment to the provision of healthy and safe environment for residents, staff, volunteers and visitors, this residence supports a scent reduction policy.</p>\r\n<p>\r\n	<strong>pets</strong><br />\r\n	Pet visits are encouraged as long as the owner assumes full responsibility of clean up, restrains the pet on a leash and pet immunizations are up to date.</p>\r\n<p>\r\n	<strong>Alcoholic Beverages</strong><br />\r\n	The consumption of alcoholic beverages is permitted with a physician&rsquo;s written order. All alcohol must be kept locked in the medication cupboard unless special arrangements are agreed upon.&nbsp;</p>\r\n<p>\r\n	<strong>Smoking Policy</strong><br />\r\n	This residence provides a smoke free environment to support the health and safety of all persons.</p>\r\n<p>\r\n	<strong>Visiting Hours and Outings for Residents</strong><br />\r\n	This residence has no specific visiting hours. Families may wish to take residents out for a day or overnight. Prior notice is appreciated to allow time for preparation of medication. The residence releases responsibility for resident when off property with family and friends.</p>\r\n<p>\r\n	<strong>Hairdressing</strong><br />\r\n	Weekly hair care is provided by staff of this residence. Washing, roller sets, and trims are included but not perms. Due to the strong smell of the products, it is appreciated that perms be done off premises when possible. Special arrangements may be possible if resident is not mobile.&nbsp;</p>\r\n<p>\r\n	<strong>Families</strong><br />\r\n	The residence would like to emphasize the importance of family involvement. We firmly believe that the continued involvement of family and friends plays an important role in enhancing the quality of life for the residents. We welcome children of all ages and if the resident likes animals, we have no objection to their favourite animal visiting. We encourage any suggestions from family to improve the care for our residents.</p>\r\n', 'en'),
(12, 11, 'Harassment', '<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>1</strong></td>\r\n			<td>\r\n				<strong>POLICY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.01</td>\r\n			<td>\r\n				The residence believes in providing and maintaining a work environment in which all employees are free from workplace harassment, sexual harassment and discrimination. Such actions are not tolerated by any persons, including customers, clients, other employers, supervisors, co-workers and members of the public and, where possible, are to be redressed.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.02</td>\r\n			<td>\r\n				Retaliation or reprisals are prohibited against any employee who has complained under this Statement of Policy and Procedure, or has provided information regarding a complaint. Any retaliation or reprisals are subject to immediate corrective action up to and including termination. Alleged retaliation or reprisals are subject to the same complaint procedures and penalties as complaints of discrimination and harassment.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.03</td>\r\n			<td>\r\n				The residence recognizes that individuals may find it difficult to come forward with a complaint under this Statement of Policy and Procedure because of concerns of confidentiality. Therefore, all complaints concerning workplace or sexual harassment or discrimination, as well as the names of parties involved, will be treated as confidential. The residence&rsquo;s obligation to conduct an investigation into the alleged complaint may require limited disclosure. No record of the complaint will be maintained on the personnel file of the complainant. If there is a finding of improper conduct that results in disciplinary action, it will be reflected only on the file of the person who engaged in such conduct in the same way as any other disciplinary action.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>2</strong></td>\r\n			<td>\r\n				<strong>PURPOSE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				2.01</td>\r\n			<td>\r\n				This Statement of Policy and Procedure outlines the procedures to be followed regarding workplace and sexual harassment and discrimination so that employees reporting alleged incidents will know the matter will be treated confidentially and may be reported without fear of retaliation or reprisal.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>3</strong></td>\r\n			<td>\r\n				<strong>SCOPE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				3.01</td>\r\n			<td>\r\n				This Statement of Policy and Procedure applies to all employees at the residence, including managers, supervisors, temporary employees, students and subcontractors and are to adhere to this policy and code of practice.&nbsp;<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				3.02</td>\r\n			<td>\r\n				This Statement of Policy and Procedure applies not only during working time, but to any activities on or off company premises which could reasonably be associated with the workplace (e.g., social events).<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>4</strong></td>\r\n			<td>\r\n				<strong>RESPONSIBILITY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.01</td>\r\n			<td>\r\n				The residence is responsible for establishing a written code of practice with respect to workplace harassment (e.g., a harassment policy) in consultation with the committee or health and safety representative. The residence will ensure that the harassment code of practice is, when followed, sufficient to provide for the health and safety of employees at the place of employment to the extent possible.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.02</td>\r\n			<td>\r\n				Supervisors are responsible for:&nbsp;\r\n				<ul>\r\n					<li>\r\n						ensuring the code of practice is implemented, adhered to and followed;</li>\r\n					<li>\r\n						providing training to all employees regarding the harassment code of practice and policy;</li>\r\n					<li>\r\n						ensuring discrimination and harassment are not tolerated, are reported, investigated and are redressed;</li>\r\n					<li>\r\n						ensuring that copies of the code of practice and training records are readily available to a WorkSafeNB officer and to employees on request.</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.03</td>\r\n			<td>\r\n				Employees are responsible for:\r\n				<ul>\r\n					<li>\r\n						adhering to the code of practice (this policy);</li>\r\n					<li>\r\n						ensuring discrimination and harassment are not tolerated;</li>\r\n					<li>\r\n						reporting as soon as circumstances permit any incident of harassment of which they are victim of, become aware of, or hear of alleged actions or complaints of discrimination or harassment.</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>5</strong></td>\r\n			<td>\r\n				<strong>DEFINITIONS</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.01</td>\r\n			<td>\r\n				<strong>&ldquo;Workplace&rdquo; </strong>means any place where business or work-related activities are conducted. It includes, but is not limited to, the physical work premises (offices or plants), work-related social functions (parties, golf games, etc.), work assignments outside the residence&rsquo;s offices or plants, work-related travel, and work-related conferences or training sessions.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<em>5.02</em><strong><em>&ldquo;</em>Harassment&rdquo;</strong> in a place of employment means any objectionable or offensive behaviour that is known or ought reasonably to be known to be unwelcome, including bullying or any other conduct, comment or display made on either a one-time or repeated basis that threatens the health or safety of an employee. It does not include reasonable conduct of an employer in respect of the management and direction of employees at the place of employment. The definition includes sexual harassment. This definition also includes any harassment or harassing behaviour based on the 16 prohibited grounds of discrimination: age, marital status, family status, creed or religion, physical disability, mental disability, race, colour, ancestry, place of origin, national origin, social condition, political belief or activity, sexual orientation&nbsp; gender identity or expression, and sex (including pregnancy) under the New Brunswick <em>Human Rights Act</em></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.03</td>\r\n			<td>\r\n				<strong>&ldquo;Sexual harassment&rdquo;</strong> is any unsolicited conduct, comment or physical contact of a sexual nature that is unwelcome by the recipient. It includes, but is not limited to, any unwelcome sexual advances (oral, written or physical) or requests for sexual favours; sexual and sexist jokes; racial, homophobic, sexist or ethnic slurs; written or verbal abuse or threats; unwelcome remarks, jokes, taunts, or suggestions about a person&rsquo;s body, a person&rsquo;s physical or mental disabilities, attire or on other prohibited grounds of discrimination; unnecessary physical contact such as patting, touching, pinching, hitting or leering; patronizing or condescending behaviour; displays of degrading, offensive or derogatory material such as graffiti or pictures; physical or sexual assault.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	<br />\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>6</strong></td>\r\n			<td>\r\n				<strong>REFERENCES and RELATED STATEMENTS of POLICY and PROCEDURE</strong><br />\r\n				General Regulations 91-191 Occupational Health and Safety Act (New Brunswick)<br />\r\n				<em>Human Rights Act </em>(New Brunswick)\r\n				<ul>\r\n					<li>\r\n						SPP HR 2.01.NB &mdash; Employment Principles</li>\r\n					<li>\r\n						SPP HR 5.01.NB &mdash; Employee Relations Principles</li>\r\n					<li>\r\n						SPP HR 5.02.NB &mdash; Dispute Resolution</li>\r\n					<li>\r\n						SPP HR 6.01.NB &mdash; Health and Safety Principles</li>\r\n					<li>\r\n						SPP HR 6.02.NB &mdash; Accident and Injury Reporting</li>\r\n					<li>\r\n						SPP HR 6.03.NB &mdash; Accident Investigation</li>\r\n					<li>\r\n						SPP HR 6.09.NB &mdash; Workplace Violence</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>7</strong></td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<strong>PROCEDURE</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				7.01</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				<strong>Step 1 &mdash; Self-help</strong><br />\r\n				Employees are encouraged to attempt to resolve their concerns by direct communication with the person(s) engaging in the unwelcome conduct. Where employees feel confident or comfortable in doing so, communicate disapproval in clear terms to the person(s) whose conduct or comments are offensive. Keep a written record of the date, time, details of the conduct and witnesses, if any.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				<strong>Step 2 &mdash; Management Support and Intervention</strong><br />\r\n				Employees who are not confident or comfortable with Step 1 and who believe they are victims of discrimination or harassment, or become aware of situations where such conduct may be occurring, are encouraged to report, verbally or in writing, these matters to any of the following: the employee&rsquo;s manager, general manager, or any person designated to deal with harassment complaints from time to time by the residence.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(c)</td>\r\n			<td>\r\n				<strong>Step 3 &mdash; Formal Complaint</strong><br />\r\n				If informal attempts at resolving the issue are not appropriate, or proving to be ineffective, a formal complaint may be filed. To file a formal complaint:</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles harassment-content\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(i)</td>\r\n			<td>\r\n				Submit a complaint by using the harassment complaint form that contains a brief account of the offensive incident (i.e., when it occurred, the persons involved, names of witnesses, if any). The form will also include the remedy sought and be signed and dated by the person complaining;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(ii)</td>\r\n			<td>\r\n				File the complaint form with your manager, the general manager or to any person designated by the residence to deal with harassment complaints;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(iii)</td>\r\n			<td>\r\n				Co-operate with those responsible for investigating the complaint.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				7.02</td>\r\n			<td>\r\n				An employee who becomes aware of situations where discrimination or harassment may be occurring is requested to notify, verbally or in writing, his or her manager, the general manager or to any person designated by the residence to deal with harassment complaints.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.03</td>\r\n			<td>\r\n				All complaints (informal or formal) must be investigated fairly, respectfully and in a timely manner. The investigation process will involve interviews with the complainant, the alleged respondent and any witnesses named by either. Within [14] working days of the incident or notice thereof, the manager will investigate the incident and prepare a written report of the investigation findings. The report will be provided along with recommendations, if any, to the general manager for action.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.04</td>\r\n			<td>\r\n				All complaints will be handled in a confidential manner, unless it is necessary to conduct the investigation or as required by law. The investigator will remind the worker who reported the workplace harassment, the respondent(s) and any witness(es) of confidentiality requirements under the workplace harassment program. Information concerning a complaint, or action taken as a result of the investigation, will not be released to anyone who is not involved with the investigation.&nbsp;<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.05</td>\r\n			<td>\r\n				Affected employees will be informed of the results of an investigation in writing.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.06</td>\r\n			<td>\r\n				Disciplinary action for violations of this Statement of Policy and Procedure will take into consideration the nature and impact of the violations, and may include a verbal or written reprimand, suspension (with or without pay) or termination (with or without notice). Similarly, deliberate false accusations are of equally serious nature and will also result in disciplinary action up to and including termination without notice for just cause. Note, however, that an unproven allegation does not mean that harassment did not occur or that there was a deliberate false allegation. It simply means that there is insufficient evidentiary basis to proceed or that while the complainant may have genuinely had reason to believe that there was harassment, investigation has not borne out the complaint.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.07</td>\r\n			<td>\r\n				Notices will be put on employee bulletin boards if there are new or revised procedures to follow following the investigation and report.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.08</td>\r\n			<td>\r\n				At least once each year, every supervisor and manager is required to ensure all employees under their direction are made aware of this policy and are trained on the policy.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.09</td>\r\n			<td>\r\n				An employee who has filed a complaint under this policy and who is not satisfied with the disposition of the complaint may seek redress under human rights legislation or WorkSafeNB.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.10</td>\r\n			<td>\r\n				The residence will review the established code of practice at least once each year in consultation with the committee or the health and safety representative, if any, or with employees if there is no committee or representative.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.11</td>\r\n			<td>\r\n				The code of practice will be updated:<br />\r\n				(a) when there is a change in conditions at the place of employment, or<br />\r\n				(b) when ordered to do so by a WorkSafeNB officer.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', 'en'),
(13, 12, 'Discrimination', '<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>1.</strong></td>\r\n			<td>\r\n				<strong>POLICY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				Our anti-discrimination policy explains how we prevent discrimination and protect our employees, customers and stakeholders from offensive and harmful behaviors. This policy supports our overall commitment to create a safe and happy workplace for everyone.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>2.</strong></td>\r\n			<td>\r\n				<strong>SCOPE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				This policy applies to all employees, contractors, visitors, customers and stakeholders.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>3.</strong></td>\r\n			<td>\r\n				<strong>PROCEDURE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				Discrimination is any negative action or attitude directed toward someone because of protected characteristics, like race and gender. Other protected characteristics are:\r\n				<ul>\r\n					<li>\r\n						Age</li>\r\n					<li>\r\n						Religion</li>\r\n					<li>\r\n						Ethnicity / nationality</li>\r\n					<li>\r\n						Disability / medical history</li>\r\n					<li>\r\n						Marriage / civil partnership</li>\r\n					<li>\r\n						Pregnancy / maternity/ paternity</li>\r\n					<li>\r\n						Gender identity / sexual orientation</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<strong>Discrimination and harassment</strong><br />\r\n				We will not tolerate any kind of discrimination that creates a hostile and unpleasant environment for employees, interns or volunteers.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				This is not an exhaustive list, but here are some instances that we consider discrimination:\r\n				<ul>\r\n					<li>\r\n						Hiring managers disproportionately disqualifying male or female job candidates on purpose.</li>\r\n					<li>\r\n						Managers bypassing team members with specific protected characteristics (e.g. race) for promotion without being able to formally prove (e.g. with documentation) the reasons other employees were selected instead.</li>\r\n					<li>\r\n						Employees making sexist comments.</li>\r\n					<li>\r\n						Employees sending emails disparaging someone&rsquo;s ethnic origin.</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				We recognize that sometimes discrimination is unintentional, as we may all have unconscious biases that could be difficult to identify and overcome. In case we conclude that an employee unconsciously discriminates, we will support them through training and counseling and implement processes that mitigate biases as we indicate in the next section. But, if this person shows unwillingness to change their behavior, we may demote or terminate them.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<strong>Actions to prevent discrimination</strong><br />\r\n				To ensure that our conduct and processes are fair and lawful, we:\r\n				<ul>\r\n					<li>\r\n						Use inclusive language in job ads</li>\r\n					<li>\r\n						Set formal job-related criteria to hire, promote and reward team members.</li>\r\n					<li>\r\n						Offer compensation and benefits according to position, seniority, qualifications and performance, not protected characteristics.</li>\r\n					<li>\r\n						<span style=\"color:#008000;\"><u>Accommodate people with disabilities</u></span><span style=\"color:#000000;\">.</span></li>\r\n					<li>\r\n						Require managers to keep detailed records of their decisions concerning their team members and job candidates.</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				We will also consider additional measures to prevent discrimination, like:\r\n				<ul>\r\n					<li>\r\n						Using hiring processes that reduce bias like<span style=\"color:#008000;\"><u>&nbsp;structured interviews</u></span>&nbsp;and&nbsp;<span style=\"color:#008000;\"><u>blind hiring programs</u></span><span style=\"color:#000000;\">.</span></li>\r\n					<li>\r\n						Organizing trainings on diversity, communication and conflict management to improve collaboration among employees of different backgrounds.</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<strong>What to do in cases of discrimination</strong><br />\r\n				If you are the victim of discriminatory behavior (or if you suspect that others are being discriminated against,) please talk to HR (or your manager) as soon as possible. HR is responsible for hearing your claim, investigating the issue and determining punishment.<br />\r\n				<br />\r\n				Punishment for discriminatory behavior depends on the severity of the offence. For example, inadvertently offending someone might warrant a reprimand. Conversely, willfully bypassing employees for promotion because of a protected characteristic will result in termination.<br />\r\n				<br />\r\n				If you decide to make a claim to a regulatory body we are committed and bound by law&nbsp;<span style=\"color:#008000;\"><u>not to retaliate&nbsp;</u></span>against you.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<strong>How we address discrimination complaints</strong><br />\r\n				HR is proactive and responsive about determining whether discrimination occurs. For example, we:<br />\r\n				<ul>\r\n					<li>\r\n						&nbsp;Look into similar claims about the same person or process to determine if discrimination is systemic.</li>\r\n					<li>\r\n						Track metrics and look into data that give us some insight on people&rsquo;s behaviors (e.g. percentage of job applicants of a certain race a hiring manager disqualifies.)</li>\r\n					<li>\r\n						Evaluate testimonies on social media that visitors, job candidates or former employees have made.</li>\r\n					<li>\r\n						Conduct discreet interviews and gather information.</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				We will investigate all claims discreetly. We will never disclose who made a complaint to anyone or give out information that may help others identify that person (e.g. which department or role they work in.)<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				We should all strive to prevent and address discrimination. Be aware of your implicit biases and speak up whenever you or your colleagues are discriminated against. If you have any ideas on how we can ensure fairness and equality in our workplace, we are happy to hear them.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				Resource:<br />\r\n				<span style=\"color:#0000ff;\"><u>https://resources.workable.com/anti-discrimination-policy#:~:text=Policy%20elements,Age</u></span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', 'en'),
(14, 13, 'Disciplinary Procedures', '<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles procedures\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>1.</strong></td>\r\n			<td class=\"procedure-policy\">\r\n				<strong>POLICY</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	Our&nbsp;<strong>Disciplinary Action company policy&nbsp;</strong>explains how we address our employees&rsquo; misconduct or inadequate performance. Employees must be aware of the consequences of their actions. We use this policy to outline our disciplinary procedure.</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles procedures\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>2.</strong></td>\r\n			<td>\r\n				<strong>SCOPE</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	This policy applies to all our employees.</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles procedures\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>3.</strong></td>\r\n			<td>\r\n				<strong>PROCEDURE</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	The stages that may be followed when discipline is deemed necessary include the following:</p>\r\n<ol>\r\n	<li>\r\n		Verbal warning</li>\r\n	<li>\r\n		Corrective Actions/Counseling</li>\r\n	<li>\r\n		Official written reprimand</li>\r\n	<li>\r\n		Disciplinary meeting with appropriate supervisor or manager</li>\r\n	<li>\r\n		Final written warning</li>\r\n	<li>\r\n		Detraction of benefits</li>\r\n	<li>\r\n		Indefinite suspension or demotion</li>\r\n	<li>\r\n		Termination</li>\r\n</ol>\r\n<p>\r\n	The nature of the offense must be explained to the employee from the beginning of the procedure. The verbal warning may take the form of a simple oral reprimand but also a full discussion if that is necessary.<br />\r\n	&nbsp;</p>\r\n<p>\r\n	The employee must read and sign the written reprimand and final written warning. These documents&nbsp;include the time limit in which an employee must correct their conduct before we take further disciplinary action.<br />\r\n	<br />\r\n	The following scenarios indicate where the disciplinary procedure starts depending on the violation:<br />\r\n	<strong>Performance issues</strong>. Disciplinary procedure starts at stage 1. It includes but is not limited to:</p>\r\n<ul>\r\n	<li>\r\n		Failure to meet performance objectives.</li>\r\n	<li>\r\n		<span style=\"color:#008000;\"><u>Attendance</u></span>&nbsp;issues.</li>\r\n	<li>\r\n		Failure to meet deadlines.&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</li>\r\n</ul>\r\n<p>\r\n	<strong>Misdemeanors/One-time minor offense</strong>. Disciplinary procedure starts at stage<br />\r\n	1. It includes but is not limited to:</p>\r\n<ul>\r\n	<li>\r\n		Rude behavior to customers or partners.</li>\r\n	<li>\r\n		On-the-job minor mistakes.</li>\r\n	<li>\r\n		Breach of dress code/<span style=\"color:#008000;\"><u>open door policy&nbsp;</u></span>etc.</li>\r\n	<li>\r\n		Involuntary Discrimination.</li>\r\n</ul>\r\n<p>\r\n	<strong>Misconduct/Frequent offender.</strong>&nbsp;Disciplinary procedure starts at stage 5. It includes but is not limited to:</p>\r\n<ul>\r\n	<li>\r\n		Lack of response to counseling and corrective actions.</li>\r\n	<li>\r\n		Lost temper in front of customers or partners.</li>\r\n	<li>\r\n		On-the-job major mistakes.</li>\r\n	<li>\r\n		Unwillingness to follow health and safety standards.</li>\r\n</ul>\r\n<p>\r\n	<strong>Severe offensive behavior/Felony</strong>.&nbsp;Disciplinary procedure starts at stage 6. It includes but is not limited to:</p>\r\n<ul>\r\n	<li>\r\n		Corruption/ Bribery.</li>\r\n	<li>\r\n		Breach of employment agreement.</li>\r\n	<li>\r\n		Harassment/ Voluntary discrimination.</li>\r\n	<li>\r\n		<span style=\"color:#008000;\"><u>Workplace Violence.</u></span></li>\r\n	<li>\r\n		Embezzlement/Fraud.</li>\r\n	<li>\r\n		<span style=\"color:#008000;\"><u>Substance Abuse.</u></span><u>&nbsp;</u></li>\r\n</ul>\r\n<p>\r\n	Managers or HR may choose to repeat stages of our disciplinary procedure as appropriate. This decision depends on employees&rsquo; reaction to our disciplinary procedure, whether they repent their behavior and the nature of their offense.<br />\r\n	<br />\r\n	Our disciplinary procedure begins when there is sufficient evidence to justify it. When there is suspicion or hints of misconduct, managers or HR must investigate the matter first.<br />\r\n	<br />\r\n	Appeals are allowed and must be filed to the next line of management as soon as possible.<br />\r\n	<br />\r\n	HR and managers should document every stage of our disciplinary procedure (except the verbal warning.) If appropriate, include necessary information like evidence, testimonies and employee&rsquo;s progress or improvement.<br />\r\n	<br />\r\n	We are obliged to refrain from disciplinary actions that may constitute retaliatory behavior. A&nbsp;<span style=\"color:#008000;\"><u>no retaliation company policy&nbsp;</u></span>will be effective at all times to ensure there is no misuse of our disciplinary procedure.<br />\r\n	<br />\r\n	We have the right to modify this policy or act in any other legal or reasonable way as each case demands. But, we will always enforce&nbsp;<span style=\"color:#008000;\"><u>discipline</u></span>&nbsp;in a fair and lawful manner.<br />\r\n	<br />\r\n	Resource:<br />\r\n	<span style=\"color:#0000ff;\"><u>https://resources.workable.com/disciplinary-action-company-policy</u></span></p>\r\n', 'en');
INSERT INTO `manual_policies_desc` (`desc_id`, `manual_policies_id`, `title`, `content`, `language`) VALUES
(15, 14, 'Conflict/Dispute Resolution', '<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>1</strong></td>\r\n			<td>\r\n				<strong>POLICY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.01</td>\r\n			<td>\r\n				This residence believes in resolving employee concerns and disputes related to their employment relationship in a prompt and equitable manner.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.02</td>\r\n			<td>\r\n				Employees who express any concerns, or lodge a formal complaint under this policy, or who provide information regarding a complaint under this Statement of Policy and Procedure may do so without fear of retaliation or reprisal. Any such conduct will be subject to immediate corrective action.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>2</strong></td>\r\n			<td>\r\n				<strong>PURPOSE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				2.01</td>\r\n			<td>\r\n				The purpose of this Statement of Policy and Procedure is to provide an effective problem-solving and dispute resolution process which every employee can utilize without concern for reprisal or recrimination. It is a vehicle by which employees may lodge complaints or express concerns to management about their employment relationship with the residence, or on other issues.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>3</strong></td>\r\n			<td>\r\n				<strong>SCOPE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				3.01</td>\r\n			<td>\r\n				This Statement of Policy and Procedure applies to all Non-union employees. Where appropriate: Dispute resolution procedures for Union employees are governed by the terms of the applicable Collective Agreement.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>4</strong></td>\r\n			<td>\r\n				<strong>RESPONSIBILITY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.01</td>\r\n			<td>\r\n				Managers are responsible for investigating and responding to employees in a timely manner regarding issues or concerns raised through this procedure.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.02</td>\r\n			<td>\r\n				Employees who believe they have legitimate complaints or concerns are encouraged to use these procedures &mdash; without fear of reprisal or recrimination.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>5</strong></td>\r\n			<td>\r\n				<strong>DEFINITIONS</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				None.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>6</strong></td>\r\n			<td>\r\n				<strong>REFERENCES and RELATED STATEMENTS of POLICY and PROCEDURE</strong><br />\r\n				SPP HR 5.01.NB &mdash; Employee Relations Principles<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>7</strong></td>\r\n			<td>\r\n				<strong>PROCEDURE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>7.01</strong></td>\r\n			<td>\r\n				<strong>&nbsp;Informal problem-solving</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				Employees who believe they have legitimate concerns about any aspect of their employment relationship with [Company] should first discuss those concerns with their immediate manager and attempt to resolve them satisfactorily. Managers are required to discuss and/or investigate any concern raised, and to respond in an appropriate manner, within [2] working days of learning of the concern or dispute. If the issue is not resolved in a manner that is satisfactory to the employee, a formal complaint may be lodged by the employee.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>7.02</strong></td>\r\n			<td>\r\n				<strong>&nbsp;Formal problem-solving</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				If an employee&#39;s concern is not resolved in a satisfactory manner through the informal problem-solving process, a formal complaint may be lodged, within [5] working days of the facts becoming known that give rise to the concern or dispute.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				A formal complaint is required to be in writing, on the Incident Resolution Form shown in Attachment A to this Statement of Policy and Procedure. The completed and signed Form shall be addressed to the employee&#39;s immediate manager and be presented to that manager. The employee may request the assistance of any member of [Company] in preparing a formal complaint. Preparing a formal complaint will not be interpreted as criticism of the immediate manager. The recipient of a formal complaint shall provide a copy of the complaint to the recipient&rsquo;s immediate superior.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				(c)</td>\r\n			<td>\r\n				&nbsp;Within [2] working days of receiving a formal complaint, or at a time mutually agreed upon, the manager&rsquo;s direct superior shall meet with the employee and manager, investigate the complaint, and respond, in writing, to the employee who lodged the complaint. If the matter is not resolved in a satisfactory manner, the employee may appeal the matter, within [five] working days of receiving the written response, and consult with the general manager, in an attempt to resolve the issue. The General Manager&#39;s decision shall be final and binding upon the parties.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n', 'en'),
(16, 15, 'Garnishment of Wages', '<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>1</strong></td>\r\n			<td>\r\n				<strong>POLICY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.01</td>\r\n			<td>\r\n				The law requires this residence to comply with court-ordered payments and wage garnishments when they are presented. Employees affected by wage garnishments or court-ordered payments will be notified by Payroll of any charge against their wages.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.02</td>\r\n			<td>\r\n				No person acting on behalf of this residence may dismiss; threaten to dismiss; discipline or suspend; impose any penalty upon; or intimidate or coerce any employee because of a wage garnishment or court-ordered payment.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>2</strong></td>\r\n			<td>\r\n				<strong>PURPOSE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				2.01</td>\r\n			<td>\r\n				The purpose of this Statement of Policy and Procedure is to set out the residence&rsquo;s&nbsp; obligations when dealing with wage garnishments and/or court-ordered payments.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>3</strong></td>\r\n			<td>\r\n				<strong>SCOPE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				3.01</td>\r\n			<td>\r\n				This Statement of Policy and Procedure applies to all employees.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>4</strong></td>\r\n			<td>\r\n				<strong>RESPONSIBILITY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.01</td>\r\n			<td>\r\n				Payroll is responsible for informing employees, in a confidential manner, when wage garnishments are placed against them.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>5</strong></td>\r\n			<td>\r\n				<strong>DEFINITIONS</strong><br />\r\n				None.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>6</strong></td>\r\n			<td>\r\n				<strong>REFERENCES and RELATED STATEMENTS of POLICY and PROCEDURE</strong><br />\r\n				<em>Garnishee Act </em>(New Brunswick)<br />\r\n				<em>Support Enforcement Act </em>(New Brunswick)<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>7</strong></td>\r\n			<td>\r\n				<strong>PROCEDURE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.01</td>\r\n			<td>\r\n				Within 24 hours of receiving a wage garnishment order, Payroll shall notify the employee, by telephone or in person, of a wage garnishment and/or a court-ordered payment against the employee and when it will be effective.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.02</td>\r\n			<td>\r\n				Wage garnishments and court-ordered payments are considered to be confidential information. Documentation related to a garnishment or court-ordered payment shall be maintained only in the necessary Payroll files.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n', 'en'),
(17, 16, 'Employee Access to Personal Information', '<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr class=\"title\">\r\n			<td>\r\n				<strong>1</strong></td>\r\n			<td>\r\n				<strong>POLICY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.01</td>\r\n			<td>\r\n				Subject to SPP HR 5.07.NB &mdash; Personal Information Protection, the residence will provide all employees, former employees, and authorized representatives of employees or former employees with access to certain contents of the employee&rsquo;s or former employee&rsquo;s records containing general employee information upon written request.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.02</td>\r\n			<td>\r\n				Employees and former employees shall be provided access to records containing general employee information upon written request as outlined in paragraph 7.02 herein. The residence may limit access to any records contained in an employee&rsquo;s personnel file in accordance with applicable privacy legislation.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.03</td>\r\n			<td>\r\n				Nothing in this policy obligates the residence to retain employee records beyond the retention periods specified by law.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<br />\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr class=\"title\">\r\n			<td>\r\n				<strong>2</strong></td>\r\n			<td>\r\n				<strong>PURPOSE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				2.01</td>\r\n			<td>\r\n				The purpose of this policy is to provide processes and procedures for the collection, maintenance and retention of employee records, for the purpose of providing employees and former employees with access to some of the employee&rsquo;s or former employee&rsquo;s records containing general employee information, in accordance with applicable privacy legislation, and for ensuring the confidentiality and security of sensitive employee records.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<br />\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr class=\"title\">\r\n			<td>\r\n				<strong>3</strong></td>\r\n			<td>\r\n				<strong>SCOPE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				3.01</td>\r\n			<td>\r\n				This policy applies to all employees and former employees.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>4</strong></td>\r\n			<td>\r\n				<strong>RESPONSIBILITY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.01</td>\r\n			<td>\r\n				The Human Resource department is responsible for:</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				collecting and retaining employee records under the following categories&mdash;general employee information, personal health information, Workers Compensation Board records and confidential data&mdash;in accordance with provincial and federal statutes and SPP HR 5.07.NB &mdash; Personal Information Protection;<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				determining the appropriate level of access to be provided to the employee or former employee when requested in writing by the employee or former employee;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(c)</td>\r\n			<td>\r\n				&nbsp;preparing the employee&rsquo;s or former employee&rsquo;s records containing general employee information for access by the employee or former employee;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(d)</td>\r\n			<td>\r\n				ensuring that any documents which identify other employees, clients/customers or any other individuals are removed from the employee&rsquo;s or former employee&rsquo;s record or appropriately redacted to protect the privacy of third parties;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(e)</td>\r\n			<td>\r\n				determining the appropriate method of providing access to the records&nbsp; containing general employee information, including:</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles employee\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(i)</td>\r\n			<td>\r\n				ensuring that a staff member is present at all times during the employee&rsquo;s or former employee&rsquo;s access to the employee&rsquo;s or former employee&rsquo;s records containing general employee information;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(ii)</td>\r\n			<td>\r\n				assisting the employee or former employee;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(iii)</td>\r\n			<td>\r\n				ensuring the integrity and continued completeness of the record;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(iv)</td>\r\n			<td>\r\n				ensuring that only the employee or former employee has access to his or her records, upon presentation of identification, or proof of status if an authorized representative of the employee or former employee has requested access to the file; and</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(v)</td>\r\n			<td>\r\n				providing a copy of the employee&rsquo;s or former employee&rsquo;s records containing general employee information to the employee or former employee, upon request;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<br />\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(f)</td>\r\n			<td>\r\n				ensuring that no documents are added to or removed from the employee&rsquo;s or former employee&rsquo;s record without authorization; and</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(g)</td>\r\n			<td>\r\n				providing employees and former employees with information and guidance regarding requests to correct, amend or challenge any record in the employee&rsquo;s or former employee&rsquo;s record.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				4.02</td>\r\n			<td>\r\n				It is the employee&rsquo;s or former employee&rsquo;s responsibility to provide the Human Resources Department with a written request for access to his or her general employee information records at least [48] hours prior to requiring access.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>5</strong></td>\r\n			<td>\r\n				<strong>DEFINITIONS</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.01</td>\r\n			<td>\r\n				&ldquo;<strong>Confidential data&rdquo;</strong> includes, but is not limited to, information obtained through reference checks, notes, observations, opinions or other data compiled by supervisors or other personnel concerning an employee&rsquo;s behaviour, conduct or notes prepared by appropriate personnel pertaining to an issue involving the employee, criminal record checks, investigation records, employee evaluative records and documents which would violate the privacy of another person if disclosed.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.02</td>\r\n			<td>\r\n				&ldquo;<strong>Employee and/or former employee</strong>&rdquo; includes the authorized representatives of an employee and/or former employee.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.03</td>\r\n			<td>\r\n				&ldquo;<strong>General employee information</strong>&rdquo; means personal information about an individual that is collected, used or disclosed solely for the purposes reasonably required to establish, manage or terminate an employment relationship between the organization and that individual, but does not include personal information that is not about an individual&#39;s employment.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.04</td>\r\n			<td>\r\n				&ldquo;<strong>Personal health information&rdquo;</strong> is information about an identifiable individual that relates to the physical or mental health of the individual, the provision of health care to the individual, the individual&rsquo;s entitlement to payment for health care, the individual&rsquo;s health card number, the identity of providers of health care to the individual or the identity of substitute decision-makers on behalf of the individual. Personal health information also includes information related to employee medical examinations, medical reports and records, medical insurance claims and data provided to the residence by third parties.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.05</td>\r\n			<td>\r\n				&ldquo;<strong>Workers&rsquo; Compensation Board records&rdquo;</strong> include information related to WCB claims, employer appeals, reports, findings, injury or investigations, return to work, etc.&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>6</strong></td>\r\n			<td>\r\n				<strong>REFERENCES and RELATED STATEMENTS of POLICY and PROCEDURE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<em>Employment Standards Act</em> (New Brunswick) and Regulations<br />\r\n				<em>Occupation Health and Safety Act </em>(New Brunswick) and Regulations<br />\r\n				<em>Personal Information Protection and Electronic Documents Act </em>(PIPEDA)<br />\r\n				(Canada)<br />\r\n				<em>Workers&rsquo; Compensation Act </em>(New Brunswick)</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<ul>\r\n					<li>\r\n						SPP HR 2.10.NB &mdash; Third Party Reference Requests</li>\r\n					<li>\r\n						SPP HR 2.23.NB &mdash; Dealing with Government Inspectors</li>\r\n					<li>\r\n						SPP HR 5.07.NB &mdash; Personal Information Protection</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>7</strong></td>\r\n			<td>\r\n				<strong>PROCEDURE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.01</td>\r\n			<td>\r\n				The Human Resources Department shall obtain, organize and maintain all employee records in accordance with applicable provincial and federal laws, this policy, and SPP HR 5.07.NB &mdash; Personal Information Protection. Supervisors shall deliver all employee&nbsp;<br />\r\n				<br />\r\n				records to the Human Resources Department for management and retention as soon as possible.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.02</td>\r\n			<td>\r\n				Upon written request and wherever possible, employees or former employees will be granted access to their general employee information records within [48] hours. Employees or former employees will not be granted access to records for which access is prohibited or not required pursuant to the applicable privacy legislation; however, upon request, employees or former employees may be provided access to a specific record or document, in the sole discretion of the General Manager. A copy of any document which bears the signature of the employee or former employee will be provided to the employee or former employee.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.03</td>\r\n			<td>\r\n				Anyone requesting access to employee or former employee general employee information records will be required to provide the residence with government issued photo identification to verify his or her identity prior to accessing employee or former employee records containing general employee information.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.04</td>\r\n			<td>\r\n				Employees or former employees should make an appointment with the Human Resources Department to view requested records and may only access records in the presence of a Human Resources Department staff member. In the case of employees, appointments to access records should be made during break periods, shift change-overs, or with the consent of the employee&rsquo;s Supervisor during work hours using banked time in lieu of overtime pay.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.05</td>\r\n			<td>\r\n				Documents in the employee&rsquo;s or former employee&rsquo;s general employee information record may be viewed but must not be removed from the record under any circumstances. If a copy of a document in the general employee information record is required, a copy will be made by Human Resources Department staff at no cost to the employee or former employee (within reason and at the discretion of Human Resources Department staff).<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				7.06</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				&nbsp;If an employee or former employee believes that there is an error or discrepancy in a document in an employee or former employee&rsquo;s general employee information records, or if an employee or former employee disagrees with something in his or her record which the employee or former employee would like to be addressed, the employee or former employee shall submit a written statement of the error, together with an explanation of the error/discrepancy, and the remedy sought. Within [2] weeks, the Human Resources Department shall advise the employee or former employee in writing of the actions taken or to be taken, if any, to address the employee or former employee&rsquo;s submission.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				If the employee or former employee is unsatisfied with the action taken by the Human Resources Department, he or she may provide further written notice within [2] weeks which shall be delivered to the General Manager. The General Manager shall arrange a meeting with the employee or former employee, the Human Resources Department and the General Manager to resolve the dispute.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(c)</td>\r\n			<td>\r\n				In the event a suitable resolution cannot be reached, the General Manager shall make the final determination.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', 'en'),
(18, 17, 'Disclosure of Employee Information Records', '<p>\r\n	Managers are frequently contacted by third parties for references concerning former employees. Inca case of involuntary termination, a complete refusal or failure to provide a reference may significantly increase the financial risk in the event of litigation. In other cases, an inaccurate reference may, at least theoretically, expose the employer to a libel or slander action from the employee. In Canada, litigation regarding &ldquo;wrongful references&rdquo; is unknown.&nbsp; In the United States, there have been a number of cases where an employee has successfully sued an ex-employer for an inaccurate or biased reference. Despite the lack of litigation in Canada, this type of policy should save management time and reduce the risks of potential legal action. Additionally, employees are assured that no information will be released without their authorization.</p>\r\n<ul>\r\n	<li>\r\n		Given the increasing emphasis on protecting persona information, employers should ensure that training is given to all employees who have access to confidential personnel information. This includes personnel in payroll, accounting, human resource and security. There should be a concerted focus on ensuring that employees understand the company&rsquo;s procedures regarding the handling of personal information.</li>\r\n	<li>\r\n		Employees who do not handle personal information on a routine basis should know what information is considered private and confidential and should be trained to refer any requests to the designated person within the organization. This avoids dangerous &ldquo;off the cuff&rdquo; comments that can expose a company to liability.</li>\r\n	<li>\r\n		Payroll personnel often receive telephone requests for references concerning employees who are applying for credit or loans. No such information should be provided over the telephone unless the employer already has a written authorization from the employee to release such information. In the alternative, payroll personnel can provide a written confirmation on letterhead to be used by the employee.</li>\r\n	<li>\r\n		With respect to requests for job performance references, an important step in minimizing legal risk is to provide written references in the form of a letter addressed to the employee at the time of termination. A copy of the letter should be kept in the employee&rsquo;s file. This ensures that only factual data is contained in the reference and the employee is then at liberty to use the reference as he/she may see fit. Additional copies can be sent out upon written request to the employee, if originals are required.</li>\r\n	<li>\r\n		It is common to designate one person who has full access to employees&rsquo; personnel files as the individual responsible for responding to all reference requests. It need not be the employee&rsquo;s manager.</li>\r\n	<li>\r\n		Employers should refrain from conjecturing about a former employee&rsquo;s potential, ability to do a job, and should never divulge medical or workers&rsquo; compensation information about the employee.</li>\r\n	<li>\r\n		Some companies, especially those with US parents, have adopted a policy of only confirming job titles, job duties, dates of employment and salary levels. In Canada, this type of policy may backfire, since Canadian courts expect that an employer will give an honest reference to assist an employee to find work elsewhere.</li>\r\n</ul>\r\n<p>\r\n	There are no legislated employment standards related to third party references but federal legislation, the<em> Personal Information Protection and Electronic Documents</em> <em>Act (PIPEDA)</em>, which became effective on January 1, 2004 for all provincially-regulated organizations engaged in commercial activities, has implications related to the disclosure of employee personal information to third parties.</p>\r\n<p>\r\n	Although PIPEDA does not directly affect employee personal information in provincially regulated organizations, employers should consider the principles contained in this legislation and the impact those principles may have on human resources and payroll functions. Prudent employers will use this opportunity to reassess their management practices related to handling employee personal information, particularly as it related to the disclosure of information to third parties.</p>\r\n', 'en'),
(19, 18, 'Social Media', '<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>1</strong></td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<strong>POLICY</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				1.01</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				This residence&rsquo;s computer resources are business systems that should only be used for legitimate company purposes. The use of computers for any purpose that is not specifically related to company business is prohibited.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				All documents, data, and information composed, sent, stored and received on or over company computer systems (including by way of e-mail whether through a mobile device or otherwise) are the property of the residence. Therefore, employees should have no expectation of privacy while using the company&rsquo;s computer systems and reserves the right to monitor and inspect all uses of computer systems including use of e-mail, Internet and social media, at any time without notice.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				1.02</td>\r\n			<td>\r\n				Employees who wish to create and/or maintain personal blogs or participate in social media may do so on their personal computer and on their own time outside of the workplace. Employees who create or contribute to blogs or social media and who identify themselves or others, either directly or indirectly, as employees must adhere to the following code of conduct:</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(i)</td>\r\n			<td>\r\n				Each social media posting/blog of an employee must contain the following disclaimer: &ldquo;The views expressed on this website/blog are the views of the author alone and do not reflect the views this residence&rdquo;;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(ii)</td>\r\n			<td>\r\n				An employee must ensure that no confidential, proprietary, copyrighted, or other sensitive information related to the residence appears on, or is referred to, on any social media site or blog, unless specific written permission has been obtained from the General Manager;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(iii)</td>\r\n			<td>\r\n				An employee must ensure that he or she does not libel, slander, intimidate, harass, or threaten any employee, customer, supplier or affiliate of the residence on any social media site or in any blog content;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(iv)</td>\r\n			<td>\r\n				An employee must not make comments which are negative, derogatory or false about this residence, its employees, its management, its suppliers, its customers, its affiliates or its competitors on any social media site or in any blog content;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(v)</td>\r\n			<td>\r\n				An employee must make best efforts to make a good impression on readers and to ensure that postings and/or blogs are thoughtful, and free of profanity and harassing and/or discriminatory comments;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(vi)</td>\r\n			<td>\r\n				If an employee is uncertain about the propriety of a public communication over a social media site or blog they should consult Human Resources, Management and/or the Legal Department prior to making such communications; and</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(vii)</td>\r\n			<td>\r\n				An employee must ensure that any social media postings and/or blogs are in accordance with Canada&rsquo;s Anti-Spam Legislation (CASL), where applicable.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>2</strong></td>\r\n			<td>\r\n				<strong>PURPOSE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				2.01</td>\r\n			<td>\r\n				This residence seeks to promote a high level of responsible behaviour in connection with the use of computer resources and Internet communications, through its own website or otherwise, and has formulated this policy to accomplish the following goals:</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				to protect the reputation and resources of the residence, its customers and employees, from irresponsible or illegal activities;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				to ensure privacy, security and reliability of the residence&rsquo;s network and systems as well as the systems of the residence&rsquo;s clients and affiliates;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(c)</td>\r\n			<td>\r\n				to establish guidelines for the acceptable use of the residence&rsquo;s network, trademarks, intellectual property and confidential information;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(d)</td>\r\n			<td>\r\n				to define generally those actions which are considered abusive and prohibited;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(e)</td>\r\n			<td>\r\n				to outline procedures for the monitoring of online communications and inspection of employee computer/e-mail use at work, and for handling and reporting any abuse to the residence; and</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(f)</td>\r\n			<td>\r\n				to establish guidelines to ensure that the residence and its employees adhere to the requirements of Canada&rsquo;s Anti-Spam Legislation (CASL).</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>3</strong></td>\r\n			<td>\r\n				<strong>SCOPE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				3.01</td>\r\n			<td>\r\n				This policy applies to all users of the residence&rsquo;s computer network and electronic resources, and all employees identifying themselves, either directly or indirectly, in online communications, including web and social media sites and blogs as employees of the residence.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<br />\r\n<br />\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>4</strong></td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<strong>RESPONSIBILITY</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				4.01</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				Users of the residence&rsquo;s computer network are strictly prohibited from creating or maintaining personal blogs or logging into social media sites on the residence&rsquo;s computer resources or during company time.&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				Employees must ensure that they do not divulge private and/or confidential information related to the residence&rsquo;s business, its clients, its affiliates and/or its employees while communicating on social media sites, the Internet in general or through blogs, unless prior written approval has been obtained from the General Manager.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(c)</td>\r\n			<td>\r\n				Employees must not violate CASL or any of the residence&rsquo;s policies including, but not limited to the policies outlined in Section 6 herein and any Information Technology policies.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				4.02</td>\r\n			<td>\r\n				The Information Technology Manager is responsible for:</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				monitoring, investigating and reporting any allegations or concerns regarding the content of employee, or other, postings or blogs that may affect the residence or any of its employees, suppliers, customers or affiliates; and</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				ensuring that all employees are adequately trained in the requirements of CASL, and that, where required, express or implied consent has been obtained prior to sending commercial electronic messages through social media.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>5</strong></td>\r\n			<td>\r\n				<strong>DEFINITIONS</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.01</td>\r\n			<td>\r\n				&ldquo;<strong>Blog</strong>&rdquo; means a personal web journal or log and includes the creation and/or maintenance of a personal web journal or contributing content to another individual&rsquo;s blog.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.02</td>\r\n			<td>\r\n				&ldquo;<strong>Blogging</strong>&rdquo; is the act of creating, maintaining or contributing to a web journal or log.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.03</td>\r\n			<td>\r\n				&ldquo;<strong>Canada&rsquo;s Anti-Spam Legislation (CASL</strong>)&rdquo; <em>means An Act to Promote the Efficiency and Adaptability of the Canadian Economy by Regulating Certain Activities that Discourage Reliance on Electronic Means of Carrying out Commercial Activities</em>, SC 2010, c-23.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.04</td>\r\n			<td>\r\n				&ldquo;<strong>Commercial electronic message</strong>&rdquo; means an electronic message that, having regard to the content of the message, the hyperlinks in the message to content on a website or other database, or the contact information contained in the message, it would be reasonable to conclude has as its purpose, or one of its purposes, to encourage participation in a commercial activity, including an electronic message that offers to purchase, sell, barter or lease a product, goods, a service, land or an interest or right in land, offers to provide a business, investment or gaming opportunity, or advertises or promotes any of these, or promotes a person, including the public image of a person, as being a person who does anything referred to above, or who intends to do so.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.05</td>\r\n			<td>\r\n				&ldquo;<strong>Posting</strong>&rdquo; is the act of uploading, adding, contributing or linking content on a blog, social media site or on the Internet in general.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.06</td>\r\n			<td>\r\n				&ldquo;<strong>Social media</strong>&rdquo; is an Internet or mobile device application, location or site that provides users with the opportunity and means to participate in, develop or share content or views and opinions with others (e.g., Facebook, Twitter, Myspace, LinkedIn, etc.).<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>6</strong></td>\r\n			<td>\r\n				<strong>REFERENCES and RELATED STATEMENTS of POLICY and PROCEDURE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				An Act to Promote the Efficiency and Adaptability of the Canadian Economy by Regulating Certain Activities that Discourage Reliance on Electronic Means of Carrying out Commercial Activities, SC 2010, c-23. (CASL) (Canada)</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				Personal Information Protection and Electronic Documents Act (PIPEDA) (Canada)\r\n				<ul>\r\n					<li>\r\n						SPP HR 5.03.NB &mdash; Workplace Harassment</li>\r\n					<li>\r\n						SPP HR 5.06.NB &mdash; Discipline</li>\r\n					<li>\r\n						SPP HR 5.07.NB &mdash; Personal Information Protection</li>\r\n					<li>\r\n						SPP HR 5.08.NB &mdash; Computer, E-Mail and Internet Use</li>\r\n					<li>\r\n						SPP HR 5.13.NB &mdash; Instant Messaging</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<br />\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>7</strong></td>\r\n			<td>\r\n				<strong>PROCEDURE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.01</td>\r\n			<td>\r\n				This residence monitors social media and blogs including employee social media and blogs, without notice, to ensure that its confidential or proprietary information is not being disclosed and to ensure that this policy is being followed. Employees should not expect privacy when posting to social media sites and blogging. Monitoring of social media and blogs shall be conducted by the information technology manager who will report to the general manager all content of social media or a blog that identifies or relates to the residence, its employees, customers, suppliers or affiliates.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.02</td>\r\n			<td>\r\n				Employees found in breach of this policy will be subject to disciplinary action up to and including termination for cause.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.03</td>\r\n			<td>\r\n				All users of the residence&rsquo;s computer resources, and all employees who maintain personal blogs or participate in social media must adhere to the terms of this policy, any applicable Information Technology policy and CASL. A copy of this policy and SPP HR 5.08.NB &mdash; Computer, E-Mail and Internet Use shall be provided to each user and each user is required to sign an acknowledgement of receipt of these policies.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.04</td>\r\n			<td>\r\n				Employees who blog or use social media sites are to refrain from:\r\n				<ul>\r\n					<li>\r\n						disclosing secret, confidential, private, or internal information related to the residence</li>\r\n					<li>\r\n						posting or downloading offensive, harassing or discriminatory language or graphics;</li>\r\n					<li>\r\n						disparaging co-workers, customers, suppliers or affiliates;</li>\r\n					<li>\r\n						posting anything that is not in the residence&rsquo;s best interests;</li>\r\n					<li>\r\n						posting anything that violates residence&rsquo;s policies; and</li>\r\n					<li>\r\n						posting anything that violates CASL.</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.05</td>\r\n			<td>\r\n				&nbsp;Managers shall review this policy with all employees at least once annually.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', 'en'),
(20, 19, 'Financial Management', '<p>\r\n	The financial department is:</p>\r\n<ul>\r\n	<li>\r\n		responsible for the management of financial resources and safeguarding of monies entrusted to the facility</li>\r\n	<li>\r\n		responsible for administering an internal financial system that is compatible with the financial reporting requirements of Social Development</li>\r\n</ul>\r\n<p>\r\n	Key Point: To ensure all appropriate charges are recorded and statements sent out to the power of attorney or person responsible in a timely fashion.</p>\r\n<p>\r\n	Resident&rsquo;s statements for previous month shall be compiled added to the billing for the following month. Monthly billing is due at the beginning of every month.</p>\r\n<p>\r\n	<br />\r\n	<strong>PROCEDURE:</strong></p>\r\n<ol>\r\n	<li>\r\n		All charges are made on the spreadsheet.</li>\r\n	<li>\r\n		Private Pay Residents &ndash; full rate.</li>\r\n	<li>\r\n		Resident receiving subsidy - residents portion is the amount of Residents income (cheques) less comfort money.</li>\r\n	<li>\r\n		DSD&rsquo;s share is the monthly rate less the determined resident portion (HRD).</li>\r\n	<li>\r\n		Obtain additional charges to be shown on statement.</li>\r\n	<li>\r\n		Medication charges to be shown on statement.</li>\r\n	<li>\r\n		Note TV cable charges if applicable.</li>\r\n	<li>\r\n		Post spreadsheet to Accounting Package.</li>\r\n	<li>\r\n		Mail statement from accounting package to client.</li>\r\n	<li>\r\n		Payment to be remitted by 1st of the month following by cheque, cash, e-transfer or pre-authorized debit.</li>\r\n</ol>\r\n<p>\r\n	Case Manager will be advised of any known changes in financial status that may affect contributions to services by a subsidized client</p>\r\n', 'en'),
(21, 20, 'Resident Gifts', '<p>\r\n	The operator and employees are unable to accept gifts from residents.</p>\r\n', 'en'),
(22, 21, 'Resident Finances and Comfort and Clothing Allowance', '<p>\r\n	Residents who are able to manage their own finances should be encouraged to do so.</p>\r\n<p>\r\n	Residents receiving financial assistance from Social Development must retain the designated amount as a personal comfort and clothing allowance.</p>\r\n<p>\r\n	<strong>Helpful Information</strong></p>\r\n<p>\r\n	Comfort and Clothing Allowance Rates<br />\r\n	April 2006...................................... $128.00&nbsp;<br />\r\n	<br />\r\n	April 2007 to the present ...............$135.00<br />\r\n	<br />\r\n	Residents may use the comfort and clothing allowance to cover expenses associated with:</p>\r\n<ul>\r\n	<li>\r\n		clothing/personal items</li>\r\n	<li>\r\n		co-pay for prescription drugs</li>\r\n	<li>\r\n		additional fees for eyeglasses, dentures and hearing aids</li>\r\n	<li>\r\n		transportation not covered by other programs</li>\r\n</ul>\r\n', 'en'),
(23, 22, 'Resident Trusts', '<p>\r\n	If residents are unable to manage their own comfort and clothing allowance and/or do not have a family member a trust will be used for the following situations where:</p>\r\n<ul>\r\n	<li>\r\n		residents cannot manage their comfort and clothing allowance and there is no family member or other representatives willing and/or able to do so</li>\r\n	<li>\r\n		residents cannot manage their income (e.g. OAS/GIS, CPP, etc.) and there is no family member or other representatives willing and/or able to do so</li>\r\n</ul>\r\n<p>\r\n	When receiving monies or other valuable securities for the benefit and use of residents, operators will</p>\r\n<ul>\r\n	<li>\r\n		deposit valuables or monies in a safe place and keep a record</li>\r\n	<li>\r\n		issue receipts and keep copies of all receipts</li>\r\n	<li>\r\n		record disbursements to or for the residents</li>\r\n	<li>\r\n		at the request of the residents, release part or all of those monies or securities to the residents</li>\r\n	<li>\r\n		not pool the Comfort and Clothing Allowance of residents</li>\r\n	<li>\r\n		allow residents to manage money for personal use in accordance with their competencies</li>\r\n</ul>\r\n<p>\r\n	On the death of clients, any accumulation of Comfort and Clothing monies belongs to the estate of the clients. Where the deceased has relatives known to the home, operators must write a cheque to the estate of the deceased for the accumulated Comfort and Clothing Allowance. Send this cheque to the next of kin.</p>\r\n', 'en');
INSERT INTO `manual_policies_desc` (`desc_id`, `manual_policies_id`, `title`, `content`, `language`) VALUES
(24, 23, 'Admission of private-pay resident in a special care home', '<p>\r\n	Private-pay residents may choose to be admitted in an approved special care home without a full LTC assessment. They must provide the operator with</p>\r\n<ul>\r\n	<li>\r\n		a certificate by a qualified physician as evidence of medical examination prior to admission</li>\r\n	<li>\r\n		evidence of an examination or a written social assessment indicating that the private-pay resident has social and personal needs that can be met in a special care home</li>\r\n</ul>\r\n<p>\r\n	The examination or the written social assessment must be completed by a health care professional from Mental Health Services, the Extra-Mural Program, or Social Development, and indicate that the client</p>\r\n<ul>\r\n	<li>\r\n		is an adult who require care and/or supervision services for more than three months</li>\r\n	<li>\r\n		is medically stable</li>\r\n	<li>\r\n		does not require regular nursing care</li>\r\n</ul>\r\n<p>\r\n	Where a special care home intends to admit a person under these conditions, the operator shall advise the Adult Residential Facility Coordinator at least ten working days before the proposed date of admission to the facility and provide the Adult Residential Facility Coordinator with</p>\r\n<ul>\r\n	<li>\r\n		a copy of the medical certificate of the person</li>\r\n	<li>\r\n		a copy of the examination or social assessment</li>\r\n</ul>\r\n<p>\r\n	Where a person is admitted to a special care home under these conditions, the operator shall advise the Adult Residential Facility Coordinator immediately of the person&rsquo;s admission<br />\r\n	<br />\r\n	Residents admitted under these conditions must remain private-pay for at least 12 months. For the form used in relation to the admission of private-pay residents, please see Appendix E in the ARF Standards.</p>\r\n', 'en'),
(25, 24, 'Insurance', '<p>\r\n	Operators will carry insurance to cover property damage.</p>\r\n<p>\r\n	Note: Social Development is only responsible for damage done to a facility by a resident when the resident is under the legal care of the Minister, that is, court ordered.</p>\r\n<p>\r\n	Operator has an insurance policy covering:</p>\r\n<ul>\r\n	<li>\r\n		proof of a minimum one million dollar ($1,000,000) liability to cover residents for any accident, mishap or other incidents on the premises</li>\r\n	<li>\r\n		liabilities to residents who are passengers in any vehicle owned by the operators.</li>\r\n</ul>\r\n<p>\r\n	Employees using their own vehicle to transport residents must show proof of a minimum of one million dollars ($1,000,000) liability coverage</p>\r\n<ul>\r\n	<li>\r\n		proof of a minimum of one million dollars ($1,000,000) liability to cover residents arising from any accident, mishap or other incidents incurred while on an outing either in the company of operators or their designates</li>\r\n</ul>\r\n<p>\r\n	Sometimes residents living in Adult Residential Facilities participate in external activities. These activities must follow safety rules approved by current laws or regulations and in accordance with the existing staff ratio.</p>\r\n', 'en'),
(26, 25, 'Care Supplies', '<p>\r\n	Operator will supply soap, shampoo, toothpaste and other toiletries to the residents. However, residents who prefer special brands must make those purchases with their comfort and clothing allowance.</p>\r\n<p>\r\n	Incontinence products are not included in the available care supplies and must be provided by the client. Financial support may be available via the case manager from Social Development to cover the cost of incontinent supplies.</p>\r\n', 'en'),
(27, 26, 'Conflict of Interest', '<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>1</strong></td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<strong>POLICY</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				1.01</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				An employee shall refrain from all Conflicts of Interest. If an employee becomes aware of, or becomes involved in, a Conflict of Interest, he or she shall immediately disclose such Conflict of Interest to the general manager. An employee must also disclose what could be &ldquo;perceived&rdquo; by an outsider as a Conflict of Interest. An employee cannot avoid the disclosure requirements because the employee feels that he or she was not, is not, or will not be influenced by the Conflict of Interest. No &quot;presumption of guilt&quot; is created by the mere existence of a relationship with outside firms. However, if an employee has any influence on transactions such as purchases, contracts, or leases, it is imperative that the employee disclose to the general manager as soon as possible the existence of any actual or potential Conflict of Interest so that safeguards can be established to protect all parties.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				An employee who is unsure whether something constitutes a Conflict of Interest shall immediately discuss the specific concern with the general manager in order to determine the residence&rsquo;s position on the issue.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(c)</td>\r\n			<td>\r\n				The general manager will determine whether:\r\n				<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles interest\">\r\n					<tbody>\r\n						<tr>\r\n							<td>\r\n								(i)</td>\r\n							<td>\r\n								a Conflict of Interest exists;</td>\r\n						</tr>\r\n						<tr>\r\n							<td>\r\n								(ii)</td>\r\n							<td>\r\n								the seriousness of the potential or actual Conflict of Interest;</td>\r\n						</tr>\r\n						<tr>\r\n							<td>\r\n								(iii)</td>\r\n							<td>\r\n								what steps are necessary to protect the residence against the Conflict of Interest.</td>\r\n						</tr>\r\n					</tbody>\r\n				</table>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"3\">\r\n				<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles interest\">\r\n					<tbody>\r\n						<tr>\r\n							<td>\r\n								In making this determination, general manager shall be guided by any other policies which may apply. Additionally, general manager may consult legal counsel if the Conflict of Interest involves an issue of legal sensitivity.</td>\r\n						</tr>\r\n					</tbody>\r\n				</table>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(d)</td>\r\n			<td>\r\n				<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n					<tbody>\r\n						<tr>\r\n							<td>\r\n								With regard to paragraph 1.01(c)(iii), the general manager may, depending on the circumstances, institute one or more of the following steps:\r\n								<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n									<tbody>\r\n										<tr>\r\n											<td>\r\n												(i)</td>\r\n											<td>\r\n												establish internal safeguards to protect the business from any Conflict of Interest;</td>\r\n										</tr>\r\n										<tr>\r\n											<td>\r\n												(ii)</td>\r\n											<td>\r\n												require that the employee cease to be involved in the situation giving rise to the Conflict of Interest;</td>\r\n										</tr>\r\n										<tr>\r\n											<td>\r\n												(iii)</td>\r\n											<td>\r\n												remove the employee from a position of influence over the business with respect to matters which give rise to the Conflict of Interest &mdash; e.g. if the conflict involves a relative&rsquo;s bid to provide services to the residence, employee may be precluded in participating in the decision-making process with respect to the bid;</td>\r\n										</tr>\r\n										<tr>\r\n											<td>\r\n												(iv)</td>\r\n											<td>\r\n												implement the procedures envisioned in SPP HR 2.11.NB &mdash; Employment of Relatives;</td>\r\n										</tr>\r\n										<tr>\r\n											<td>\r\n												(v)</td>\r\n											<td>\r\n												require that the employee re-pay any benefit he or she has received as a result of the Conflict of Interest;</td>\r\n										</tr>\r\n										<tr>\r\n											<td>\r\n												(vi)</td>\r\n											<td>\r\n												discipline the employee, up to and including termination for cause, for being involved in the Conflict of Interest. In determining the nature of such discipline, the general manager shall consider:\r\n												<ul>\r\n													<li>\r\n														the employee&rsquo;s disclosure of the existence of the Conflict of Interest, if such disclosure was made;</li>\r\n													<li>\r\n														whether any such disclosure was made promptly;</li>\r\n													<li>\r\n														the severity of the Conflict of Interest;</li>\r\n													<li>\r\n														whether the Conflict of Interest involved any kind of dishonesty;</li>\r\n													<li>\r\n														whether the residence was actually harmed by reason of the Conflict of Interest;</li>\r\n													<li>\r\n														the employee&rsquo;s past record for honesty;</li>\r\n													<li>\r\n														what harm could have resulted to the residence as a result of the Conflict of Interest;</li>\r\n													<li>\r\n														whether or not such harm, if any, can be repaired in any way; and</li>\r\n													<li>\r\n														whether disciplinary steps, short of termination, are adequate to deal with the infraction.</li>\r\n												</ul>\r\n											</td>\r\n										</tr>\r\n										<tr>\r\n											<td>\r\n												(vii)</td>\r\n											<td>\r\n												take any other action deemed advisable by the general manager in the circumstances.</td>\r\n										</tr>\r\n									</tbody>\r\n								</table>\r\n							</td>\r\n						</tr>\r\n					</tbody>\r\n				</table>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				1.02</td>\r\n			<td>\r\n				It is a requirement that incumbents of certain positions within the Company periodically complete the Confidential Declaration of Interest Questionnaire shown in Attachment A to this policy. The general manager shall develop and maintain a list of those positions required to complete the Questionnaire.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.03</td>\r\n			<td>\r\n				An employee who becomes aware of a Conflict of Interest or a potential Conflict of Interest and fails to immediately report that conflict is subject to discipline. Depending on the severity of the conflict or potential conflict, an employee who fails to report a Conflict of Interest may also be terminated for &ldquo;cause&rdquo; without notice or compensation in lieu thereof.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>2</strong></td>\r\n			<td>\r\n				<strong>PURPOSE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				2.01</td>\r\n			<td>\r\n				The purpose of this Statement of Policy and Procedure is to communicate the residence&rsquo;s position on what matters could constitute a Conflict of Interest to employees and to establish a protocol for disclosing and dealing with such Conflicts of Interest.\r\n				<p>\r\n					&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>3</strong></td>\r\n			<td>\r\n				<strong>SCOPE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				3.01</td>\r\n			<td>\r\n				This Statement of Policy and Procedures applies to all employees.\r\n				<p>\r\n					&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>4</strong></td>\r\n			<td>\r\n				<strong>RESPONSIBILITY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.01</td>\r\n			<td>\r\n				All employees are responsible for reading and understanding the Conflict of Interest policy and immediately disclosing to the general manager] any Conflicts of Interest which may occur within 24 hours of having knowledge of same.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	<br />\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				4.02</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				&nbsp;general manager is required to develop and maintain a list of those positions for which a Confidential Declaration of Interest Questionnaire is required and to ensure the incumbents of the positions on that list complete the Questionnaire on their accession to a position on the list and at least once a year thereafter.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				general manager is responsible for determining what, if any, further steps or actions should be taken regarding any disclosure received.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	<br />\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>5</strong></td>\r\n			<td>\r\n				<strong>DEFINITIONS</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.01</td>\r\n			<td>\r\n				&ldquo;Conflict of interest&rdquo; refers to a situation where an employee&rsquo;s personal relationship(s) or financial interest(s) could reasonably be seen as influencing the employee&rsquo;s duty to act in the best interests of the residence. Such Conflicts of Interest include but are not limited to:</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				holding shares in a corporation which seeks to do business with the residence,&nbsp; except where the corporation and the employee holds shares which are worth less than [1%] of the issued shares in a publicly-traded corporation;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				being a relative of an individual who is employed by or involved with a business which seeks to do business with the residence or does business with the residence;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(c)</td>\r\n			<td>\r\n				being involved with or investing in a business which is competitive to the business conducted by the residence;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(d)</td>\r\n			<td>\r\n				being retained by another company or organization, whether on a consultancy or part-time basis, to advise on business similar to that which is being transacted by the residence;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(e)</td>\r\n			<td>\r\n				accepting &ldquo;kickbacks&rdquo; or &ldquo;bribes&rdquo;;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(f)</td>\r\n			<td>\r\n				failing to advise the residence of a situation in which the Employment of Relatives policy would apply &mdash; See Policy HR 2.11.NB;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(g)</td>\r\n			<td>\r\n				&nbsp;failing to advise the residence of a situation in which SPP HR 2.12.NB &mdash; Gifts, Favours and Entertainment would apply;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(h)</td>\r\n			<td>\r\n				being investigated, charged, indicted or convicted for a criminal activity which may have an impact on the perception of the individual&rsquo;s business conduct and which, if publicly known, might affect the reputation of the residence as a company which maintains high ethical standards.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				5.02</td>\r\n			<td>\r\n				&ldquo;<strong>Relatives</strong>&rdquo; shall include spouse, common-law spouse, same sex partner, child, sibling, parent or grandparent, former spouses or common-law spouses, cousins or being related through marriage.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>6</strong></td>\r\n			<td>\r\n				<p>\r\n					<strong>REFERENCES and RELATED STATEMENTS of POLICY and PROCEDURE</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<ul>\r\n					<li>\r\n						SPP HR 2.09.NB &mdash; Termination of Employment</li>\r\n					<li>\r\n						SPP HR 2.11.NB &mdash; Employment of Relatives</li>\r\n					<li>\r\n						SPP HR 2.12.NB &mdash; Gifts, Favours, Entertainment</li>\r\n					<li>\r\n						SPP HR 5.06.NB &mdash; Discipline<br />\r\n						&nbsp;</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>7</strong></td>\r\n			<td>\r\n				<strong>PROCEDURE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.01</td>\r\n			<td>\r\n				Any employee who suspects or believes that he or she is involved in or may become involved in a Conflict of Interest must immediately disclose such Conflict of Interest fully and accurately to the general manager. The Confidential Declaration of Interest Questionnaire, shown in Attachment A to this policy, shall be used for this purpose.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.02</td>\r\n			<td>\r\n				The general manager shall investigate the disclosure and seek legal counsel if appropriate, regarding what steps should be taken as a result of the conflict of interest.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.03</td>\r\n			<td>\r\n				The general manager shall ensure that the Confidential Declaration of Interest Questionnaire (Attachment A) is completed by the incumbent(s) of the positions specified in Attachment B on their accession to any of those positions at least once each year thereafter.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.04</td>\r\n			<td>\r\n				A copy of this policy shall be provided to each employee on his or her first day of employment with the residence and periodically thereafter as deemed necessary by the general manager</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', 'en'),
(28, 27, 'Employment of Relatives', '<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>1</strong></td>\r\n			<td>\r\n				<strong>POLICY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.01</td>\r\n			<td>\r\n				Relatives of current employees may be employed only where:\r\n				<ul>\r\n					<li>\r\n						they will not be working directly for or supervising a relative;</li>\r\n					<li>\r\n						they will not be working directly above the relative&#39;s immediate superior; or</li>\r\n					<li>\r\n						they will not be working directly for the relative&#39;s immediate subordinate.</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.02</td>\r\n			<td>\r\n				Employees shall not be transferred into a reporting relationship described in paragraph 1.01. Exceptions to this Statement of Policy and Procedure will be made for situations which were in effect at the time of initial issuance of this policy.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.03</td>\r\n			<td>\r\n				In other cases where a conflict or the potential for conflict arises, even if there is no supervisory relationship involved, the parties may be separated by reassignment, if possible at the sole discretion of the residence.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.04</td>\r\n			<td>\r\n				An employee who is related to a candidate for employment shall not be involved in any aspect of the recruiting process so as to avoid any conflicts of interest or allegations of preferential treatment.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>2</strong></td>\r\n			<td>\r\n				<strong>PURPOSE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				2.01</td>\r\n			<td>\r\n				The purpose of this Statement of Policy and Procedure is to minimize the potential for claims of partiality in treatment at work and to minimize the potential for personal conflicts from outside the work environment being carried into day-to-day working relationships.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>3</strong></td>\r\n			<td>\r\n				<strong>SCOPE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				3.01</td>\r\n			<td>\r\n				This policy applies to all employees.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>4</strong></td>\r\n			<td>\r\n				<strong>RESPONSIBILITY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.01</td>\r\n			<td>\r\n				It is the responsibility of employees and/or candidates for employment with the residence to declare whether other employees are their relatives.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>5</strong></td>\r\n			<td>\r\n				<strong>DEFINITIONS</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.01</td>\r\n			<td>\r\n				&ldquo;<strong>Relative</strong>&rdquo; means any person who is a spouse, child, sibling, parent or grandparent of the employee or is related by marriage and includes in-laws, individuals who were previously married and are presently divorced, or whose relationship with the employee is similar to that of persons who are family members or related by marriage.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				6</td>\r\n			<td>\r\n				<strong>REFERENCES and RELATED STATEMENTS of POLICY and PROCEDURE</strong>\r\n				<ul>\r\n					<li>\r\n						SPP HR 2.01.NB &mdash; Employment Principles</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>7</strong></td>\r\n			<td>\r\n				<strong>PROCEDURE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.01</td>\r\n			<td>\r\n				Employees are requested to notify their managers of their relationship to other employees, or to candidates for employment.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.02</td>\r\n			<td>\r\n				In the event a reassignment becomes necessary as provided for in paragraph 1.03 of this policy, the parties affected by the reassignment will be asked to recommend which party is to be reassigned. The managers of the parties directly affected by the reassignment shall make the decision on reassignment having regard for the recommendation received from the parties, if any, department operating efficiency, the complexity of the job responsibilities of each party, the availability of qualified replacement personnel and the parties&rsquo; tenure in their respective jobs.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', 'en'),
(29, 28, 'Working Alone', '<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>1</strong></td>\r\n			<td>\r\n				<strong>POLICY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.01</td>\r\n			<td>\r\n				The residence will make every reasonable effort to schedule work to minimize the incidence of employees working alone. However, we recognize that there are certain circumstances in which employees will be required to work alone.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.02</td>\r\n			<td>\r\n				When employees are required to work alone, this residence is committed to providing and maintaining procedures which will promote a safe and healthy work environment.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>2</strong></td>\r\n			<td>\r\n				<strong>PURPOSE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				2.01</td>\r\n			<td>\r\n				The purpose of this policy is to provide a safe environment and promote safe work practices for employees who are required to work alone.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>3</strong></td>\r\n			<td>\r\n				<strong>SCOPE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				3.01</td>\r\n			<td>\r\n				This policy applies to all employees of the residence, while they are on duty, regardless of their work site.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>4</strong></td>\r\n			<td>\r\n				<strong>RESPONSIBILITY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.01</td>\r\n			<td>\r\n				<strong>Human Resources</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				The Human Resources Department is responsible for maintaining current information on safety risks and preventative measures for working alone situations, making supervisors aware of these issues, and updating this policy as necessary.&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				The Human Resources Department will produce written procedures, checklists or other tools to assist supervisors to conduct safety assessments.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				4.02</td>\r\n			<td>\r\n				<strong>Supervisors</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				Supervisors are responsible for being aware of this policy and procedure and for ensuring compliance by all employees within their jurisdiction.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				Supervisors are responsible for carrying out assessments, identifying risks, communicating risks to employees and taking steps to eliminate such risks. Supervisors must also ensure training, investigations and evaluations required by this policy are completed.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(c)</td>\r\n			<td>\r\n				Supervisors will record each incident, occurrence or concern of employees working alone, making note of the date, circumstances, resolutions and other details.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(d)</td>\r\n			<td>\r\n				Supervisors will take every step reasonable to respond to employees&rsquo; concerns or correct any unsafe situation.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				4.03</td>\r\n			<td>\r\n				<strong>Employees</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				&nbsp;Employees must report any concerns they have regarding health and safety to their Supervisor.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				Employees must follow security procedures and practise safe work practices.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>5</strong></td>\r\n			<td>\r\n				<strong>DEFINITIONS</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.01</td>\r\n			<td>\r\n				&ldquo;<strong>Working alone</strong>&rdquo; means that the employee is the only worker of the residence working at that workplace, in circumstances where assistance is not readily available to the worker in the event of injury, ill health or emergency.<br />\r\n				<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>6</strong></td>\r\n			<td>\r\n				<strong>REFERENCES and RELATED STATEMENTS of POLICY and PROCEDURE</strong><br />\r\n				<em>Occupational Health and Safety Act and Code of Practice for Working Alone Regulation</em> (New Brunswick)\r\n				<ul>\r\n					<li>\r\n						SPP F6.01.NB &mdash; Health and Safety Principles</li>\r\n					<li>\r\n						SPP F6.02.NB &mdash; Accident and Injury Reporting</li>\r\n					<li>\r\n						SPP F6.03.NB &mdash; Accident Investigation<br />\r\n						<br />\r\n						&nbsp;</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>7</strong></td>\r\n			<td>\r\n				<strong>PROCEDURE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.01</td>\r\n			<td>\r\n				Situations where employees may be working alone must be examined and identified. Written procedures must be developed and implemented to ensure, as far as is reasonably practicable, the health and safety of the worker from risks arising out of, or in connection with, the work assigned.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				The written procedures must include, but is not limited to:\r\n				<ul>\r\n					<li>\r\n						the name, address, location and telephone number of workplace;</li>\r\n					<li>\r\n						the name, address, location and telephone number of the residence;</li>\r\n					<li>\r\n						the nature of the business conducted at the workplace;</li>\r\n					<li>\r\n						identification of the possible risks to each employee working alone that arise from or in connection with the work assigned;</li>\r\n					<li>\r\n						the steps to be followed to minimize the risks identified, which include specifying the time intervals for checking on the employee; specifying the person responsible for contacting the employee and recording the results; outlining the process to be followed if the employee cannot be contacted including provisions for an emergency rescue; and a system to check with the employee at the end of the employee&rsquo;s shift;</li>\r\n					<li>\r\n						details of how the employee can secure, and how the operator can provide, assistance in the event of injury or other circumstances that may endanger the health or safety of the employee.</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.02</td>\r\n			<td>\r\n				Potential workplace hazards and safety risks must be identified and communicated to affected employees. Employees must be consulted when identifying risks and discussing potential corrections.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.03</td>\r\n			<td>\r\n				The Joint Health and Safety Committee or Health and Safety Representative, as applicable, and the affected employee(s) shall be involved in the process of assessment, identification and elimination or reduction of any risks.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.04</td>\r\n			<td>\r\n				Any identified hazards or risks shall be eliminated or reduced if practicable.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.05</td>\r\n			<td>\r\n				An effective means of communication, and other security measures shall be available to all employees who work alone, and appropriate training related thereto shall be provided.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.06</td>\r\n			<td>\r\n				Employees and Supervisors will be educated about the potential risks associated with working alone and shall be trained to take steps to prevent or eliminate such risks.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.07</td>\r\n			<td>\r\n				&nbsp;Employees working alone shall be checked on by a supervisor every [interval] and at end of shift, and the results of such contact shall be recorded.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.08</td>\r\n			<td>\r\n				In the event that the employee cannot be contacted when checked on, a supervisor shall physically locate employee to ensure his or her safety. In the unlikely event of an emergency, the supervisor shall implement emergency procedures.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.09</td>\r\n			<td>\r\n				All incidents, occurrences, concerns of employees will be recorded and assessed. Where possible, corrective measures will be taken to prevent recurrences.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.10</td>\r\n			<td>\r\n				All security practices and working alone procedures shall be thoroughly reviewed every [2] months and adjusted accordingly.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.11</td>\r\n			<td>\r\n				A copy of any written procedures related to working alone will be provided to each employee who is required to work alone. A copy of any written procedures related to working alone shall be made readily available to a Health and Safety Officer on request.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n', 'en');
INSERT INTO `manual_policies_desc` (`desc_id`, `manual_policies_id`, `title`, `content`, `language`) VALUES
(30, 29, 'Drug and Alcohol Testing', '<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>1</strong></td>\r\n			<td>\r\n				<strong>POLICY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.01</td>\r\n			<td>\r\n				All employees are required to report to work fit for duty regardless of whether or not they are eligible for testing under this policy.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.02</td>\r\n			<td>\r\n				The residence&rsquo;s testing policy and procedures and limits are consistent with the provisions set out in <em>The Human Right Act</em> (New Brunswick), <em>The Personal Health</em> <em>Information Privacy and Access Act</em> (New Brunswick) (PHIPAA) and common law principles.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.03</td>\r\n			<td>\r\n				The residence conducts alcohol and drug testing in the following situations:\r\n				<ul>\r\n					<li>\r\n						Post-incident</li>\r\n					<li>\r\n						Based on reasonable grounds</li>\r\n					<li>\r\n						Post-reinstatement</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.04</td>\r\n			<td>\r\n				Post-incident testing may be conducted as part of an investigation into an incident where it has been determined by a supervisor/manager or health and safety committee or representative investigating the incident that the individual&rsquo;s acts or omissions could have caused or contributed to the occurrence.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.05</td>\r\n			<td>\r\n				Reasonable grounds testing is conducted when an individual&rsquo;s actions, appearance or conduct while at work give reasonable grounds to suspect impairment due to the consumption or use of alcohol, drugs or certain medications.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.06</td>\r\n			<td>\r\n				Post-reinstatement testing may be conducted where an employee is returning to a safety-sensitive job after treatment for alcohol or drug addiction. An employee may be expected to meet certain conditions when they return to work, which may include unannounced testing.&nbsp;<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.07</td>\r\n			<td>\r\n				Alcohol and drug testing is just one process used to identify impairment and control the consumption of alcohol and use of drugs in the workplace. Testing and testing procedures are designed to respect the individual&rsquo;s privacy and meet applicable requirements for confidentiality (in accordance with the residence&rsquo;s privacy practices), accuracy and quality. All information regarding drug or alcohol tests will be kept confidential and used only for the purpose for which it was collected or as required by law. As with all health information, records will be stored in separate locked files or encrypted electronic files and accessed only by those with proper authority. Regardless of whether the personal health information is stored in a paper-based system or electronic system, it is essential that employees trust that their privacy will be protected.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.08</td>\r\n			<td>\r\n				Every decision to test or not test will be clearly documented and include the date, time, description of events and information that led to the decision.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.09</td>\r\n			<td>\r\n				When a drug test of an employee has a positive result, the residence will take into consideration the employee&rsquo;s use of any prescribed medication, including medical marijuana, and any substance dependency disclosed to the residence and will work with the employee to determine if any accommodation of the employee is required in accordance with SPP HR.5.04.NB &mdash; Accommodation on the Basis of Disability<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.10</td>\r\n			<td>\r\n				Failure to report for a test, refusal to submit to a test, refusal to sign the consent form or any attempt to tamper with a test sample may be grounds for disciplinary action up to and including termination of employment for cause. The circumstances around the refusal will be examined on a case-by-case basis, considering the reasons for refusing the test and other relevant factors, such as the employee&rsquo;s service record.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>2</strong></td>\r\n			<td>\r\n				<strong>PURPOSE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				2.01</td>\r\n			<td>\r\n				This policy is intended to operate in conjunction with, and as a supplement to, the provisions of SPP HR 6.06.NB &mdash; Alcohol and Drugs in the Workplace and HR.5.04.NB &mdash; Accommodation on the Basis of Disability.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				2.02</td>\r\n			<td>\r\n				The residence recognizes that the use of alcohol and drugs can adversely affect job performance, the work environment and the safety of its employees and the public. Employees in designated safety-sensitive positions will be subject to testing in accordance with this policy in order to ensure the safety of all employees and the public at large.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>3</strong></td>\r\n			<td>\r\n				<strong>SCOPE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				3.01</td>\r\n			<td>\r\n				This Statement of Policy and Procedure applies to all employees in designated safety-sensitive positions.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>4</strong></td>\r\n			<td>\r\n				<strong>RESPONSIBILITY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.01</td>\r\n			<td>\r\n				Supervisors/managers are responsible for obtaining the written consent of an employee in a designated safety-sensitive position to submit to drug or alcohol testing, as well as the employee&rsquo;s consent to the collection of information regarding his or her health related to drug or alcohol use.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.02</td>\r\n			<td>\r\n				Supervisors/managers must monitor situations where there are reasonable grounds to believe there has been a violation of the Alcohol and Drugs in the Workplace policy; recognize the effects of alcohol, drugs and medications in the workplace; monitor the work performance of teams and take action regarding any violations or suspected violations.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.03</td>\r\n			<td>\r\n				Supervisors/managers must determine if alcohol and drug testing is required in accordance with this policy and arrange for testing in a timely manner.&nbsp;<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.04</td>\r\n			<td>\r\n				Supervisors/managers must coordinate and conduct incident investigations with due consideration of potential alcohol and drug impairment and make decisions on alcohol and drug testing that may result from an investigation.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.05</td>\r\n			<td>\r\n				Supervisors/managers must oversee employee treatment, monitoring and return&ndash;to-work programs.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.06</td>\r\n			<td>\r\n				Supervisors/managers must oversee the interpretation and communication of employee alcohol and drug testing results.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.07</td>\r\n			<td>\r\n				Employees are responsible for complying with the provisions of SPP HR 6.06.NB &mdash; Alcohol and Drugs in the Workplace and this policy.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.08</td>\r\n			<td>\r\n				Employees must report immediately to a supervisor or manager situations where there are reasonable grounds to believe that he or she, or a co-worker, is not fit for duty due to impairment from the use of alcohol or drugs.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.09</td>\r\n			<td>\r\n				Employees must disclose and take responsibility for resolving personal alcohol or drug problems and cooperate with any investigation into a possible violation of the Alcohol and Drug Program.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>5</strong></td>\r\n			<td>\r\n				<strong>DEFINITIONS</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.01</td>\r\n			<td>\r\n				&ldquo;<strong>Designated safety-sensitive position</strong>&rdquo; is a position that the residence determines has a role in the operation where impairment due to drugs or alcohol in the performance of the duties of the position could result in a significant risk of injury to the employee, other employees, the public, property or the environment. In determining which positions are designated safety-sensitive positions the residence will examine what consequences are risked if the person performing the particular kind of work does so while impaired by drugs or alcohol. The residence will also evaluate the specific work being performed and the nature of the equipment/materials operated or used.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<strong>[Note: Consider the following definitions:</strong>\r\n				<ol>\r\n					<li>\r\n						<em>&ldquo;A position which the Company determines has a role in the operation where impaired performance could result in a significant incident affecting the health and safety of employees, customers, customers&rsquo; employees, the public, property or the environment.&rdquo; (J.D. Irving Ltd v CEP [2002],111 L.A.C. [4th] 328)</em></li>\r\n					<li>\r\n						<em>&ldquo;A safety sensitive position is one in which incapacity due to drug or alcohol impairment could result in direct and significant risk of injury to the employee, others or the environment.&rdquo; (The Canadian Human Rights Commission)]</em><br />\r\n						&nbsp;</li>\r\n				</ol>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.02</td>\r\n			<td>\r\n				&ldquo;<strong>Drug</strong>&rdquo; refers to any substance that is taken to change the way the body and/or mind functions (for example: alcohol or illicit, recreational or prescribed drugs) (source: RCMP).&nbsp;<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.03</td>\r\n			<td>\r\n				&ldquo;<strong>Substance abuse</strong>&rdquo; is when someone uses drugs in a way they are not intended to be used, obtains them from illegitimate sources or uses more than the recommended dosage or when drug usage affects his or her daily life, activities or relationships negatively (source: RCMP).</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.04</td>\r\n			<td>\r\n				&ldquo;<strong>Drug addiction</strong>&rdquo; refers to when someone engages in regular, long-term drug use despite knowing the risks of doing so. The user may have a psychological dependence or physical dependence and experience withdrawal symptoms if they do not consume the drugs within a certain period of time. When a person continues to use drugs over a period of time, that person may develop a tolerance to that drug; in other words, the individual&rsquo;s body becomes used to the drug and larger doses are required to achieve the same effects (source: RCMP).<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.05</td>\r\n			<td>\r\n				&ldquo;<strong>Legal drugs</strong>&rdquo; include drugs that are regulated yet legal to consume, when done within the limits of the regulations. While these products can be consumed legally, they can still produce undesirable effects on one&rsquo;s health, body and social life (i.e., cannabis, alcohol) (source: RCMP).<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.06</td>\r\n			<td>\r\n				&ldquo;<strong>Prescription drugs</strong>&rdquo; are drugs that are regulated by the Federal Government of Canada under the <em>Canadian</em> <em>Food</em> <em>and</em> <em>Drugs</em> <em>Act</em> (the regulation system for pharmaceutical and over-the-counter products such as cough syrup, pain killers, etc.) (source: RCMP).<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.07</td>\r\n			<td>\r\n				&ldquo;<strong>Illegal drugs</strong>&rdquo; are usually produced (grown or manufactured) for sale on the street and used mainly for recreational purposes. They are often chemical products or other substances that produce a mind-altering effect when ingested (i.e., cocaine, crack, heroin, methamphetamine) (source: RCMP).<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.08</td>\r\n			<td>\r\n				&ldquo;<strong>Alcohol</strong>&rdquo; is any substance that may be consumed and that has an alcoholic content in excess of 0.5 percent by volume (source: RCMP).<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>6</strong></td>\r\n			<td>\r\n				<strong>REFERENCES and RELATED STATEMENTS of POLICY and PROCEDURE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<ul>\r\n					<li>\r\n						The Human Rights Act (New Brunswick)</li>\r\n					<li>\r\n						The Personal Health Information Privacy and Access Act (New Brunswick)</li>\r\n					<li>\r\n						The Occupational Health and Safety Act (New Brunswick)</li>\r\n					<li>\r\n						&nbsp;Personal Information Protection and Electronic Documents Act (Canada)</li>\r\n					<li>\r\n						SPP HR.5.04.NB &mdash; Accommodation on the Basis of Disability</li>\r\n					<li>\r\n						SPP HR.5.07.NB &mdash; Personal Information Protection</li>\r\n					<li>\r\n						SPP HR.6.06.NB &mdash; Alcohol and Drugs in the Workplace</li>\r\n					<li>\r\n						&nbsp;SPP HR 5.05.NB &mdash; Conduct and Behaviour</li>\r\n					<li>\r\n						&nbsp;SPP HR 5.06.NB &mdash; Discipline</li>\r\n					<li>\r\n						&nbsp;SPP HR 6.02.NB &mdash; Accident and Injury Reporting</li>\r\n					<li>\r\n						&nbsp;SPP HR 6.03.NB &mdash; Accident Investigation<br />\r\n						&nbsp;</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>7</strong></td>\r\n			<td>\r\n				<strong>PROCEDURE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.01</td>\r\n			<td>\r\n				The decision to require an employee in a designated safety-sensitive position to submit to drug or alcohol testing must only be made by a supervisor/manager trained in this Policy, with the concurrence of a second trained person whenever possible.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.02</td>\r\n			<td>\r\n				Drug and alcohol testing will be administered at the workplace, whenever possible, as long as there are qualified personnel available; otherwise testing will be administered at a location designated by the residence. Drug or alcohol testing will be performed by qualified professionals in a safe, secure and private environment.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.03</td>\r\n			<td>\r\n				<strong>Post-Incident or Near-Miss Testing</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				After a workplace accident, a safety-related incident or a near miss, if, in the opinion of the responsible supervisor/manager or health and safety committee or representative, there is a reasonable belief that impairment due to alcohol or drug use may have been a contributing factor, the employee(s) in a designated safety-sensitive position involved in the incident will submit to drug or alcohol testing in a timely manner as part of the investigation as soon as reasonably practicable after the incident.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				Employees in designated safety-sensitive positions, when informed of the requirement to test, will receive a private communication and will be required to provide written consent to the testing and to the release of results directly to the residence.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(c)</td>\r\n			<td>\r\n				Collection and administration of post-incident alcohol and drug tests must be performed by trained collection agents. The employee must be escorted to the collection site if the test is for post-incident purposes. Samples must be collected within [8] hours after the incident for an alcohol test and [32] hours after the incident for a drug test [these timelines may vary depending on the testing site you use. Please make sure you use their timelines]. If a worker requires immediate medical attention, alcohol and drug testing may occur at the discretion of the medical service provider at the time of treatment. Once discharged from the medical facility, alcohol and drug testing can be conducted in accordance with this guideline and respecting the applicable timeframes.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(d)</td>\r\n			<td>\r\n				Where an employee in a designated safety-sensitive position undergoes drug or alcohol testing and the test results are positive, the results will be dealt with in accordance with applicable policies including, but not limited to, SPP HR 5.04.NB &mdash; Accommodation on the Basis of Disability, SPP HR 5.05.NB &mdash; Conduct and Behaviour, SPP HR 5.06.NB &mdash; Discipline, SPP HR 6.02.NB &mdash; Accident Reporting, SPP HR 6.03.NB &mdash; Accident Investigation and SPP HR 6.06.NB &mdash; Alcohol and Drugs in the Workplace.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cass=\"sub-titles\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				7.04</td>\r\n			<td>\r\n				<strong>Reasonable Grounds Testing</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				Reasonable grounds testing is conducted when an individual&rsquo;s actions, appearance or conduct while at work give reasonable grounds to suspect the consumption or use of alcohol, drugs or certain medications. If a supervisor/manager or other individual reasonably believes there are reasonable grounds, where possible, they must engage a second person to confirm the assessment of the individual&rsquo;s actions, appearance and conduct.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				Referral of an individual for testing will be based on personal observations. Examples of reasonable grounds may include, but are not limited to:\r\n				<ul>\r\n					<li>\r\n						direct visual observation of a policy violation;</li>\r\n					<li>\r\n						discovery of alcohol or illegal or legal drugs that are prohibited in a company vehicle or on company premises that can reasonably be associated with a particular staff member;</li>\r\n					<li>\r\n						direct visual observation of physical or behavioural signs (e.g., difficulty maintaining coordination, slurred speech, smell of alcohol on breath, extreme drowsiness, etc.); or</li>\r\n					<li>\r\n						a pattern of failing to follow safety rules or operating procedures.</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(c)</td>\r\n			<td>\r\n				If a supervisor/manager or other individual believes there are reasonable grounds for testing, he or she should engage another supervisor, manager, health and safety committee member or representative or security staff on site to confirm the assessment of the individual&rsquo;s actions, appearance or conduct.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(d)</td>\r\n			<td>\r\n				Referral of an individual for testing should be based on specific personal observations and should be documented and also include prior discussions with the employee as to whether there might be a legitimate, reasonable, non-impairment related rationale to explain the employee&rsquo;s conduct.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(e)</td>\r\n			<td>\r\n				Employees informed of the requirement to test will be communicated with in private and will be required to provide written consent to the testing and to the release of results directly to the residence.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(f)</td>\r\n			<td>\r\n				The collection and administration of reasonable-grounds alcohol and drug tests must be performed by trained collection agents. Documentation (employers should have a standard form) is required when alcohol or drug use is suspected by workers in safety-sensitive positions and testing is considered.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(g)</td>\r\n			<td>\r\n				The collection and administration of reasonable grounds drug tests must be performed by trained collection agents. The employee must be escorted to the collection site if the test is for reasonable grounds purposes.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(h)</td>\r\n			<td>\r\n				Where an employee undergoes drug or alcohol testing and the test results are positive, the results will be dealt with in accordance with applicable policies including, but not limited to,\r\n				<ul>\r\n					<li>\r\n						SPP HR 5.04.NB &mdash; Accommodation on the Basis of Disability</li>\r\n					<li>\r\n						SPP HR 5.05.NB &mdash; Conduct and Behaviour</li>\r\n					<li>\r\n						SPP HR 5.06.NB &mdash; Discipline</li>\r\n					<li>\r\n						SPP HR 6.02.NB &mdash; Accident and Injury Reporting</li>\r\n					<li>\r\n						SPP HR 6.03.NB &mdash; Accident Investigation</li>\r\n					<li>\r\n						SPP HR 6.06.NB &mdash; Alcohol and Drugs in the Workplace.</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				7.05</td>\r\n			<td>\r\n				<strong>Procedures When Testing Is Chosen</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				Once a supervisor/manager, working as appropriate with the health and safety committee or representative, has made a decision to send an employee for post-incident, near-miss or reasonable grounds testing, the following steps should be taken:</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<ul>\r\n					<li>\r\n						Every decision to test or not test will be clearly documented and include the date, time, description of events and information that led to the decision.</li>\r\n					<li>\r\n						The supervisor/manager should inform the employee that they will be referred for testing.</li>\r\n					<li>\r\n						the supervisor/manager or the supervisor/manager&rsquo;s delegate must transport the employee to the testing facility promptly, and in any event no later than in the timeframes set out in this policy or attached guidelines (<em>employers can create detailed guidelines and criteria for testing</em>)<em>.</em></li>\r\n					<li>\r\n						The Testing Program Administrator [i.e., the breath alcohol technician (BAT) or the screening test technician (STT)] will instruct the employee as to the testing procedures, including identification of the employee.</li>\r\n					<li>\r\n						The employee must follow the instructions of his or her supervisor/manager and the Testing Program Administrator, and he or she cannot return to duty unless compliant test results are obtained. The employee will continue to be paid his or her wages as usual at all times while following employer-issued directions. Non-compliant test results will be addressed in accordance with SPP HR 6.06.NB &mdash; Alcohol and Drugs in the Workplace.</li>\r\n					<li>\r\n						The employee&rsquo;s supervisor/manager will receive notice as to whether the test results were compliant or non-compliant and proceed as per the company policy stated in 7.03(d) and 7.04(h).</li>\r\n					<li>\r\n						The company must securely store information about alcohol or drug test results to ensure that disclosure to unauthorized persons does not occur.</li>\r\n					<li>\r\n						After a person tests positive on a drug or alcohol test, that individual should be advised of the availability of accommodations. Employers should offer an individualized assessment conducted by someone with expertise in substance use disorders to inquire into possible disability and assess any support needed. Accommodation must be offered to people with addictions unless it causes undue hardship.</li>\r\n					<li>\r\n						f an employee&#39;s drug or alcohol addiction is interfering with his or her ability to perform the essential duties of his or her job, the employer will provide the support necessary to enable that person to undertake a rehabilitation program, if required, unless it can be shown that such accommodation would&nbsp; cause undue hardship for the residence. The type of program or accommodation would depend on the person&rsquo;s individualized needs. Examples could include harm reduction or abstinence-based approaches. Other accommodations (e.g., modified duties) may also need to be explored.<br />\r\n						&nbsp;</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.06</td>\r\n			<td>\r\n				<strong>Post-Reinstatement Testing</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				The residence provides support to employees with personal alcohol and drug problems.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				When an employee enters into a return-to-work agreement, he or she will be referred to a qualified professional, such as a Substance Abuse Expert, who will assess the individual, make recommendations regarding treatment and recommend a return-to-work monitoring program that may include alcohol or drug testing.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', 'en'),
(31, 30, ' Personal Phone Calls, Use of Cellular Phones and Other Wireless Communication Devices', '<p>\r\n	<strong>POLICY</strong></p>\r\n<p>\r\n	Staff shall use their working hours productively.&nbsp; Personal phone calls shall not be received while the employee is at work unless it is very important.</p>\r\n<p>\r\n	If staff members receive incoming personal phone calls while at work, the call time should be kept to a minimum. Staff should make their families (and any other persons) aware of this. Staff are to make use of their mealtime breaks to make their phone calls &ndash; if necessary.</p>\r\n<p>\r\n	Wireless Communication Devices:</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				a)</td>\r\n			<td>\r\n				<u>Ban on Wireless Communication devices</u>: Employees are not permitted to have wireless communication devices on their person in the workplace at any time.&nbsp; This includes cellular phones, iPads, and any other wireless devices.&nbsp; The reasons for this are as follows:</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<ol>\r\n					<li>\r\n						Wireless devices can and do serve as a distraction to employees.&nbsp; Becoming distracted while at work can and will jeopardize the wellbeing and safety of residents, the employee, and co-workers.</li>\r\n					<li>\r\n						Employees are required to devote full time attention to resident care and wellbeing at all times while on duty;</li>\r\n					<li>\r\n						Wireless devices can and do get damaged in the workplace.&nbsp; The employer will not be responsible for any damage to wireless device if they are brought to the workplace.&nbsp; Employees who bring wireless devices to the workplace must leave them in their purse or jacket; these devices may be accessed during approved breaks and approved lunch periods.&nbsp; In no situation must wireless devices be used in corridors, within hearing of the public, or in any public area.&nbsp; When conversing or texting, it is only courteous to all concerned to perform those functions in private.&nbsp;</li>\r\n				</ol>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				b)</td>\r\n			<td>\r\n				<strong><u>Scope</u></strong>: The ban on the use of wireless communication devices above also applies to all workers while on duty who are operating vehicles, whether owned by the company, the individual worker, or a rental vehicle.&nbsp; Exceptions to this rule apply only as follows:</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				c)</td>\r\n			<td>\r\n				&nbsp; &nbsp; c) <u>Handling calls while driving a company vehicle:</u>\r\n				<ul>\r\n					<li>\r\n						Incoming calls or texts: If the phone rings, don&rsquo;t answer it until you pull over in a safe spot or let a passenger answer the call. If it is urgent, you may accept or return the call, if you remain parked off the roadway. You may not resume driving until your conversation ends.</li>\r\n					<li>\r\n						Outgoing calls or texts: You may not make outgoing calls while driving. If you want to place a call, pull over in a safe spot first.</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	Violations of the foregoing rules will be considered a serious offence and may result in the imposition of discipline up to and including termination.</p>\r\n', 'en'),
(32, 31, 'Safety Policy', '<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>1</strong></td>\r\n			<td>\r\n				<strong>POLICY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.01</td>\r\n			<td>\r\n				In co-operation with employees, the residence is committed to creating and maintaining a safe workplace in order to minimize and/or prevent occupational injuries and illnesses. Consistent and continuous efforts by all employees shall be directed to preventing workplace accidents and maintaining the workplace and equipment in a safe condition.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.02</td>\r\n			<td>\r\n				At all times, the residence and its employees are required to observe and comply with the requirements of the New Brunswick <em>Occupational Health and Safety Act </em>and its regulations.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.03</td>\r\n			<td>\r\n				<strong>[Optional] </strong>Health and safety responsibilities shall be specified in the Job Description for each job.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.04</td>\r\n			<td>\r\n				Everyone who undertakes, or has the authority, to direct how another person does work or performs a task is under a legal duty to take reasonable steps to prevent bodily harm to that person, or any other person, arising from the work or task.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>2</strong></td>\r\n			<td>\r\n				<strong>PURPOSE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				2.01</td>\r\n			<td>\r\n				The purpose of this Statement of Policy and Procedure is to establish minimum guidelines for providing and maintaining a safe work environment for all employees.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>3</strong></td>\r\n			<td>\r\n				<strong>SCOPE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				3.01</td>\r\n			<td>\r\n				This Statement of Policy and Procedure applies to all employees.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>4</strong></td>\r\n			<td>\r\n				<strong>RESPONSIBILITY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.01</td>\r\n			<td>\r\n				The senior on-site manager or supervisor at each location, in consultation with a Joint Health and Safety Committee or an employee Health and Safety Representative, as appropriate, is responsible for developing and publishing a comprehensive Location Health and Safety Program and for ensuring that appropriate health and safety standards are developed, implemented and maintained in accordance with the provisions of the New Brunswick <em>Occupational Health and Safety Act.</em><br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.02</td>\r\n			<td>\r\n				Managers and supervisors are directly responsible for ensuring the health and safety of employees under their supervision and for ensuring:\r\n				<ul>\r\n					<li>\r\n						safe and healthy work conditions are maintained in their areas of responsibility;</li>\r\n					<li>\r\n						&nbsp;employees perform their work in compliance with accepted safe work practices and procedures;</li>\r\n					<li>\r\n						&nbsp;adequate training is provided to employees so that tasks assigned to employees can be performed safely;</li>\r\n					<li>\r\n						employees are notified of any potential hazards which may exist in and around the employee&rsquo;s work location.</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.03</td>\r\n			<td>\r\n				Each employee is responsible for working safely in compliance with accepted safe work practices, procedures and legislated health and safety standards.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.04</td>\r\n			<td>\r\n				Contractors and their workers are responsible for meeting or exceeding the requirements of the Location Health and Safety Program.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>5</strong></td>\r\n			<td>\r\n				<strong>DEFINITIONS</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.01</td>\r\n			<td>\r\n				&ldquo;<strong>New employee</strong>&rdquo; means an employee who is</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				new to a position or place of employment,</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				returning to a position or place of employment in which the hazards have changed during the employee&rsquo;s absence,</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(c)</td>\r\n			<td>\r\n				under 25 years of age and returning to a position or a workplace after an absence of more than six months, or</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(d)</td>\r\n			<td>\r\n				affected by a change in the hazards of a position or the workplace.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	<br />\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>6</strong></td>\r\n			<td>\r\n				<strong>REFERENCES and RELATED STATEMENTS of POLICY and PROCEDURE</strong>\r\n				<ul>\r\n					<li>\r\n						<em>Occupational Health and Safety Act</em> and Regulations (OHSA) (New Brunswick)</li>\r\n					<li>\r\n						<em>Criminal</em> Code (Canada)<br />\r\n						<br />\r\n						&nbsp;</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles safety-policy\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>7</strong></td>\r\n			<td>\r\n				<strong>PROCEDURE</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				7.01</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				At each company site employing more than five but fewer than 20 employees, at least one employee Health and Safety Representative (HSR) shall be selected by employees to deal with workplace health and safety concerns.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				At each company site employing 20 or more employees, a Joint Health and Safety Committee (JHSC) shall be established. The Committee shall deal with the location&rsquo;s health and safety concerns. The membership of the Committee shall be comprised equally of at least two employee representatives and two representatives of management, as agreed by the residence and employees.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<br />\r\n<br />\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				7.02</td>\r\n			<td>\r\n				The senior site manager or supervisor in charge of each company location, in cooperation with the Joint Health and Safety Committee or the employee Health and Safety Representative, as appropriate, shall ensure that:</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles safety-policy1\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				The residence&rsquo;s Health and Safety Policy is posted at each location and effectively communicated to each employee.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				the names of JHSC members or HSR are posted, together with any minutes of JHSC meeting minutes;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(c)</td>\r\n			<td>\r\n				a Location Health and Safety Program is developed and published for distribution to every employee. As a minimum standard, the contents of the program shall contain information on the following topics:</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<ul>\r\n					<li>\r\n						the training and supervision of the employees in matters necessary to their health and safety;</li>\r\n					<li>\r\n						the preparation of written work procedures and codes of practice for the implementation of health and safety work practices, required by the OHSA;</li>\r\n					<li>\r\n						the identification of the types of work for which the work procedures are required;</li>\r\n					<li>\r\n						a hazard identification system that includes</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles safety-policy2\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				i.evaluation of the workplace to identify potential hazards,</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				ii.procedures and schedules for inspections, and&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				iii.procedures for ensuring the reporting of hazards, prompt follow-up and control of the hazards;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles safety-policy1\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<ul>\r\n					<li>\r\n						a system for the prompt investigation of hazardous occurrences to determine their causes and the actions needed to prevent recurrences;</li>\r\n					<li>\r\n						a record management system that includes reports of employee training, accident statistics, work procedures and health and safety inspections, maintenance, follow-up and investigations;</li>\r\n					<li>\r\n						health and safety monitoring and reporting procedures;</li>\r\n					<li>\r\n						other information deemed advisable or necessary by local management and/or the JHSC or HSR, for the protection of employees;</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<strong>[Optional for inclusion in a Location Health and Safety Program</strong>\r\n				<ul>\r\n					<li>\r\n						accident/injury reporting</li>\r\n					<li>\r\n						accident investigation procedures;</li>\r\n					<li>\r\n						Workplace Hazardous Material Information System (WHMIS);</li>\r\n					<li>\r\n						first aid arrangements and procedures;</li>\r\n					<li>\r\n						equipment lockout procedures;</li>\r\n					<li>\r\n						emergency spill procedures, if appropriate;</li>\r\n					<li>\r\n						driver licensing program for in-plant vehicles, if appropriate, and]</li>\r\n					<li>\r\n						monitoring the implementation and effectiveness of the program.</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(d)</td>\r\n			<td>\r\n				the location develops and publishes an Emergency Plan which outlines procedures to be followed in the event of an emergency arising from fire, theft, break-in, bomb threats, power failure, or other natural disasters. Employee emergency response training, including fire training and drills, shall be conducted on a regular basis;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(e)</td>\r\n			<td>\r\n				the Location Health and Safety Program, plans, and practices are in compliance with the legislated requirements governing the location.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(f)</td>\r\n			<td>\r\n				the Location Health and&nbsp; Safety Program is reviewed at least once per year and updated as required. If requested, a copy shall be made available to the JHSC or HSR or to an employee.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				7.03</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				&nbsp;A new employee shall receive orientation and training specific to the new employee&rsquo;s position and place of employment before the new employee begins work. The orientation for a new employee shall include the following:</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<ul>\r\n					<li>\r\n						the name and contact information of the new employee&rsquo;s supervisor;</li>\r\n					<li>\r\n						the contact information of the committee or the health and safety representative;</li>\r\n					<li>\r\n						the new employee&rsquo;s rights, liabilities and duties under this Act and the regulations, including reporting requirements and the right to refuse to perform an act under section 19 of the OHSA;</li>\r\n					<li>\r\n						the health and safety procedures and codes of practice related to the new employee&rsquo;s job tasks;</li>\r\n					<li>\r\n						he location of first aid facilities and how to obtain first aid;</li>\r\n					<li>\r\n						the procedures related to the reporting of illnesses and injuries;</li>\r\n					<li>\r\n						the procedures related to emergencies; and</li>\r\n					<li>\r\n						the use of personal protective equipment, if applicable.</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				Records of the orientation and training of new employees shall be kept for at least three years.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				7.04</td>\r\n			<td>\r\n				Disciplinary action up to and including termination for cause shall be taken against any employee who fails to observe this policy or who violates established workplace safety requirements.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', 'en'),
(33, 32, ' Accident and Injury Reporting', '<p>\r\n	See <span style=\"color:#0000ff;\"><u>WorkSafeNB</u></span></p>\r\n', 'en');
INSERT INTO `manual_policies_desc` (`desc_id`, `manual_policies_id`, `title`, `content`, `language`) VALUES
(34, 33, 'Joint Health and Safety Committee', '<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>1</strong></td>\r\n			<td>\r\n				<strong>POLICY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.01</td>\r\n			<td>\r\n				The residence shall establish and maintain a Joint Health and Safety Committee (JHSC). The membership of the Committee shall be comprised equally of at least <strong>[<span style=\"color:#ffa500;\">2</span>]</strong> employee representatives and <strong>[<span style=\"color:#ffa500;\">2</span><span style=\"color:#000000;\">] </span></strong>representatives of management, as determined by the Committee. Employee representatives shall be elected by other employees or appointed in accordance with the constitution of any applicable union. Management representatives shall be appointed by the residence.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.02</td>\r\n			<td>\r\n				The residence fully supports the work of the JHSC in making the residence a safer place to work and will endeavour to co-operate with the JHSC whenever possible.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.03</td>\r\n			<td>\r\n				All managers, supervisors and employees are expected to extend their full co-operation and support to the Committee and its individual members.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>2</strong></td>\r\n			<td>\r\n				<strong>PURPOSE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				2.01</td>\r\n			<td>\r\n				The purpose of this policy is to ensure practices and procedures within the residence support a safe workplace by assisting the work of the JHSC.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>3</strong></td>\r\n			<td>\r\n				<strong>SCOPE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				3.01</td>\r\n			<td>\r\n				This policy applies to all employees.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>4</strong></td>\r\n			<td>\r\n				<strong>RESPONSIBILITY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.01</td>\r\n			<td>\r\n				Supervisors are responsible for responding to employees&rsquo; queries and/or complaints regarding safety matters and for providing information and assistance requested by the JHSC.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.02</td>\r\n			<td>\r\n				Employees are responsible for reporting any health and safety concerns to their immediate supervisors. Failing resolution of a concern at this point, employees should consult with a member from the JHSC. Employees must also co-operate with the JHSC and provide any information or assistance requested by the JHSC.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>5</strong></td>\r\n			<td>\r\n				<strong>DEFINITIONS</strong><br />\r\n				None.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>6</strong></td>\r\n			<td>\r\n				<strong>REFERENCES and RELATED STATEMENTS of POLICY and PROCEDURE</strong>\r\n				<ul>\r\n					<li>\r\n						<em>Occupational Health and Safety Act</em> and Regulations (OHSA) (New Brunswick)</li>\r\n					<li>\r\n						<em>Workers Compensation Act</em> (WCA) (New Brunswick)</li>\r\n					<li>\r\n						SPP F6.01.NB &mdash; Health and Safety Principles</li>\r\n					<li>\r\n						SPP F6.02.NB &mdash; Accident and Injury Reporting</li>\r\n					<li>\r\n						SPP F6.03.NB &mdash; Accident Investigation</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>7</strong></td>\r\n			<td>\r\n				<strong>PROCEDURE</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				7.01</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				The residence will select two members to represent management interests on the JHSC, one with experience with office operations and one with plant experience. Management nominees must have at least one year of service with the residence in order to serve on the Committee. The residence will endeavour to fill its representatives&rsquo; vacancies prior to the next JHSC meeting.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				Employee representatives to the Committee shall be nominated and elected by non-management employees on a date to be established by the Committee.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"sub-titles\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				7.02</td>\r\n			<td>\r\n				JHSC members are considered &ldquo;on duty&rdquo; when engaged in JHSC business and are entitled to at least one hour of preparation time prior to each meeting. Supervisors are required to use the appropriate JHSC reason codes when recording JHSC-related absences.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.03</td>\r\n			<td>\r\n				The residence shall provide appropriate meeting space for JHSC business to be conducted. It is the Committee&rsquo;s responsibility to ensure that such space is properly reserved in advance to ensure against meeting room scheduling conflicts.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.04</td>\r\n			<td>\r\n				The residence shall provide a JHSC bulletin board outside the lunchroom. The Committee is responsible for authorizing material to be posted on the bulletin board.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.05</td>\r\n			<td>\r\n				The residence shall respect the privacy of employees who exercise their rights under the <em>Occupational Health and Safety Act</em> and/or the <em>Workers&rsquo;</em> <em>Compensation Ac</em>t. It shall keep all relevant medical information confidential, except where required by law to disclose such information.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.06</td>\r\n			<td>\r\n				Employees are encouraged to discuss any health and safety concerns with their immediate supervisor. Should further assistance then be necessary, employees are encouraged to utilize the JHSC to help resolve health and safety concerns. Employees will not be subject to discipline or reprisals for exercising such rights.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.07</td>\r\n			<td>\r\n				Managers, supervisors and employees are expected to co-operate fully with the JHSC, and to provide requested information and assistance as soon as is practicable. The residence reserves the right to implement disciplinary action to ensure such co-operation.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.08</td>\r\n			<td>\r\n				The residence shall endeavour to keep the JHSC informed, and to consult with the Committee, whenever possible, regarding any new equipment, processes, procedures, materials or substances which will be introduced onto the premises.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.09</td>\r\n			<td>\r\n				The JHSC shall report to senior management at least once every quarter.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n', 'en'),
(35, 34, 'Smoking in the Workplace (Employees)', '<p>\r\n	In keeping with the Smoke Free Places Act, there is no smoking on the property.</p>\r\n', 'en'),
(36, 35, 'Communicable Diseases and Infection Control', '<p>\r\n	To handle communicable diseases</p>\r\n<ul>\r\n	<li>\r\n		Isolate the resident suspected of having a communicable disease</li>\r\n	<li>\r\n		Seek medical advice for the resident and obtain instructions to protect the other residents</li>\r\n	<li>\r\n		Apply universal precaution for preventative measures</li>\r\n</ul>\r\n<p>\r\n	Operators are required to have an infection control plan; such a plan would include:&nbsp;</p>\r\n<ul>\r\n	<li>\r\n		A plan for educating and training all staff on universal precautions as it relates to the prevention and control of infection</li>\r\n	<li>\r\n		A contingency plan, as well as policies and procedures that can be implemented in the event of a health hazard or suspected outbreak</li>\r\n	<li>\r\n		A process in place to facilitate early communication of an outbreak within the facility and to external agencies</li>\r\n	<li>\r\n		A hand hygiene policy and procedure that enforces strict hand washing or hand sanitization between resident care and within all sections of the residence</li>\r\n	<li>\r\n		Specific procedures for handling contaminated materials &ndash; laundry system</li>\r\n	<li>\r\n		Defined roles and responsibilities for cleaning and disinfecting the physical environment</li>\r\n</ul>\r\n<p>\r\n	Blood born infections refer to HIV, Hepatitis B and C.&nbsp;&nbsp; Refer to Policies and Procedures respecting HIV/AIDS, Hepatitis B and C infections.<br />\r\n	<br />\r\n	Notify the Regional Health Protection Branch of two or more cases of infectious diseases&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n', 'en'),
(56, 55, 'Ownership and Responsibility', '<p>\r\n	This Special Care Home is a private facility licensed by Social Development.</p>\r\n<p>\r\n	The operators will comply with all licensing requirements from Social Development and will display their Certificate of Approval to operate an Adult Residential Facility in a common area.</p>\r\n<p>\r\n	The policies and procedures included in this document are created to manage the operation of the Special Care Home.</p>\r\n<p>\r\n	The language of operation of the care home is English.</p>\r\n<p>\r\n	Employees are expected to review these policies upon hiring and at least annually (or sooner if a new policy is introduced).</p>\r\n', 'en'),
(57, 56, 'Organizational Chart', '<p>\r\n	Please refer to the organizational chart below to describe current relationships, responsibilities, lines of authority and the lines of communications within the facility.</p>\r\n<p>\r\n	A dated copy of this chart will be displayed within the home.</p>\r\n', 'en'),
(58, 57, ' Vision and Mission Statement', '<div>\r\n	Vision Statement:</div>\r\n<p>\r\n	It is the vision of this Special Care Home to provide exceptional holistic care to adults and seniors, enabling residents to lead full and abundant lives within the local community.</p>\r\n<p>\r\n	Mission Statement:</p>\r\n<p>\r\n	It is the mission of this Special Care Home to fully meet the needs of each resident while challenging them to the highest level of personal functionality in the tasks of daily living. We will strive for advancement in the physical, mental, and spiritual aspects of their lives using the best nursing care, nutrition, exercise, as well as recreational and community building activities to enhance the lives of our residents.</p>\r\n', 'en'),
(59, 58, 'Goals and Objectives', '<p>\r\n	The goals and objectives of this Special Care Home are as follows:</p>\r\n<ol>\r\n	<li>\r\n		Provide a homelike environment for all residents</li>\r\n	<li>\r\n		Provide exceptional person-centered care to the residents, respecting individual choices</li>\r\n	<li>\r\n		Honor the dignity of all residents</li>\r\n	<li>\r\n		Maintain independence</li>\r\n	<li>\r\n		Maintain community and family connections</li>\r\n	<li>\r\n		Have a culture of safety and respect in the workplace</li>\r\n	<li>\r\n		Have all staff certified as personal support workers under the voluntary registration program within one year of hire.</li>\r\n</ol>\r\n<p>\r\n	<br />\r\n	These goals and objectives are part of the basis our program review and staff performance measurement appraisals.&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n', 'en'),
(60, 59, ' Policies and Procedures', '<p>\r\n	The policies and procedures shared in this document guide the operation of the Special Care Home.</p>\r\n<p>\r\n	A copy of the policy and procedure manual is kept (ENTER NAME OF LOCATION).</p>\r\n<p>\r\n	Employees will review the manual upon hire, yearly and as new policies are released.</p>\r\n<p>\r\n	Every policy will include the date it was created and the date of last revision.</p>\r\n', 'en'),
(61, 60, ' Compliance with Laws, Regulations and Inspections', '<p>\r\n	<u><strong>Compliance with Laws:</strong></u></p>\r\n<p>\r\n	The operator will comply with Federal, Provincial and Municipal laws and regulations relevant to the facility. They include but are not limited to:</p>\r\n<ul>\r\n	<li>\r\n		Part II of the Family Services Act and Regulation 83-77</li>\r\n	<li>\r\n		Health Act</li>\r\n	<li>\r\n		Fire Prevention Act</li>\r\n	<li>\r\n		Family Income Security Act and Regulations</li>\r\n	<li>\r\n		Infirm Person&#39;s Act</li>\r\n	<li>\r\n		Mental Health Act</li>\r\n	<li>\r\n		Employment Standards Act</li>\r\n	<li>\r\n		Occupational Health and Safety Act and Regulation 2004-130</li>\r\n	<li>\r\n		The Human Rights Act</li>\r\n	<li>\r\n		Smoke Free Place Act</li>\r\n</ul>\r\n<p>\r\n	<u><strong>Compliance with Regulations:</strong></u></p>\r\n<p>\r\n	The facility will be in compliance with the Provincial Standards and Procedures for Adult Residential Facilities which provide detailed information to assist staff of Social Development, board members, operators and staff of Residential Facilities</p>\r\n<p>\r\n	Management will ensure there is a master copy of the document kept on site for reference. As new directives are released, it will be updated in the master document.</p>\r\n<p>\r\n	It should also be noted that this manual is accessible online via the following link: <span style=\"color:#0000cd;\"><u>AdultResidential</u></span>-<span style=\"color:#0000cd;\"><u>e.pdf (gnb.ca)</u></span></p>\r\n<p>\r\n	<strong><u>Compliance with Inspections:</u></strong></p>\r\n<p>\r\n	Owners, management and staff will be fully cooperative with inspections and audits from government agencies. These may include, but are not limited to:</p>\r\n<ul>\r\n	<li>\r\n		Annual inspection by Regional coordinator for licensing</li>\r\n	<li>\r\n		WSNB inspections</li>\r\n	<li>\r\n		Fire Marshall inspections</li>\r\n	<li>\r\n		Food Inspection Agencies</li>\r\n</ul>\r\n<p>\r\n	&nbsp;</p>\r\n', 'en'),
(62, 61, ' Employment Criteria for all Staff Members', '<p>\r\n	All staff (care staff, cooks, housekeepers) who work in the facility must have the following pre- requisites for employment:</p>\r\n<ul>\r\n	<li>\r\n		Valid standard Emergency First Aid and Cardio Pulmonary Resuscitation certificate. Certification must be renewed prior to expiry date. This is at the employee&rsquo;s expense.</li>\r\n	<li>\r\n		Satisfactory Social Development Record Check (repeat every 5 years or sooner if concern)</li>\r\n	<li>\r\n		Satisfactory Criminal Record Check including vulnerable sector check (repeat every 5 years or sooner if concern)</li>\r\n</ul>\r\n<p>\r\n	Note all staff must be age sixteen (16) years of age or over. Staff under nineteen (19) years of age must be supervised by an adult primary staff member at all times while providing care services directly to residents</p>\r\n<p>\r\n	Anyone providing direct care to the residents must have one of the following qualifications:</p>\r\n<ul>\r\n	<li>\r\n		Home Support Worker Program, or</li>\r\n	<li>\r\n		Special Care Home Worker Program, or</li>\r\n	<li>\r\n		Health Care Aid Program, or</li>\r\n	<li>\r\n		Human Services Program, or</li>\r\n	<li>\r\n		Nursing Assistant Program</li>\r\n</ul>\r\n<p>\r\n	<strong>For Level 3B homes you must include this section:</strong></p>\r\n<p>\r\n	The operator will employ at least one primary staff member, who is a registered nurse or a Licensed Practical Nurse (LPN).</p>\r\n<p>\r\n	Note: Casual or relief care workers must have as a minimum a grade 12 education</p>\r\n', 'en'),
(63, 62, 'Employee Files', '<p>\r\n	For each employee, the operators will have a personnel file that contains</p>\r\n<ul>\r\n	<li>\r\n		oath of confidentiality</li>\r\n	<li>\r\n		identifying information form, for example, name, address, date of birth, social insurance number</li>\r\n	<li>\r\n		documentation of qualifications that include:</li>\r\n	<li style=\"margin-left: 40px;\">\r\n		professional qualifications,</li>\r\n	<li style=\"margin-left: 40px;\">\r\n		valid standard Emergency First Aid and CPR Certificate</li>\r\n	<li style=\"margin-left: 40px;\">\r\n		verification of current registration (if applicable)</li>\r\n	<li>\r\n		professional qualifications,</li>\r\n	<li>\r\n		valid standard Emergency First Aid and CPR Certificate</li>\r\n	<li>\r\n		verification of current registration (if applicable)</li>\r\n</ul>\r\n<p>\r\n	Employees are entitled to view their personnel file. This request will come in writing and an appointment will be made at a mutually convenient time to review the file with their supervisor.</p>\r\n', 'en'),
(64, 63, ' Employee Orientation', '<p>\r\n	All employees will be given a formal orientation. This will be completed within 2 weeks of hiring.</p>\r\n<p>\r\n	Orientation will include a checklist of the following activities:</p>\r\n<ul>\r\n	<li>\r\n		Tour of facility</li>\r\n	<li>\r\n		Review of polices and procedures</li>\r\n	<li>\r\n		Fire and Emergency review</li>\r\n	<li>\r\n		Confidentiality Form</li>\r\n	<li>\r\n		Introduction to staff and residents</li>\r\n	<li>\r\n		Review of all technology and equipment</li>\r\n	<li>\r\n		Payroll orientation</li>\r\n	<li>\r\n		Review of resident documentation and facility communication</li>\r\n</ul>\r\n<p>\r\n	The completed checklist will be kept in the personnel file. Any employee under the age of 25 who has been absent from the workplace from more than 9 months, must complete another orientation period as per WSNB guidelines.</p>\r\n', 'en'),
(65, 64, 'Staff Development', '<p>\r\n	All staff are required to maintain current Standard First Aid and CPR. This must be renewed before the certification expiry date.</p>\r\n<p>\r\n	As opportunities arise, staff are encouraged to take part in additional educational activities. These could include:</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"table-development\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				.</td>\r\n			<td>\r\n				Suicide Prevention Training</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				.</td>\r\n			<td>\r\n				Behaviour Management</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<ul>\r\n					<li>\r\n						U-First</li>\r\n					<li>\r\n						Gentle Persuasive Approaches</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				.</td>\r\n			<td>\r\n				Dementia Care</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				.</td>\r\n			<td>\r\n				WSNB Occupational Health and Safety Training</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				.</td>\r\n			<td>\r\n				Training provided by the NB Alzheimer&rsquo;s Society</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', 'en'),
(66, 65, 'Performance Appraisal', '<p>\r\n	Written performance appraisals will be completed on each employee at the following intervals:</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"table-appraisal\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				.</td>\r\n			<td>\r\n				After 6 months of full-time work (probationary period)</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				.</td>\r\n			<td>\r\n				Annually, thereafter</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	The performance appraisal will be reviewed by both the employee and the operator and include both signatures.</p>\r\n<p>\r\n	Employees will be provided a copy of the performance appraisal. The original document will become part of the employee file.</p>\r\n', 'en'),
(67, 66, ' Internal Posting', '<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"table-posting\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>1</strong></td>\r\n			<td>\r\n				<strong>POLICY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				Our internal job posting policy outlines our instructions for posting and communicating vacancies internally before doing so externally. We are committed to investing in our employees and helping them advance their careers within our company whenever possible.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>2</strong></td>\r\n			<td>\r\n				<strong>SCOPE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				This policy applies to our full-time and part-time employees (both permanent and temporary.)<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<strong>Policy elements</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				Preparing an internal job posting<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				Our internal job postings should follow the same guidelines as external job postings. They should include:</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<ul>\r\n					<li>\r\n						Job title.<br />\r\n						&nbsp;</li>\r\n					<li>\r\n						Location.<br />\r\n						&nbsp;</li>\r\n					<li>\r\n						Department.<br />\r\n						&nbsp;</li>\r\n					<li>\r\n						Duties and requirements.<br />\r\n						&nbsp;</li>\r\n					<li>\r\n						Instructions on how to apply and deadline.<br />\r\n						&nbsp;</li>\r\n					<li>\r\n						Equal employment opportunity (EEO) statement.<br />\r\n						&nbsp;</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				As an exception, internal job postings don&rsquo;t need to include our company&rsquo;s description and mission statement.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<strong>Posting a job internally</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				Internal job postings should appear in at least one of the following places:</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<ul>\r\n					<li>\r\n						Emails to staff.<br />\r\n						&nbsp;</li>\r\n					<li>\r\n						Bulletin boards.<br />\r\n						&nbsp;</li>\r\n					<li>\r\n						Company intranet.<br />\r\n						&nbsp;</li>\r\n					<li>\r\n						Internal newsletter.<br />\r\n						&nbsp;</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				HR and hiring managers are responsible for communicating internal job postings to everyone who may be eligible to apply.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				Each job may be posted internally for [TIMEFRAME] before it&rsquo;s posted externally. If a position has to be filled quickly, HR may post a job both internally and externally and assess candidates from both sources simultaneously. Internal candidates may be given preference over external candidates when possible.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				Although we typically post jobs, we may occasionally fill positions through other means (e.g. directly promoting an employee or searching through our existing candidate database.) Generally, we don&rsquo;t have to post a job either externally or internally unless necessary.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				In any case, our company is an equal opportunity employer. We will not allow favoritism or discrimination on the basis of protected characteristics.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<strong>Employees who will be considered for a position</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				We&rsquo;ll consider employees who apply to our job postings if they:</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<ul>\r\n					<li>\r\n						Meet each position&rsquo;s minimum criteria.<br />\r\n						&nbsp;</li>\r\n					<li>\r\n						Perform their current jobs satisfactorily.<br />\r\n						&nbsp;</li>\r\n					<li>\r\n						Have held their current position for at least [X months/ years]<br />\r\n						&nbsp;</li>\r\n					<li>\r\n						Were not subjected to disciplinary action in the past [X years]<br />\r\n						&nbsp;</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<strong>Employees who are short-listed will be invited to an interview.</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				They won&rsquo;t need to go through other hiring stages (e.g. pre-employment screening or background check.)<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				Applicants from different location</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				Internal applicants may come from different company locations. In cases when an internal applicant lives in a different city or country, HR will consider relocating the employee on a case-by-case basis. If relocation isn&rsquo;t possible (e.g. relocation expenses aren&rsquo;t included in the budget), HR is responsible for mentioning this in the job posting. HR is also responsible for communicating that posting to eligible employees only.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>3</strong></td>\r\n			<td>\r\n				<strong>PROCEDURE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				We&rsquo;ll follow this procedure to post jobs internally:</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<ul>\r\n					<li>\r\n						HR and hiring managers discuss whether they should post a job internally.<br />\r\n						&nbsp;</li>\r\n					<li>\r\n						HR drafts the job description with input from hiring managers.<br />\r\n						&nbsp;</li>\r\n					<li>\r\n						[VP of HR, Head of HR etc.] approves the job description.<br />\r\n						&nbsp;</li>\r\n					<li>\r\n						HR posts the job and communicates the listing immediately to employees.<br />\r\n						&nbsp;</li>\r\n					<li>\r\n						HR/ hiring managers receive and evaluate internal applications, checking employee performance records if needed.<br />\r\n						&nbsp;</li>\r\n					<li>\r\n						HR will inform all internal candidates about the status of their application.<br />\r\n						&nbsp;</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				If an employee declines an offer, their decision will not affect their current job. If they accept an offer, they should meet with their managers to discuss a transfer. An employee&rsquo;s transfer may be delayed until their current position is filled.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				To apply properly, employees should follow instructions mentioned in the job posting. Employees are not obligated to inform their managers about their job application at this stage. Employees or HR should notify managers if their team members are short-listed and invited for an interview.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				HR is responsible for keeping accurate records of applications and interviews.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				Resource:<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<span style=\"color:#0000cd;\"><u>https://resources.workable.com/internal-job-posting-policy</u></span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p style=\"text-align: justify; margin-left: 40px;\">\r\n	&nbsp;</p>\r\n<p dir=\"rtl\" style=\"text-align: left; margin-right: 40px;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify; margin-left: 40px;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify; margin-left: 40px;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify; margin-left: 40px;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: justify; margin-left: 40px;\">\r\n	&nbsp;</p>\r\n', 'en'),
(68, 67, ' External Posting', '<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"external\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>1.</strong></td>\r\n			<td>\r\n				<strong>POLICY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.1</td>\r\n			<td>\r\n				This residence is committed to recruiting and selecting individuals who are the most qualified to perform the requirements of each position available. Candidates for job vacancies may be from existing staff or from outside sources. Where qualifications are deemed equivalent, preference will normally be given to internal candidates.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.2</td>\r\n			<td>\r\n				All external recruitment requires the prior approval of the general manager, or a designated nominee.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.3</td>\r\n			<td>\r\n				The successful applicant(s) for a job vacancy shall be given a written offer of employment which outlines all the terms and conditions of employment in that job. The applicant(s) must accept the offer by signing it before commencing work. There shall be no verbal or informal offers provided to the applicant. Instead, the applicant should be told that the residence will be making a formal, written offer, which the applicant should carefully review and consider.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.4</td>\r\n			<td>\r\n				No offers or commitments, financial or otherwise, are to be made to any job candidate until all appropriate approvals have been obtained.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>2</strong></td>\r\n			<td>\r\n				<strong>PURPOSE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				2.1</td>\r\n			<td>\r\n				The purpose of this Statement of Policy and Procedure is to ensure a consistent approach to the recruitment and selection of staff and to ensure compliance with all legislative requirements.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>3</strong></td>\r\n			<td>\r\n				<strong>SCOPE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				3.1</td>\r\n			<td>\r\n				This Statement of Policy and Procedure applies to all managers who have responsibility for recruiting and selecting personnel to fill a new or existing job vacancy.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>4</strong></td>\r\n			<td>\r\n				<strong>RESPONSIBILITY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.1</td>\r\n			<td>\r\n				Managers are responsible for:</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<ul>\r\n					<li>\r\n						Initiating the recruitment process, as necessary</li>\r\n					<li>\r\n						If required, developing and placing media job advertisements which comply with provisions of the Human Rights Act.</li>\r\n					<li>\r\n						Developing and/or understanding the job selection criteria for job vacancy.</li>\r\n					<li>\r\n						Screening candidates in accordance with the job selection criteria.</li>\r\n					<li>\r\n						Selecting final candidates to fill job vacancies.</li>\r\n					<li>\r\n						Conducting candidate reference checks, as necessary.</li>\r\n					<li>\r\n						Providing each successful applicant with a written offer of employment.</li>\r\n					<li>\r\n						Ensuring the offer of employment is accepted before the candidate starts work.</li>\r\n					<li>\r\n						Ensuring the successful applicant receives a proper and thorough orientation to the new position and, where necessary, to the company.</li>\r\n				</ul>\r\n				<p>\r\n					<br />\r\n					&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>5</strong></td>\r\n			<td>\r\n				<strong>DEFINITIONS</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				None<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>6</strong></td>\r\n			<td>\r\n				<strong>REFERENCES AND RELATED STATEMENTS OF POLICY AND PROCEDURE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<ul>\r\n					<li>\r\n						Human Right Act (New Brunswick)</li>\r\n					<li>\r\n						SPP HR 2.01.NB &ndash; Employment Principles</li>\r\n					<li>\r\n						SPP HR 2.03.NB &ndash; Terms and Conditions of Employment</li>\r\n					<li>\r\n						SPP HR 2.04.NB &ndash; Probationary Period</li>\r\n					<li>\r\n						SPP HR 2.07.NB &ndash; Employee Orientation</li>\r\n					<li>\r\n						SPP HR 2.08.NB &ndash; Confidentiality and Inventions</li>\r\n					<li>\r\n						SPP HR 3.02.NB &ndash; Job Evaluation</li>\r\n					<li>\r\n						SPP HR 5.04.NB &ndash; Workplace Accommodation on the Basis of Disability<br />\r\n						<br />\r\n						&nbsp;</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>7</strong></td>\r\n			<td>\r\n				<strong>PROCEDURE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.1</td>\r\n			<td>\r\n				Each job vacancy for which recruitment is deemed necessary, either for a replacement candidate or a new position, requires the approval of the general manager. Supporting documentation shall include:</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<ul>\r\n					<li>\r\n						The reason for vacancy</li>\r\n					<li>\r\n						The number of candidates to be hired</li>\r\n					<li>\r\n						A job description</li>\r\n					<li>\r\n						Job selection and candidate criteria</li>\r\n					<li>\r\n						Anticipated salary and salary range</li>\r\n					<li>\r\n						A recruiting plan and budget<br />\r\n						&nbsp;</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>7.2</strong></td>\r\n			<td>\r\n				<strong>External Candidates</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"external-cont2\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				External candidates may be recruited from many locations using varied means including print, video, and personal contacts. In some cases, it may be deemed necessary to use recruitment agencies or executive search organizations to assist in the search for qualified candidates.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				If recruitment agencies or executive search organizations are expected to be used to identify external candidates, the approval of the general manager is required before any such agency is engaged to assist in a recruitment assignment. Only one organization may be used at any time on any recruiting assignment. Representatives of outside organizations who contact managers directly, regarding job vacancies, should be referred immediately to the general manager. No information regarding vacancies, staff levels, names of employees, or organization plans, are to be divulged to outside organizations unless done so by the general manager or a designated nominee.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(c)</td>\r\n			<td>\r\n				All external candidates for employment are required to complete the residence&rsquo;s application form, or, in lieu of this, to provide a r&eacute;sum&eacute; that contains at least the same information as that required in the application form.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(d)</td>\r\n			<td>\r\n				Each final external candidate shall be interviewed by at least two managers prior to any offer(s) of employment being extended. The manager is responsible for screening candidates on the basis of the job selection criteria.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(e)</td>\r\n			<td>\r\n				Reference checks shall be completed for all potential new hires from external sources. As a minimum, the last [2] employers should be contacted to verify the candidate&rsquo;s employment history and quality or work. Academic achievements should be verified also when they are essential to meeting minimum job qualifications.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(f)</td>\r\n			<td>\r\n				Special approval is required before any person is recruited directly from secure employment with another employer.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"external-selection\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>7.3</strong></td>\r\n			<td>\r\n				<strong>Selection</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"external-cont3\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				Performance, skill and ability to perform the job, are the primary considerations for the selection of internal candidates. Previous directly related experience, skill and ability are the primary consideration for selecting external candidates.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				Final candidates (internal or external) for a job vacancy may be required to complete employment tests deemed necessary to determine their skill, knowledge, or ability to perform the job.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"external-cont4\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				7.4</td>\r\n			<td>\r\n				Pre-employment medical examinations, if deemed necessary, may be arranged only after an offer of employment has been extended.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.5</td>\r\n			<td>\r\n				All offers of employment shall be approved by the general manager or a designated nominee prior to being extended to a successful candidate. A written offer of employment shall include the following components:</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<ul>\r\n					<li>\r\n						Title of the position being offered;</li>\r\n					<li>\r\n						Starting salary stated;</li>\r\n					<li>\r\n						Name of the department in which position is located;</li>\r\n					<li>\r\n						Name and title of manager to which the individual will report;</li>\r\n					<li>\r\n						Duration of the probationary period, if any;</li>\r\n					<li>\r\n						Date of first salary review;</li>\r\n					<li>\r\n						Effective date of hiring and reporting time;</li>\r\n					<li>\r\n						Summary of benefit plans including date(s) of eligibility for each plan;</li>\r\n					<li>\r\n						Special arrangements such as relocation (requires management&rsquo;s approval);</li>\r\n					<li>\r\n						Confidentiality and inventions agreement (if applicable);</li>\r\n					<li>\r\n						Conditions of employment that must be observed;</li>\r\n					<li>\r\n						A statement that the offer of employment is conditional on the candidate&rsquo;s ability to satisfy the residence&rsquo;s medical requirements (if applicable);</li>\r\n					<li>\r\n						Termination clause</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.6</td>\r\n			<td>\r\n				Any candidates who deliberately misrepresents or falsifies information during the recruitment or selection process shall be eliminated from the selection process and, if employed, shall be discharged for cause</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', 'en'),
(69, 68, ' Probationary Period', '<p>\r\n	<strong>Policy brief &amp; purpose</strong></p>\r\n<p>\r\n	Our probationary period policy will define the employment probationary period and explain its meaning for the employee and the company. This practice is designed to allow an introductory period for everyone involved to decide if the hiring of the particular employee was a correct decision and whether the employee should continue working for the company.</p>\r\n<p>\r\n	<strong>Scope</strong><br />\r\n	This policy applies to all prospective and existing employees of our company.</p>\r\n<p>\r\n	<strong>Policy elements</strong><br />\r\n	Those who can be placed in an employee probationary period include the following:</p>\r\n<ul>\r\n	<li>\r\n		New employees who have just passed through a hiring process for a permanent position</li>\r\n</ul>\r\n<ul>\r\n	<li>\r\n		Current employees who are promoted to a position of higher responsibility</li>\r\n</ul>\r\n<ul>\r\n	<li>\r\n		Current employees who have to be disciplined for various reasons</li>\r\n</ul>\r\n<p>\r\n	The length of the probationary period will be six months. The probationary period may be extended for reasons that justify this action. The employee will receive formal notification in writing with the reason for the extension and clearly defined objectives.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<strong><em>New Employees</em></strong></p>\r\n<p>\r\n	The company will make an effort to ensure that its hiring procedures serve the purpose of recruiting the best employees for each open position. However, we acknowledge that they are inevitably not always accurate in predicting performance or determining appropriate cultural fit. The probation period for new employees gives both employee and employer enough time to find out if their employment relation will eventually work out to the benefit of both.</p>\r\n<p>\r\n	<strong>What is the meaning of probation period?</strong></p>\r\n<p>\r\n	The probationary period definition for new employees is the time between signing an employment contract and being granted permanent employment status. It is a &ldquo;trial period&rdquo; during which the employee is being evaluated as a suitable fit to the position and the company. The new employee will be given consistent feedback and coaching to have the chance to learn their new job and improve during the probationary period. At the end of the probation period (or possibly before that), the supervisor will determine if the employee should be retained in the organization. This decision will be made by appraising the following criteria:</p>\r\n<ul>\r\n	<li>\r\n		The skills, competencies and knowledge of the employee on the job</li>\r\n</ul>\r\n<ul>\r\n	<li>\r\n		The employee&rsquo;s progress on given assignments</li>\r\n</ul>\r\n<ul>\r\n	<li>\r\n		Their reliability, trustworthiness and other relevant personality characteristics of the employee</li>\r\n</ul>\r\n<ul>\r\n	<li>\r\n		The employee&rsquo;s relations and collaboration with subordinates, supervisors and peers</li>\r\n</ul>\r\n<p>\r\n	The abovementioned criteria will be assessed with quantitative measures if applicable. Their progress will be documented whenever possible, and the decision to retain them will be at the supervisor&rsquo;s discretion.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<strong><em>Permanent employees</em></strong></p>\r\n<p>\r\n	When an employee has completed their initial probationary period, they are granted permanent employment status. After that they may be placed on probation as part of a disciplinary procedure. The reason for the disciplinary action, ranging from low performance to policy violations, will determine the length of the probationary period. During this period, the employee is obliged to change their behavior or they will be faced with more severe repercussions leading up to termination. The company will help them as much as possible with guidance, feedback and training and will expect them to comply.</p>\r\n<p>\r\n	An employee may also be placed in a probationary period if they have been promoted or transferred to a position. The purpose of this is to ensure that the employee is able to complete their new duties. If they are deemed unsuitable, they may be allowed to be demoted or return to their previous position or other solutions may be found. Employment is not guaranteed and the decision remains at the supervisor&rsquo;s discretion.</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	<em><strong>Termination: Dismissal during probationary period</strong></em></p>\r\n<p>\r\n	If the employee is deemed unsuitable while on a probationary period, they may be terminated without the minimum prior notice mandated by law. The <span style=\"color:#008000;\"><u>termination of employment</u></span> during this period may be for cause or without cause depending on the circumstances and the individual&rsquo;s evaluatio<em><strong>n.</strong></em></p>\r\n<p>\r\n	Termination may occur before the ending of the probationary period. This may happen if the tactical evaluations of the employee are highly unsatisfying or if the employee engages in behavior that justifies a for-cause dismissal. The employee will be officially notified in writing for the decision to terminate them. The document will explicitly state the reason behind their termination and the expected date it will take effect</p>\r\n<p>\r\n	Employees may still have to be dismissed for various reasons, after the end of the probationary period. In such cases, the company will follow probation period employment law, legal guidelines and its own<u> <span style=\"color:#006400;\">separation of employment policy.</span></u></p>\r\n<p>\r\n	Resource:<br />\r\n	<span style=\"color:#0000cd;\"><u>https://resources.workable.com/probationary-period-company-policy</u></span></p>\r\n<p>\r\n	&nbsp;</p>\r\n', 'en');
INSERT INTO `manual_policies_desc` (`desc_id`, `manual_policies_id`, `title`, `content`, `language`) VALUES
(70, 69, ' Hours of Work', '<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"table-hours\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>1</strong></td>\r\n			<td>\r\n				<strong>POLICY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.01</td>\r\n			<td>\r\n				<strong>Working Hours</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"work\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				<strong>Regular Full-time Working Hours</strong> are <strong>[8]</strong> hours per shift,&nbsp; for a total of <strong>[40]</strong> hours per week. The normal work day commences varies based on shift worked. Shift start/end times are as follows:&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"policy-table\">\r\n	<tbody>\r\n	</tbody>\r\n</table>\r\n<ul style=\"margin-left: 80px;\">\r\n	<li style=\"margin-left: 40px;\">\r\n		Day Shift 7am to 3pm</li>\r\n	<li style=\"margin-left: 40px;\">\r\n		Evening Shift 3pm to 11pm</li>\r\n	<li style=\"margin-left: 40px;\">\r\n		Overnight Shift 11pm to 7am&nbsp;</li>\r\n</ul>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"work-cont\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				&nbsp;Overtime must be approved in accordance with the Overtime Policy, HR3.06.NB. Subject to paragraphs 1.06 and 7.04, an employee has the right to refuse to work more than [44] hours a week.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"work-cont2\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				1.02</td>\r\n			<td>\r\n				<strong>Eating Period</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				Employees scheduled to work a full regular shift shall observe a 30 minute unpaid eating period. Eating Periods shall commence at a time that is no longer than five hours after the shift starting time. The exact time of the half-hour eating period shall be established by the employee&rsquo;s immediate supervisor in light of operational requirements. Employees are not allowed to forego their eating period and are not allowed to leave early if they forego their eating period.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.03</td>\r\n			<td>\r\n				<strong>Shift Rest Period </strong>[Optional]</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				Employees scheduled to work a full regular shift shall observe a 30 minute unpaid eating period. Eating Periods shall commence at a time that is no longer than five hours after the shift starting time. The exact time of the half-hour eating period shall be established by the employee&rsquo;s immediate supervisor in light of operational requirements. Employees are not allowed to forego their eating period and are not allowed to leave early if they forego their eating period.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.04</td>\r\n			<td>\r\n				<strong>Emergency Situations</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				In situations where there is work of an emergency nature, employees may be required to work outside of the normal established hours.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>2</strong></td>\r\n			<td>\r\n				<strong>PURPOSE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				2.01</td>\r\n			<td>\r\n				The purpose of this Statement of Policy and Procedure is to establish standard weekly hours of work and to ensure consistent administration of working hours.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>3</strong></td>\r\n			<td>\r\n				<strong>SCOPE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				3.01</td>\r\n			<td>\r\n				This Statement of Policy and Procedure applies to all Full-time employees.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>4</strong></td>\r\n			<td>\r\n				<strong>RESPONSIBILITY</strong><br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.01</td>\r\n			<td>\r\n				Employees are responsible for being punctual in their work habits. Tardiness is not acceptable and repeated tardiness may lead to termination for cause, without notice or pay in lieu thereof.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.02</td>\r\n			<td>\r\n				The General Manger is responsible for securing necessary employee and government approvals, if required, for variations in working hours beyond those outlined herein.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>5</strong></td>\r\n			<td>\r\n				<strong>DEFINITIONS</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.01</td>\r\n			<td>\r\n				&ldquo;Workweek&rdquo; means a period of seven continuous days used by the residence for the purpose of scheduling work.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>6</strong></td>\r\n			<td>\r\n				<strong>REFERENCES and RELATED STATEMENTS of POLICY and PROCEDURE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<ul>\r\n					<li>\r\n						Employment Standards Act and Regulations (New Brunswick)</li>\r\n					<li>\r\n						SPP HR 2.05.NB &mdash; Employment Classifications</li>\r\n					<li>\r\n						SPP HR 3.06.NB &mdash; Overtime</li>\r\n					<li>\r\n						SPP HR 3.07.NB &mdash; Reporting Pay</li>\r\n					<li>\r\n						SPP HR 3.08.NB &mdash; Call-in Pay<br />\r\n						<br />\r\n						&nbsp;</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>7</strong></td>\r\n			<td>\r\n				<strong>PROCEDURE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.01</td>\r\n			<td>\r\n				Variations in the shift starting and quitting times of some positions may be approved by the General Manager if the purpose is to provide more effective access to the incumbents of those positions by clients, families and/or other employees.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.02</td>\r\n			<td>\r\n				For purposes of Payroll administration, the week begins on [Sunday at 12:01am] and ends at midnight the following [Saturday].<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.03</td>\r\n			<td>\r\n				An employee may be required to work more than maximum number of hours specified in paragraph 1.01&nbsp;<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"work-cont3\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				to deal with an emergency that necessitates immediate remedial action or that constitutes an imminent hazard to life or property, and</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				only so far as is necessary to avoid serious interference with the ordinary working of the residence&rsquo;s establishment or operations.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', 'en'),
(71, 70, 'Overtime', '<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"table-overtime\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>1</strong></td>\r\n			<td>\r\n				<strong>POLICY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.01</td>\r\n			<td>\r\n				Overtime work, in excess of [44] hours per week must be authorized in advance by the General Manager before being worked.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.02</td>\r\n			<td>\r\n				Authorized overtime hours worked are paid at the rate of [one and one-half (1&frac12;) times the employee&#39;s base hourly rate, or its equivalent (as of April 1, 2020, $17.55 per hour based on the minimum wage rate; as of April 1, 2021, $17.63 per hour based on the minimum wage rate)] for all hours worked in excess of [44] hours per week.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.03</td>\r\n			<td>\r\n				<span avenir=\"\" data-doc-id=\"1067983000000028001\" data-doc-type=\"writer\" font-size:=\"\" lt=\"\" next=\"\" style=\"font-family: \">In general, only work done on the</span><span avenir=\"\" font-size:=\"\" lt=\"\" next=\"\" style=\"font-family: \"> residence&rsquo;s </span><span avenir=\"\" font-size:=\"\" lt=\"\" next=\"\" style=\"font-family: \">premises will qualify for pay or overtime pay. Exceptions to this requirement must be authorized by </span><span avenir=\"\" font-size:=\"\" lt=\"\" next=\"\" style=\"font-family: \">General Manager.</span><br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>2</strong></td>\r\n			<td>\r\n				<strong>PURPOSE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				2.01</td>\r\n			<td>\r\n				The purpose of this Statement of Policy and Procedure is to ensure overtime hours and premiums are administered consistently, fairly and equitably; recorded accurately, and paid correctly.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>3</strong></td>\r\n			<td>\r\n				<strong>SCOPE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				3.01</td>\r\n			<td>\r\n				This Statement of Policy and Procedure applies to all Non-union employees in all Occupational Categories unless a category of employees is exempt from the overtime provisions contained in the <em>Employment Standards</em> Act and Regulations. [Where appropriate: Overtime provisions for Union employees are governed by the terms of the applicable Collective Agreement.]<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>4</strong></td>\r\n			<td>\r\n				<strong>RESPONSIBILITY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.01</td>\r\n			<td>\r\n				Managers are responsible for obtaining advance authorization for employees to work overtime and for submitting the Overtime Reporting Form in a timely manner. Employees are required to obtain advanced authorization before working overtime hours. Managers are responsible for monitoring and controlling overtime costs.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>5</strong></td>\r\n			<td>\r\n				<strong>DEFINITIONS</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.01</td>\r\n			<td>\r\n				&ldquo;<strong>Base hourly rate</strong>&rdquo; for an employee who is paid by the hour is the amount paid for an hour of work in the employee&rsquo;s usual work week, not counting overtime hours and exclusive of premiums, benefits, etc. For employees who are paid other than an hourly rate of pay the base hourly rate is computed by dividing the amount the employee earned in a given work week divided by the number of non-overtime hours actually worked in that week.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>6</strong></td>\r\n			<td>\r\n				<strong>REFERENCES and RELATED STATEMENTS of POLICY and PROCEDURE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<ul>\r\n					<li>\r\n						<em>Employment Standards Act</em> and General Regulations (New Brunswick)</li>\r\n					<li>\r\n						Minimum Wage Regulation</li>\r\n					<li>\r\n						Minimum Wage for Categories of Employees in Crown Construction Work Regulation</li>\r\n					<li>\r\n						Minimum Wage for Counsellor and Program Staff at Residential Summer Camps Regulation</li>\r\n					<li>\r\n						SPP HR 2.05.NB &mdash; Employment Classifications</li>\r\n					<li>\r\n						SPP HR 2.06.NB &mdash; Hours of Work</li>\r\n					<li>\r\n						SPP HR 4.03.NB &mdash; Holidays</li>\r\n					<li>\r\n						SPP HR 3.05.NB &mdash; Pay Range Structure<br />\r\n						&nbsp;</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>7</strong></td>\r\n			<td>\r\n				<strong>PROCEDURE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.01</td>\r\n			<td>\r\n				To be eligible for overtime payment, all overtime must be documented and authorized in advance by the General Manager or a designated nominee.&nbsp;<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.02</td>\r\n			<td>\r\n				Commuting between home and workplace is not considered working, travel or waiting time.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.03</td>\r\n			<td>\r\n				Overtime will not be paid for work performed off company premises, unless specific written approval has been obtained in advance from the General Manager.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.04</td>\r\n			<td>\r\n				Overtime will be reported weekly to Payroll, by each manager on the Overtime. Approved overtime pay is normally paid in the first pay period following the period in which the overtime is worked.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.05</td>\r\n			<td>\r\n				Employees who work overtime on or outside of company premises without prior approval will be subject to disciplinary measures.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', 'en'),
(72, 71, 'Vacation and Vacation Pay', '<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"table-vacation\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>1</strong></td>\r\n			<td>\r\n				<strong>POLICY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.01</td>\r\n			<td>\r\n				Vacations are granted annually, based on each completed vacation pay year of employment with the residence. Both active employment and non-active employment are included for this purpose.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.02</td>\r\n			<td>\r\n				<strong>Vacation Entitlement</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"vacation-cont\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				Vacation time is not earned until the completion of each vacation pay year.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				Employees on the payroll who have not completed one year of continuous employment at the end of a vacation pay year are entitled to a pro rata vacation period reflecting the period they have worked for the residence. The pro rata vacation shall be calculated in accordance with the provisions of the <em>Employment Standards</em> Act.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(c)</td>\r\n			<td>\r\n				Employees on the payroll are entitled to a vacation entitlement of:</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<ul>\r\n					<li>\r\n						two weeks or one day for each month worked during the vacation pay year, whichever is less, if the employee has less than eight years of service;</li>\r\n					<li>\r\n						three weeks or one and one-quarter (1&frac14;) days for each month worked during the vacation pay year, whichever is less, if the employee has eight years or more of service;</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(d)</td>\r\n			<td>\r\n				Earned vacation entitlement cannot be carried over from one vacation entitlement year to another, except with the written approval of the General Manager.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"vacation-pay\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				1.03</td>\r\n			<td>\r\n				<strong>vacation pay</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"vacation-cont1\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				Employees with vacation entitlement of two weeks or less will receive vacation pay of four percent of wages for the period in which vacation entitlement was earned.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				Employees with vacation entitlement of three weeks but less than four weeks will receive vacation pay of six percent of wages for the period in which vacation entitlement was earned.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"Vacation-Scheduling\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				1.04</td>\r\n			<td>\r\n				<strong>Vacation Scheduling</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"vacation-cont2\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				Earned vacation entitlement shall be taken within the four month period following the completion of a vacation pay year. Vacation scheduling must be approved in advance by the employee&rsquo;s immediate manager. Employees should not make any vacation commitments until vacation scheduling has been approved in writing.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				&nbsp;Except where operational and/or customer service interfere, employees&rsquo; requests for vacation scheduling will receive preference.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(c)</td>\r\n			<td>\r\n				If an employee does not schedule an earned vacation entitlement within the four month period following the completion of a vacation pay year, the residence shall schedule the vacation and give the employee at least one week&rsquo;s written notice of such vacation.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"table-period\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"vacation-cont3\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				1.05</td>\r\n			<td>\r\n				The residence reserves the right, in the future, to schedule vacations or to institute a company-wide vacation shutdown period.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>2</strong></td>\r\n			<td>\r\n				<strong>PURPOSE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				2.01</td>\r\n			<td>\r\n				The purpose of this Statement of Policy and Procedure is to specify vacation entitlements that are earned by employees and to ensure consistency in the calculation and payment of vacation pay.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>3</strong></td>\r\n			<td>\r\n				<strong>SCOPE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				3.01</td>\r\n			<td>\r\n				This Statement of Policy and Procedure applies to all Non-union employees. [Where appropriate: Vacation provisions for Union employees are governed by the terms of the applicable Collective Agreement.]<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>4</strong></td>\r\n			<td>\r\n				<strong>RESPONSIBILITY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.01</td>\r\n			<td>\r\n				Managers are responsible for receiving vacation requests, reviewing, scheduling, approving and monitoring vacation time requested and taken, and for advising Payroll of any changes to approved vacation plans.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.02</td>\r\n			<td>\r\n				Payroll is responsible for monitoring vacation entitlements, vacation schedules and shall ensure vacation pay is paid in a timely manner, consistent with the provisions of this Statement of Policy and Procedure.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.03</td>\r\n			<td>\r\n				Payroll is responsible for replying to an employee&rsquo;s written request for a statement of vacation time and vacation pay by the later of:</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<p style=\"margin-left: 40px;\">\r\n					a. seven days after the start of the next vacation entitlement year, or<br />\r\n					b. the first pay day of the next vacation entitlement year.</p>\r\n				<p style=\"margin-left: 80px;\">\r\n					&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>5</strong></td>\r\n			<td>\r\n				<strong>DEFINITIONS</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.01</td>\r\n			<td>\r\n				<strong>&ldquo;Non-active employment&rdquo;</strong> includes periods during which an employee is on an approved leave of absence for illness or injury, a personal leave or other statutory leaves of absence during which the residence deems the employee&rsquo;s employment relationship is not terminated.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.02</td>\r\n			<td>\r\n				<strong>&ldquo;Vacation pay year&rdquo;</strong> means the period from July 1 of the previous year to June 30 of the current year.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.03</td>\r\n			<td>\r\n				&ldquo;<strong>Continuous employment</strong>&rdquo; means a period of 12 consecutive months of employment including approved vacation time and/or approved leaves of absence(s).<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.04</td>\r\n			<td>\r\n				<strong>&ldquo;Wages&rdquo;</strong>, for the purposes of this policy, include salary, commissions, compensation in any form for work or services measured by time, piece or otherwise, but does not include public holiday pay, pay in lieu of public holiday pay, vacation pay or pay in lieu of vacation pay, gratuities or honoraria.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>6</strong></td>\r\n			<td>\r\n				<strong>REFERENCES and RELATED STATEMENTS of POLICY and PROCEDURE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<ul>\r\n					<li>\r\n						<em>Employment Standards Act and Regulations (New Brunswick)</em></li>\r\n					<li>\r\n						SPP HR 4.05.NB &mdash; Pregnancy Leave</li>\r\n					<li>\r\n						SPP HR 4.06.NB &mdash; Parental/Adoption Leave</li>\r\n					<li>\r\n						SPP HR 4.12.NB &mdash; Emergency/Family Responsibility Leave [if applicable]<br />\r\n						&nbsp;</li>\r\n				</ul>\r\n				<p>\r\n					&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>7</strong></td>\r\n			<td>\r\n				<strong>PROCEDURE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.01</td>\r\n			<td>\r\n				<strong>Vacation Scheduling</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				</td>\r\n			<td>\r\n				<p style=\"margin-left: 40px;\">\r\n					a. In the event no vacation shutdown period is declared by the residence, employees are required to submit requests for vacation to their immediate manager for approval.<br />\r\n					b. Vacation entitlement of two weeks or less shall be taken in periods of not less than complete weeks unless the employee requests in writing that vacation time be taken in periods shorter than one week and the residence agrees to that request.<br />\r\n					c .In the event the operation of a work unit may be impaired by scheduled vacations, the manager may limit the number of employees who can take vacations at the same time. In such cases, the manager, in his or her sole discretion, will approve vacations based on the operational needs of the residence. Where all other factors are equal, the manager will give preference to vacation requests in order of the employee&rsquo;s length of service with&nbsp; the residence.<br />\r\n					d. Employees shall be advised in writing by their managers when their vacations are scheduled to begin and end. The written notice shall be provided at least one week in advance of an employee&rsquo;s scheduled vacation.<br />\r\n					&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.02</td>\r\n			<td>\r\n				<strong>Vacation Pay on Termination</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				An employee who ceases employment, for any reason, will receive vacation pay for vacation time accrued, but not yet taken, to the date of termination.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.03</td>\r\n			<td>\r\n				<strong>Payment in Lieu of Vacation</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				No payment will be made in lieu of vacation except where:</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<p style=\"margin-left: 40px;\">\r\n					a. an employee ceases employment;<br />\r\n					b. an employee who is entitled to an earned vacation period is on extended leave of absence and/or is physically unable to take earned vacation in the current year and the Director, Employment Standards approves such payment; or<br />\r\n					c. in those rare and exceptional cases where it is clear an employee cannot schedule the earned vacation entitlement within the authorized period. In such cases, payment in lieu of vacation may be made only for vacation entitlement in excess of statutory minimums. Approval for pay in lieu of vacation requires the approval of the immediate manager and the General Manager, or a designated nominee.<br />\r\n					&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.04</td>\r\n			<td>\r\n				<strong>Vacation time and vacation pay records</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"vacation-pay1\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				The residence records the following information related to an employee&rsquo;s entitlement to vacation time and vacation pay:</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<p>\r\n					(i) The amount of vacation time, if any, that the employee had earned since the start of employment but had not taken before the start of the vacation entitlement year.<br />\r\n					(ii) The amount of vacation time the employee earned during the vacation entitlement year.<br />\r\n					(iii) The amount of vacation time, if any, taken by the employee during the vacation entitlement year.<br />\r\n					(iv) The amount of vacation time, if any, earned by the employee since the start of employment but not taken at the end of the vacation entitlement year.<br />\r\n					(v) The amount of vacation pay paid to the employee during the vacation entitlement year.<br />\r\n					(vi) The amount of wages on which the vacation pay referred to in paragraph 5 was calculated and the period of time to which those wages relate.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p style=\"margin-left: 120px;\">\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"vacation-cont4\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				The information specified in paragraph 7.08(a) shall be prepared by the Payroll Department by the later of:</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(i)&nbsp; seven days after the start of the next vacation entitlement year, or<br />\r\n				(ii)&nbsp; the first pay day of the next vacation entitlement year.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(c)</td>\r\n			<td>\r\n				An employee may make a written request to receive a copy of the record specified under paragraph 7.08(a) and shall be provided with that information within the time frames specified in paragraph 4.03 herein.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(d)</td>\r\n			<td>\r\n				Vacation time and vacation pay records shall be kept for a minimum of three years after they are made.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p style=\"margin-left: 80px;\">\r\n	&nbsp;</p>\r\n', 'en'),
(73, 72, ' Bereavement Leave', '<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"leave-table\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>1</strong></td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<strong>POLICY</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"leave-content\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				1.01</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				In the event of a death of a person with a close family relationship with an employee, an employee is entitled to and shall be granted a bereavement leave to a maximum of 5 working days for the purpose of arranging and/or attending the funeral.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				1 day of the bereavement leave shall be with pay and the remainder of the leave shall be without pay.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"leave-subcontent\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				1.02</td>\r\n			<td>\r\n				In the event of a death of extended family member, employees may request a bereavement leave, and a leave of 1 working day with pay will be granted to attend the funeral. If attendance at the funeral requires extensive travel, unpaid leave of up to 2 additional days will be granted.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>2</strong></td>\r\n			<td>\r\n				<strong>PURPOSE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				2.01</td>\r\n			<td>\r\n				The purpose of this Statement of Policy and Procedure is to establish an employee&rsquo;s entitlement to a bereavement leave in the event of a death of a member in a close family relationship with the employee or a death in the employee&rsquo;s extended family.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>3</strong></td>\r\n			<td>\r\n				<strong>SCOPE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				3.01</td>\r\n			<td>\r\n				This Statement of Policy and Procedure applies to all employees.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>4</strong></td>\r\n			<td>\r\n				<strong>RESPONSIBILITY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.01</td>\r\n			<td>\r\n				Employees are responsible for notifying their immediate managers of their need for a bereavement leave as soon as possible.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.02</td>\r\n			<td>\r\n				Managers are responsible for advising Payroll of the name of any employee who takes a bereavement leave and for ensuring the payroll record properly reflects the employee&rsquo;s pay entitlement.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>5</strong></td>\r\n			<td>\r\n				<strong>DEFINITIONS</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.01</td>\r\n			<td>\r\n				&ldquo;<strong>Close family relationship</strong>&rdquo; means the relationship between persons who are married to one another, between parents and their children, between siblings and between grandparents and their grandchildren, and includes a relationship between persons who, though not married to one another and whether or not a blood relationship exists, demonstrate an intention to extend to one another the mutual affection and support normally associated with those relationships first mentioned<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.02</td>\r\n			<td>\r\n				&ldquo;<strong>Extended family</strong>&rdquo; means a parent-in-law, son-in-law, daughter-in-law, sister-in-law, brother-in-law.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.03</td>\r\n			<td>\r\n				&ldquo;<strong>Working day</strong>&rdquo; means a day on which the employee is regularly scheduled to work.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				6</td>\r\n			<td>\r\n				<strong>REFERENCES and RELATED STATEMENTS of POLICY and PROCEDURE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<ul>\r\n					<li>\r\n						<em>Employment Standards Act </em>(New Brunswick)</li>\r\n					<li>\r\n						SPP HR 4.04.NB &mdash; Personal Leave of Absence</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>7</strong></td>\r\n			<td>\r\n				<strong>PROCEDURE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.01</td>\r\n			<td>\r\n				Employees who wish to take a bereavement leave shall provide written notice to their immediate supervisor of the date the leave is to commence and its duration, as soon as practicable after learning of a death that qualifies for this type of leave. The notice of leave shall be filed in the employee&rsquo;s Personnel file and a copy shall be provided to Payroll. If necessary, and on behalf of the employee, the immediate supervisor may complete the Request for Leave form.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.02</td>\r\n			<td>\r\n				The residence reserves the right to require an employee to provide evidence, such as a photocopy of the death certificate, obituary, burial certificate or other confirmation of the need for bereavement leave.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.03</td>\r\n			<td>\r\n				Bereavement leave must commence no later than the day of the funeral.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', 'en'),
(74, 73, ' Compassionate Care Leave', '<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"table-care\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>1</strong></td>\r\n			<td>\r\n				<strong>POLICY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.01</td>\r\n			<td>\r\n				Subject to the presentation of an appropriate medical certificate from a qualified medical practitioner, and subject to paragraph 7.03, an employee is entitled to compassionate care leave without pay up to a maximum period of 28 weeks for the purpose of providing care or support to someone with whom the employee shares a close family relationship who has a serious medical condition with a significant risk of death within 28 weeks.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>2</strong></td>\r\n			<td>\r\n				<strong>PURPOSE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				2.01</td>\r\n			<td>\r\n				The purpose of this Statement of Policy and Procedure is to establish effective procedures to be used by employees who may be required to provide care or support to family members who are critically ill and at a significant risk of death.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>3</strong></td>\r\n			<td>\r\n				<strong>SCOPE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				3.01</td>\r\n			<td>\r\n				This Statement of Policy and Procedure applies to all employees.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>4</strong></td>\r\n			<td>\r\n				<strong>RESPONSIBILITY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.01</td>\r\n			<td>\r\n				Where possible, employees are responsible for providing two weeks of written notice, or as soon as possible, when requesting compassionate care leave and for presenting an appropriate medical certificate either prior to commencing the leave or upon return from the leave.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5</td>\r\n			<td>\r\n				<strong>DEFINITIONS</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.01</td>\r\n			<td>\r\n				<strong>&ldquo;Close family relationship,&rdquo;</strong> for the purpose of this policy only, means a relationship between persons who are married, between parents and children, between siblings, between grandparents and grandchildren, and includes a relationship between persons who, though not married to one another, and whether or not a blood relationship exists, demonstrate an intention to extend to one another the mutual affection and support normally associated with those relationships first mentioned.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.02</td>\r\n			<td>\r\n				<strong>&ldquo;Medical certificate&rdquo;</strong> means a certificate signed by a qualified medical practitioner stating that the family member has a serious medical condition with a significant risk of death within 28 weeks from when the certificate was issued.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.03</td>\r\n			<td>\r\n				<strong>&ldquo;Provide care or support&rdquo;</strong> means providing psychological or emotional support, arranging for care by a third party or directly providing or participating in the care of a family member.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>6</strong></td>\r\n			<td>\r\n				<strong>REFERENCES and RELATED STATEMENTS of POLICY and PROCEDURE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<p style=\"margin-left: 40px;\">\r\n					<em>Employment Standards Act (New Brunswick) and Regulations<br />\r\n					Employment Insurance Act (Canada)</em><br />\r\n					SPP HR 4.04.NB &mdash; Personal Leave of Absence<br />\r\n					SPP HR 4.12.NB &mdash; Family Leave (Emergency Leave)<br />\r\n					SPP HR 4.24.NB &mdash; Critical Illness Leave<br />\r\n					SPP HR 4.25.NB &mdash; Crime-Related Child Death or Disappearance Leave</p>\r\n				<p>\r\n					&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>7</strong></td>\r\n			<td>\r\n				<strong>PROCEDURE</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"care-content\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				7.01</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				Upon giving their immediate manager written notice of their intention to take a compassionate care leave, employees shall be granted leave without pay for a maximum period of up to 28 weeks.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				Compassionate care leave may be taken only in one-week periods during the period that</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"care-content1\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(i)</td>\r\n			<td>\r\n				starts with the first day of the week in which the medical certificate is issued, or where the leave was commenced before the certificate was issued, he first day of the week in which the leave was commenced; and</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(ii)</td>\r\n			<td>\r\n				ends with the last day of the week in which the earlier of the following first occurs:</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<ul>\r\n					<li>\r\n						a total of 28 weeks of leave have been taken;</li>\r\n					<li>\r\n						the family members dies; or</li>\r\n					<li>\r\n						28 weeks following the first day of the week as determined in paragraph 7.01(b) (i)<br />\r\n						&nbsp;</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"care-content2\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				7.02</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				An employee who takes compassionate care leave may take 28 consecutive weeks of leave or divide up the leave into a maximum of 28 one-week periods over the twenty-eight-week period. The employee shall advise the residence how the leave shall be taken.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				Any change or revision related to how the leave will be taken must have the agreement of both the approving supervisor and the employee.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"care-content3\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				7.03</td>\r\n			<td>\r\n				If two or more employees apply to take compassionate care leave for the same family member, the 28-week leave period may be divided between them.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.04</td>\r\n			<td>\r\n				On expiry of a compassionate care leave, an employee who returns to work shall be reinstated in the position occupied by that employee at the commencement of the leave, or if that position is not available, in a comparable position. The employee shall receive a pay rate that is equal to the greater of the rate the employee most recently earned and the rate the employee would be earning had he or she worked throughout the leave.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.05</td>\r\n			<td>\r\n				If an employee wishes to return to work prior to the expiry of the leave, the employee must give the employer 1 week&rsquo;s written notice.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.06</td>\r\n			<td>\r\n				If an employee wishes to extend the compassionate care leave period beyond 28 weeks, such requests will be considered personal leaves of absence and will be administered in accordance with the Personal Leave of Absence policy, unless a new medical certificate for an additional twenty-eight-week period has been issued.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.07</td>\r\n			<td>\r\n				Upon return to work, the employee must provide a copy of the medial certificate to the employer as soon as possible.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.08</td>\r\n			<td>\r\n				An employee continues to accumulate seniority during a leave of absence, therefore, the employee&rsquo;s employment status is not affected. The period of the leave is not included when determining whether the employee has completed any probationary period.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.09</td>\r\n			<td>\r\n				During compassionate care leave, an employee who is eligible to participate in life insurance plans, accidental death plans, extended health plans and/or dental plans may continue to participate in those plans, unless the employee elects in writing not to do so or the employee provides written notice that he or she does not intend to pay the required contributions, if any, to the plan(s). If employee contributions are required, the employee is responsible for paying those contributions unless, prior to taking leave or within two weeks thereafter, the employee notifies the residence in writing of his/her intention to discontinue contributions during the leave period. Benefits do not accrue during the leave if required employee contributions are not paid. An employee wishing to continue benefits during the leave will be required to provide either post-dated cheques or make other suitable arrangements regarding payment of the employee&rsquo;s portion of premiums for benefit coverage.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.10</td>\r\n			<td>\r\n				An employee&rsquo;s entitlement to compassionate care leave is in addition to an employee&rsquo;s entitlement to other statutory leaves specified in the Employment Standards Act (New Brunswick).</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', 'en');
INSERT INTO `manual_policies_desc` (`desc_id`, `manual_policies_id`, `title`, `content`, `language`) VALUES
(75, 74, ' Jury and Witness Duty', '<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"table-jury\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>1</strong></td>\r\n			<td>\r\n				<strong>POLICY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.01</td>\r\n			<td>\r\n				An employee who is summoned for jury duty, or as a court witness, shall be granted jury or court witness leave, without pay, for the period required.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.02</td>\r\n			<td>\r\n				Seniority shall continue to accrue during jury or court witness leave.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.03</td>\r\n			<td>\r\n				On conclusion of jury or court witness leave, the employee shall be reinstated to the position most recently held, if it still exists, or to a comparable position, if it does not, at the same wage rate the employee enjoyed at the time the employee&rsquo;s leave commenced.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.04</td>\r\n			<td>\r\n				An employee who is granted jury or court witness leave may continue to receive pay at the employee&rsquo;s regular base wage for a maximum of 2 weeks conditional on the employee:</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"jury-content\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(i)</td>\r\n			<td>\r\n				providing the residence with a copy of the jury summons or summons to be a witness as soon as possible;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(ii)</td>\r\n			<td>\r\n				submitting a certified statement of fees paid by the court, or any other parties for serving; and</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(iii)</td>\r\n			<td>\r\n				authorizing the deduction from regular base pay an amount equivalent to the fees paid by the court or any other party, if any, for serving as a juror or court witness. In this connection, fees do not include reimbursement to the employee by the court or any other party for expenses such as travel expenses.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"jury-content1\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				1.05</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				An employee who is granted time off work for jury or court witness leave and whose services as a juror, or as a court witness, are not required is expected to return to work immediately if more than 3 hours remain in the employee&rsquo;s regular work shift.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				[Optional] Where facilities exist and such activity is not prohibited by the Court, during breaks or while waiting to be empanelled, the employee is expected to attempt to attend to his or her job-related duties, including checking and returning messages or working on company-related matters on his or her laptop computer.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"jury-content2\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				1.06</td>\r\n			<td>\r\n				An employee who attends court as a plaintiff or defendant in a personal matter is expected to use vacation time, or to make arrangements for an unpaid personal leave of absence in accordance with the Personal Leave of Absence policy. Employees must use accrued vacation and/or time in lieu of overtime prior to requesting an unpaid personal leave of absence.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>2</strong></td>\r\n			<td>\r\n				<strong>PURPOSE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				2.01</td>\r\n			<td>\r\n				The purpose of this policy is to assist employees to fulfill their civic responsibilities to the judicial process by providing paid and unpaid leaves of absence.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>3</strong></td>\r\n			<td>\r\n				<strong>SCOPE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				3.01</td>\r\n			<td>\r\n				This policy applies to all active employees.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>4</strong></td>\r\n			<td>\r\n				<strong>RESPONSIBILITY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.01</td>\r\n			<td>\r\n				<strong>Employee</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"jury-employee\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				It is the employee&rsquo;s responsibility to notify his or her immediate Supervisor as soon as a Notice and Questionnaire Re: Jury Duty, or summons to witness is received.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				Upon serving as a potential juror, juror or court witness, it is the employee&rsquo;s responsibility to provide the employer with proof of time served.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(c)</td>\r\n			<td>\r\n				If the employee has received a paid leave of absence and receives any remuneration from the court or other party for such services, it is the employee&rsquo;s responsibility to report any such payments to the residence, in the form required, save and except for travel and other expenses.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(d)</td>\r\n			<td>\r\n				It is the employee&rsquo;s responsibility to keep his or her supervisor informed of the progress of the matter, and his or her estimated date to return to work.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(e)</td>\r\n			<td>\r\n				Depending on the employee&#39;s position and job function, to the maximum extent possible, the employee is expected to try to discharge work-related duties during breaks or while waiting to be empanelled.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table class=\"jury-content4\" border=\"0\" cellpadding=\"1\" cellspacing=\"1\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				4.02</td>\r\n			<td>\r\n				It is the Supervisor&rsquo;s responsibility to ensure that proof of time served is obtained from an employee on jury leave and that time sheets and attendance records of paid and unpaid absences are properly completed and submitted.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>5</strong></td>\r\n			<td>\r\n				<strong>DEFINITIONS</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				None.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>6</strong></td>\r\n			<td>\r\n				<strong>REFERENCES and RELATED STATEMENTS of POLICY and PROCEDURE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<ul>\r\n					<li>\r\n						<em>Jury Act</em> (New Brunswick)</li>\r\n					<li>\r\n						<em>Employment Standards Act </em>(New Brunswick)</li>\r\n					<li>\r\n						SPP HR.4.04.NB &mdash; Personal Leave of Absence<br />\r\n						<br />\r\n						&nbsp;</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>7</strong></td>\r\n			<td>\r\n				<strong>PROCEDURE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.01</td>\r\n			<td>\r\n				Employees are requested to provide Supervisors with notice of impending jury service or court attendance requiring leave as soon as possible.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.02</td>\r\n			<td>\r\n				An employee who is granted time off work under the policy and whose services as a juror, or as a court witness, are not required on that day is expected to return to work immediately if more than 3 hours remain in the employee&rsquo;s regular work shift. Where facilities exist and provided that the Court does not prohibit, the employee is expected to try to discharge his or her work-related duties while on breaks or while waiting to be empanelled.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.03</td>\r\n			<td>\r\n				If an employee is granted leave with pay, the employee must present his or her Supervisor with certified documentation of time served as a juror or witness, and any remuneration received from the court, or other party for such services, save and except reimbursement for expenses. The required documentation must be received by the Supervisor no later than weeks after the end of jury of court witness leave. Failure to comply with this requirement could result in the equivalent of wages paid during the leave being treated as an overpayment of wages during the leave. Any such overpayment will be withheld from future wages owing to the employee.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', 'en'),
(76, 75, 'Pregnancy/Maternity Leave', '<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"table-pregnancy\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				1</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<strong>POLICY</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"maternity-contents\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				1.01</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				A pregnant employee is entitled to and must be granted a pregnancy leave of absence without pay, not exceeding 17 weeks in duration provided she:</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"maternity-contents1\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(i)</td>\r\n			<td>\r\n				advises the residence of the pregnancy at least four months before the expected due date;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(ii)</td>\r\n			<td>\r\n				in the absence of an emergency, submits a written application for Pregnancy Leave at least two weeks before the date on which the leave is to commence advising the residence of the expected duration of the leave; and</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(iii)</td>\r\n			<td>\r\n				if requested, provides a medical certificate from a medical practitioner, nurse practitioner or midwife stating the expected birth date.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"maternity-contents2\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				In the case of an employee who stops working because of complications caused by her pregnancy or because of a birth, still-birth or miscarriage that happens earlier than the date the employee was expected to give birth, the employee must, as soon as reasonably practicable, provide:</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"maternity-contents3\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(i)</td>\r\n			<td>\r\n				a written notice of the date pregnancy leave began or is to begin;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(ii)</td>\r\n			<td>\r\n				notice of the delivery, if the delivery was sooner than expected; and</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(iii)</td>\r\n			<td>\r\n				the expected return to work date.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"maternity-contents4\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				1.02</td>\r\n			<td>\r\n				Unless Paragraph 1.01(b) applies, pregnancy leave may commence no earlier than 13 weeks before the expected birth date and no later than the day on which the employee gives birth. Such leave must be for a continuous period and not intermittent.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.03</td>\r\n			<td>\r\n				If, as a result of an employee&rsquo;s pregnancy, an employee is unable to reasonably perform the essential duties of her job, and no alternative employment is available, the residence may require the employee to commence the leave of absence.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>2</strong></td>\r\n			<td>\r\n				<strong>PURPOSE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				2.01</td>\r\n			<td>\r\n				The purpose of this Statement of Policy and Procedure is to ensure employees are aware of their rights related to pregnancy leave.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>3</strong></td>\r\n			<td>\r\n				<strong>SCOPE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				3.01</td>\r\n			<td>\r\n				This Statement of Policy and Procedure applies to all employees.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>4</strong></td>\r\n			<td>\r\n				<strong>RESPONSIBILITY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.01</td>\r\n			<td>\r\n				Employees are responsible for providing the appropriate written notices and medical certificates required by this Statement of Policy and Procedure.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>5</strong></td>\r\n			<td>\r\n				<strong>DEFINITIONS</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				None.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>6</strong></td>\r\n			<td>\r\n				<strong>REFERENCES and RELATED STATEMENTS of POLICY and PROCEDURE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<ul>\r\n					<li>\r\n						Employment Standards Act (New Brunswick)</li>\r\n					<li>\r\n						SPP HR 4.02.NB &mdash; Vacation and Vacation Pay</li>\r\n					<li>\r\n						SPP HR 4.03.NB &mdash; Public Holidays</li>\r\n					<li>\r\n						SPP HR 4.06.NB &mdash; Parental and/or Adoption Leave</li>\r\n					<li>\r\n						SPP HR 5.04.NB &mdash; Accommodation on the Basis of Disability</li>\r\n				</ul>\r\n				<p style=\"margin-left: 40px;\">\r\n					<br />\r\n					&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>7</strong></td>\r\n			<td>\r\n				<strong>PROCEDURE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.01</td>\r\n			<td>\r\n				<strong>[Optional]</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				During pregnancy leave, an employee who is eligible to participate in life insurance plans, accidental death plans, extended health plans, and/or dental plans, may continue to participate in those plans, unless the employee elects in writing not to do so or the employee provides written notice that she does not intend to pay the required contributions, if any, to the plan(s). If employee contributions are required, the employee is responsible for paying the contributions of both the company and the employee unless, prior to taking leave or within two weeks thereafter, the employee notifies the residence in writing of her intention to discontinue contributions during the leave period. If required contributions are not paid, benefits do not accrue during the leave. An employee wishing to continue benefits during the leave will be required to provide either post-dated cheques or make other suitable arrangements regarding payment of both the employer&rsquo;s and the employee&rsquo;s portion of premiums for benefit coverage.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.02</td>\r\n			<td>\r\n				The period of a pregnancy leave is included in calculating an employee&rsquo;s length of employment, service or seniority and the period of employment prior to the commencement of the leave and after the employee&rsquo;s return to work is considered to be continuous.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.03</td>\r\n			<td>\r\n				On expiry of a pregnancy leave, an employee who returns to work must be reinstated in the position occupied by her at the commencement of the leave, or if that position is not available, in a comparable position with not less than the same wages and benefits and with no loss of seniority or benefits accrued to the commencement of the leave.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.04</td>\r\n			<td>\r\n				An employee who is entitled to parental leave in addition to pregnancy leave must commence that leave upon the expiry of pregnancy leave, unless the child has not yet come into the care and control of the employee, unless agreed by the employee and the residence.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"maternity-contents5\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				7.05</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				An employee may end a pregnancy leave earlier than planned provided she gives at least four weeks of written notice of the date on which she intends to return to work.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)<br />\r\n				&nbsp;</td>\r\n			<td>\r\n				An employee may end a pregnancy leave later than planned, subject to the maximum leave period specified in paragraph 1.01, provided she gives written notice at least four weeks&rsquo; before the original return to work date.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.06</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				An employee who does not intend to return to work after the leave ends is required to provide written notice at least four weeks before the scheduled date of her return to work.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				An employee who fails to return to work upon the expiry of her pregnancy leave and fails to provide a written notice of termination as required in paragraph 7.06(a) is deemed to have voluntarily resigned her employment effective with the expiry of the leave.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"maternity-contents5\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				7.07</td>\r\n			<td>\r\n				No employee will be intimidated, suspended, laid off, dismissed or penalized in any way because she becomes eligible to, intends to take, or takes pregnancy leave.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.08</td>\r\n			<td>\r\n				<strong>Holiday falling during leave</strong> &mdash; If a holiday falls during an employee&rsquo;s leave and the employee is on pregnancy or parental leave, the employee is entitled to holiday pay only if otherwise entitled under paragraph 1.03 of SPP HR 4.03.NB &ndash; Public Holidays.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.09</td>\r\n			<td>\r\n				Once the leave is granted, an employee may be entitled to 15 weeks of EI maternity benefits and must apply directly with Service Canada for approval, in accordance with the requirements under the <em>Employment Insurance Act.</em></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', 'en'),
(77, 76, ' Parental Leave', '<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"parental\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>1</strong></td>\r\n			<td>\r\n				<strong>POLICY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.01</td>\r\n			<td>\r\n				An employee who has completed at least 20 weeks of employment and who is the parent of a child is entitled to parental/adoption leave without pay, up to a maximum period of 62 consecutive weeks commencing no earlier than the date of:</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"parental-content\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				the birth of the child; or</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				when the child comes into the custody, care and control of a parent for the first time.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"parental-leave\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				1.02</td>\r\n			<td>\r\n				The parental/adoption leave must end no later than 62 weeks within a 78-week period after the date in 1.01(a) or 1.01(b).<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.03</td>\r\n			<td>\r\n				Where two parents are both employees of the residence, the leave may be taken either by one parent or shared between the two employees.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"parental-cont\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>2</strong></td>\r\n			<td>\r\n				<strong>PURPOSE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				2.01</td>\r\n			<td>\r\n				The purpose of this Statement of Policy and Procedure is to establish effective procedures to be used by employees who become parents and wish to provide for the care and custody of a newborn or adopted child.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>3</strong></td>\r\n			<td>\r\n				<strong>SCOPE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				3.01</td>\r\n			<td>\r\n				This Statement of Policy and Procedure applies to all employees.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>4</strong></td>\r\n			<td>\r\n				<strong>RESPONSIBILITY</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"parental-cont1\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				4.01</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				Unless there is an emergency, employees are responsible for providing at least four weeks of written notice when requesting a parental leave, specifying the date the leave is to commence and the duration of the leave.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				Employees requesting adoption leave must provide written notice of the employee&rsquo;s intention to take the leave as soon as he or she has been approved by a family services agency as a prospective adopting parent, or four months in the case of a private adoption. In the case of an emergency or unanticipated early placement of the child with the adopting parent, the employee must provide the residence with as much notice as is reasonably practicable.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"parental-cont2\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>5</strong></td>\r\n			<td>\r\n				<strong>DEFINITIONS</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.01</td>\r\n			<td>\r\n				<strong>&ldquo;Child&rdquo;</strong> means a newborn child of the parent and, in the case of an adopted child, a child who has not reached the age of nineteen.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.02</td>\r\n			<td>\r\n				<strong>&ldquo;Parent&rdquo;</strong> means the father or mother of a newborn child and includes a person with whom a child is placed for adoption.&nbsp;<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>6</strong></td>\r\n			<td>\r\n				<strong>REFERENCES and RELATED STATEMENTS of POLICY and PROCEDURE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<ul>\r\n					<li>\r\n						Employment Standards Act and Regulations (New Brunswick)</li>\r\n					<li>\r\n						SPP HR 4.02.NB &mdash; Vacation and Vacation Pay</li>\r\n					<li>\r\n						SPP HR 4.03.NB &mdash; Public Holidays</li>\r\n					<li>\r\n						SPP HR 4.05.NB &mdash; Pregnancy/Maternity Leave<br />\r\n						<br />\r\n						&nbsp;</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>7</strong></td>\r\n			<td>\r\n				<strong>PROCEDURE</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"parental-cont3\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				7.01</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				Eligible employees, upon giving their immediate manager the minimum prescribed written notice of their intention to take a parental/adoption leave, must be granted the requested leave for a maximum period of up to 62 consecutive weeks.&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				If an employee stops working because a child comes into the employee&rsquo;s custody, care and control for the first time earlier than expected:</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"parental-cont4\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(i)</td>\r\n			<td>\r\n				the employee&rsquo;s parental/adoption leave begins on the day he or she stops working; and</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(ii)</td>\r\n			<td>\r\n				the employee must, as soon as practicable after stopping work, give written notice that he or she is taking parental/adoption leave.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"parental-cont5\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				7.02</td>\r\n			<td>\r\n				An employee who takes pregnancy leave and wishes to take parental leave must commence the parental leave immediately upon expiry of the pregnancy leave, without a return to work, unless agreed to otherwise by the residence and the employee, or in the case where the child is hospitalized at the time the pregnancy leave expires. Otherwise, parental/adoption leave must commence not later than 78 weeks after the day the child is born or comes into the custody, care and control of a parent for the first time.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.03</td>\r\n			<td>\r\n				On expiry of a parental/adoption leave, an employee who returns to work must be reinstated in the position occupied by that employee at the commencement of the leave, or if that position is not available, in an equivalent position with no loss in pay, seniority or benefits accrued up to the commencement of the leave.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.04</td>\r\n			<td>\r\n				The statutory period of employment will not be broken by the leave. The period of the leave is not included when determining whether the employee has completed the probationary period.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.05</td>\r\n			<td>\r\n				<strong>[Optional]</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				During parental/adoption leave, an employee who is eligible to participate in pension plans, life insurance plans, accidental death plans, extended health plans and/or dental plans may continue to participate in those plans unless the employee elects in writing not to do so, or the employee provides written notice that he or she does not intend to pay the required contributions, if any, to the plan(s). If the employee elects to continue participating in such plans, the employee is responsible for paying both the company&rsquo;s and employee&rsquo;s contributions prior to taking leave or within two weeks thereafter. If the employee notifies the residence in writing of his or her intention to discontinue contributions during the leave period, benefits coverage will be discontinued.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.06</td>\r\n			<td>\r\n				Where two employees are eligible for parental/adoption leave in regards to the same child, the leave of absence may be taken by one of the employees, or shared between the two employees, but the total parental/adoption leave taken by both employees must not exceed 62 weeks.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.07</td>\r\n			<td>\r\n				An employee may end a parental/adoption leave earlier than planned by providing at least four weeks of written notice of the date on which the leave is to end.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.08</td>\r\n			<td>\r\n				An employee who takes parental/adoption leave must not terminate his or her employment before the leave expires or when it expires without giving at least four weeks&rsquo; written notice of termination. Unless written notice of termination is given, an employee who fails to return to work upon the expiry of a parental/adoption leave is deemed to have voluntarily resigned his or her employment coincident with the expiry of the leave.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.09</td>\r\n			<td>\r\n				No employee who has completed the probationary period will be terminated or laid off solely because the employee has applied for leave in accordance with this policy.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.10</td>\r\n			<td>\r\n				<strong>Holiday Falling during Leave</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				If a holiday falls during an employee&rsquo;s leave and the employee is on pregnancy or parental leave, the employee is entitled to holiday pay only if otherwise entitled under paragraph 1.03 of SPP HR 4.03.NB &mdash; Public Holidays.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.11</td>\r\n			<td>\r\n				EI parental benefits are offered to parents who are caring for a newborn or newly adopted child or children. There are two options available for receiving parental benefits: standard or extended.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"parental-cont7\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				Standard parental benefits can be paid for a maximum of 35 weeks and must be claimed within a 52-week period (12 months) after the week the child was born or placed for the purpose of adoption. The benefits are available to biological, adoptive, or legally recognized parents at a&nbsp;<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				weekly benefit rate of 55 percent of the claimant&rsquo;s average weekly insurable earnings up to a maximum amount. The two parents can share these 35 weeks of standard parental benefits.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				&nbsp;Extended parental benefits can be paid for a maximum of 61 weeks and must be claimed within a 78-week period (18 months) after the week the child was born or placed for the purpose of adoption. The benefits are available to biological, adoptive, or legally recognized parents at a weekly benefit rate of 33 percent of the claimant&rsquo;s average weekly insurable earnings up to a maximum amount. The two parents can share these 61 weeks of extended parental benefits.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"parental-cont8\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				The employee can choose to claim extended parental benefits only if the child was born or placed with the employee for the purpose of adoption on or after March 16, 2018.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', 'en'),
(78, 77, 'Benefits Coverage', '<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"table-benefits\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>1</strong></td>\r\n			<td>\r\n				<strong>POLICY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.01</td>\r\n			<td>\r\n				It is the policy of the residence to provide fair and equitable benefit plans and programs based on industry practices. The following principles govern considerations regarding the type of benefit programs that may be made available to employees.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"benifit-cont\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				The residence believes that employees are primarily responsible for their own welfare and the welfare of their dependants.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				The residence may make arrangements to provide appropriate benefit plans/programs that are designed to protect employees from significant financial hardship resulting from unforeseen circumstances. Where plans are provided, reasonable efforts will be made to ensure such plans are competitive with those that are generally in use in the same industry and communities in which we compete.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(c)</td>\r\n			<td>\r\n				The residence may contribute to some, or all, of the costs of various plans, where it can afford to do so and where it is general practice to do so in our industry. Employees are expected to contribute to any premium costs associated with benefit programs.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(d)</td>\r\n			<td>\r\n				Employees may be offered the opportunity to participate in appropriate plans on the assumption that costs of a group plan would normally be less than the costs an employee would have to pay as an individual.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(e)</td>\r\n			<td>\r\n				The residence may, in its sole discretion, decide upon the following:</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<ul>\r\n					<li>\r\n						plan design or modification, including the reduction of benefits;</li>\r\n					<li>\r\n						introduction of new plans;</li>\r\n					<li>\r\n						selection of carriers;</li>\r\n					<li>\r\n						funding arrangements;</li>\r\n					<li>\r\n						cost sharing arrangements; and</li>\r\n					<li>\r\n						benefits to be provided.</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(f)</td>\r\n			<td>\r\n				Wherever possible and practical, master benefit programs shall be used for all locations of The residence; however, benefits under those programs may vary depending upon differing circumstances.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"benefits-cont2\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>2</strong></td>\r\n			<td>\r\n				<strong>PURPOSE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				2.01</td>\r\n			<td>\r\n				The purpose of this Statement of Policy and Procedure is to establish the principles to be considered when determining the appropriateness of introducing or modifying benefits plans or programs for employees.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>3</strong></td>\r\n			<td>\r\n				<strong>SCOPE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				3.01</td>\r\n			<td>\r\n				This Statement of Policy and Procedure applies to all employees.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>4</strong></td>\r\n			<td>\r\n				<strong>RESPONSIBILITY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.01</td>\r\n			<td>\r\n				The General Manager is responsible for ensuring the consistent application and administration of this policy, throughout the organization, and for approving the introduction of any new benefit plan, or modifications to any existing benefit plans prior to any implementation.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>5</strong></td>\r\n			<td>\r\n				<strong>DEFINITIONS</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				None.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>6</strong></td>\r\n			<td>\r\n				<strong>REFERENCES and RELATED STATEMENTS of POLICY and PROCEDURE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				None.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>7</strong></td>\r\n			<td>\r\n				<strong>PROCEDURE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.01</td>\r\n			<td>\r\n				Approved benefit plans and programs shall be communicated through benefit booklets, published by the plan providers. The booklet or plan summary shall outline the essential elements of each benefit plan, cost sharing arrangements (if any), claims procedures, carrier information, and other pertinent information deemed necessary, or advisable, for understanding the various benefit plans.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.02</td>\r\n			<td>\r\n				In certain circumstances, the residence provides benefit plan and programs on a direct basis without normally involving third parties such as insurance carriers or regulatory authorities. Where direct benefits are provided the residence shall publish the appropriate policies and procedures governing those plans.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.03</td>\r\n			<td>\r\n				In the case of conflict related to the administration of, or to the benefits provided by, a benefit plan or program, the master contract(s) negotiated with the plan provider(s) and/or regulatory authority shall govern the resolution of such conflict; otherwise, the conflict shall be resolved by the decision of the General Manager in his or her sole discretion.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.04</td>\r\n			<td>\r\n				Proposed changes to existing benefit programs, or proposals to introduce new benefit programs, shall be submitted to the General Manager for approval. Such proposals shall provide a detailed outline of the reasons for the change or introduction, the design of the benefits program, the proposed effective date of the change or introduction, the number of employees affected by the proposal, and a five-year projection of all costs and/or cost increases associated with the proposal.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', 'en'),
(79, 78, 'Salary Administration, pay Schedules, Paid Statutory Holidays', '<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"salary\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>1</strong></td>\r\n			<td>\r\n				<strong>POLICY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.01</td>\r\n			<td>\r\n				The prime determinants of pay levels and/or pay increases are the individual&#39;s sustained past performance, the individual&#39;s present wage related to the Job Rate for the job and the time the individual has been performing the job. Length of service or tenure in a position does not, standing alone, justify granting pay increases.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.02</td>\r\n			<td>\r\n				Pay ranges will be published and remain in effect until further notice.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>2</strong></td>\r\n			<td>\r\n				<strong>PURPOSE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				2.01</td>\r\n			<td>\r\n				The purpose of this Statement of Policy and Procedure is to ensure that each employee is fairly and equitably rewarded for sustained competent performance in a job and to ensure that reasonable pay relationships are maintained from one performance level to another, from one organizational level to another and from one department to another.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>3</strong></td>\r\n			<td>\r\n				<strong>SCOPE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				3.01</td>\r\n			<td>\r\n				This Statement of Policy and Procedure applies to all Non-union employees. [Where appropriate: Pay Administration for Union employees is governed by the terms of the applicable Collective Agreement.]<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>4</strong></td>\r\n			<td>\r\n				<strong>RESPONSIBILITY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.01</td>\r\n			<td>\r\n				The General Manager is responsible for issuing annual pay increase guidelines and approving all pay increases.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.02</td>\r\n			<td>\r\n				Managers are responsible for initiating the necessary documents to affect pay increases for their direct reports consistent with published guidelines.&nbsp;<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>5</strong></td>\r\n			<td>\r\n				<strong>DEFINITIONS</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.01</td>\r\n			<td>\r\n				Types of Pay Increases</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"salary-cont\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				<strong>Merit Increase</strong> &mdash; An increase, based on performance, within the same job level and pay range.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				<strong>Promotion Increase</strong> &mdash; An increase which may be granted to an individual who is promoted to a position with a higher job level.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(c)</td>\r\n			<td>\r\n				<strong>Reclassification Increase</strong> &mdash; An increase that may be granted when the duties of a job have changed sufficiently so that the job is reclassified to a higher job level.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(d)</td>\r\n			<td>\r\n				<strong>Remedial Increase</strong> &mdash; An increase which may be granted, at any time, to adjust an individual&#39;s wage at least to the minimum of the wage range.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	<br />\r\n	<br />\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"salary-cont1\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>6</strong></td>\r\n			<td>\r\n				<strong>REFERENCES and RELATED STATEMENTS of POLICY and PROCEDURE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<ul>\r\n					<li>\r\n						SPP HR 3.01.NB &mdash; Pay Principles</li>\r\n					<li>\r\n						SPP HR 3.02.NB &mdash; Job Evaluation</li>\r\n					<li>\r\n						SPP HR 3.04.NB &mdash; Job Performance Review</li>\r\n					<li>\r\n						SPP HR 3.05.NB &mdash; Pay Range Structure<br />\r\n						<br />\r\n						&nbsp;</li>\r\n				</ul>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>7</strong></td>\r\n			<td>\r\n				<strong>PROCEDURE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.01</td>\r\n			<td>\r\n				<strong>Size and Timing of Pay Adjustments</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"salary-cont3\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				<strong>Merit Increase</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				The size of a merit increase may vary based on an individual&rsquo;s job performance and Range Index. Generally, a merit increase may be granted after a minimum time interval of 12 months from the date of the last pay adjustment. More frequent adjustments may be justified by performance.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				<strong>Promotion Increase</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				A promotion may occur at any time and a Promotion Increase may be granted at any time up to 6 months after the effective date of the promotion, depending on the individual&#39;s date of last increase. A Promotion Increase granted after the effective date of promotion, shall not be retroactive to the date of promotion.&nbsp;<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(c)</td>\r\n			<td>\r\n				<strong>Reclassification Increase</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				This type of pay adjustment may be granted when an individual&rsquo;s job is re-evaluated and adjusted upwards to a classification which warrants the assignment of a new pay range. The prime determinants for an adjustment are the individual&#39;s performance level and the Range Index related to the new pay range. The size of a Reclassification Increase should be less than the amount that would be appropriate for a Promotion Increase.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(d)</td>\r\n			<td>\r\n				<strong>Remedial Increase</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				This type of increase is used only for individuals who are paid below the minimum of their pay range. The size of such an adjustment is dependent upon the individual&rsquo;s current wage in relation to the minimum of the pay range. This type of increase should be infrequent since only in rare and exceptional cases should an individual be paid less than the minimum of the pay range.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', 'en');
INSERT INTO `manual_policies_desc` (`desc_id`, `manual_policies_id`, `title`, `content`, `language`) VALUES
(80, 79, 'Termination of Employment (Voluntary and/or Involuntary)', '<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"table-termination\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>1</strong></td>\r\n			<td>\r\n				<strong>POLICY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.01</td>\r\n			<td>\r\n				Terminations arise from Resignation, Retirement, Involuntary Termination and/or Indefinite Lay-off. All terminations shall be handled in a fair and lawful manner.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.02</td>\r\n			<td>\r\n				Employees who resign from their employment voluntarily shall be requested to participate in an Exit Interview.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.03</td>\r\n			<td>\r\n				Indefinite lay-offs and Involuntary Terminations require prior review and the written approval of the General Manager.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.04</td>\r\n			<td>\r\n				An employee who is released for disciplinary reasons (except for just cause) or inability to perform work usually will receive a warning, written or otherwise, prior to Involuntary Termination proceedings being implemented.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.05</td>\r\n			<td>\r\n				The residence will comply with the requirements of the New Brunswick Employment Standards Act with respect to all terminations.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>2</strong></td>\r\n			<td>\r\n				<strong>PURPOSE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				2.01</td>\r\n			<td>\r\n				The purpose of this Statement of Policy and Procedure is to ensure that employees who terminate employment, both voluntarily and involuntarily, are treated fairly and in a consistent manner.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>3</strong></td>\r\n			<td>\r\n				<strong>SCOPE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				3.01</td>\r\n			<td>\r\n				This Statement of Policy and Procedure applies to Full-time Non-union and Part-time Non-union employees in New Brunswick. [Where appropriate: Termination of Union employees are governed by the terms of the applicable Collective Agreement.]<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>4</strong></td>\r\n			<td>\r\n				<strong>RESPONSIBILITY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.01</td>\r\n			<td>\r\n				It is the responsibility of the General Manager to ensure this policy is administered consistently and fairly.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.02</td>\r\n			<td>\r\n				Managers of terminating employees are responsible for completing a Termination Check Chart similar to that contained in Attachment A and for ensuring the Check Chart is filed in the employee&rsquo;s Personnel file.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>5</strong></td>\r\n			<td>\r\n				<strong>DEFINITIONS</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.01</td>\r\n			<td>\r\n				<strong>&ldquo;Involuntary Termination&rdquo;</strong> means a cessation of employment which may be initiated by the residence, at any time, in its sole discretion.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.02</td>\r\n			<td>\r\n				<strong>&ldquo;Indefinite Lay-off&rdquo; </strong>means:</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"termination-cont\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				a temporary interruption of the employment relationship at the direction of the residence because of a lack of work;&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				a lay-off that is longer than six (6) days.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"termination-cont1\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				5.03</td>\r\n			<td>\r\n				<strong>&ldquo;Resignation&rdquo;</strong> means a cessation of employment which is initiated by an employee, without duress.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.04</td>\r\n			<td>\r\n				<strong>&ldquo;Temporary Lay-off&rdquo;</strong> means a lay-off of not more than six (6) days.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>6</strong></td>\r\n			<td>\r\n				<strong>REFERENCES and RELATED STATEMENTS of POLICY and PROCEDURE</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<ul style=\"margin-left: 80px;\">\r\n	<li>\r\n		Employment Standards Act and Regulations (New Brunswick)</li>\r\n	<li>\r\n		SPP HR 2.05.NB &mdash; Employment Classifications</li>\r\n	<li>\r\n		SPP HR 5.06.NB &mdash; Discipline</li>\r\n</ul>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"termination-cont2\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>7</strong></td>\r\n			<td>\r\n				<strong>PROCEDURE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.01</td>\r\n			<td>\r\n				The Termination Check Chart shown as Attachment A, or a reasonable facsimile thereof, shall be completed by the immediate manager for all employees who cease employment.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.02</td>\r\n			<td>\r\n				<strong>Resignation</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				Employees are expected to provide written notice of resignation to their immediate manager at least two (2) weeks in advance of the effective date of resignation. Employees resigning from more senior positions are expected to give advance written notice in keeping with their job responsibilities.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.03</td>\r\n			<td>\r\n				<strong>Involuntary Termination</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"termination-cont3\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				An Involuntary Termination shall be fully documented by the releasing manager and discussed with the General Manager prior to any termination discussion with the employee. The Recommendation to Terminate Form shown as Attachment B to this Statement of Policy and Procedure is used for this purpose.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				An Involuntary Termination of any employee requires the written approval of the General Manager, or a designated nominee prior to implementation.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"termination-cont4\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				7.04</td>\r\n			<td>\r\n				<strong>Notice of Involuntary Termination/Indefinite Lay-off</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"termination-cont6\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				If eligible, an employee who is continuously employed for more than six months and who is terminated involuntarily, or placed on Indefinite Lay-off, shall receive written notice of termination, or pay in lieu thereof, in accordance with the provisions of the <em>Employment Standards</em> Act or a written contract of employment, whichever is the greater.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				For greater clarity, a person is not eligible for notice of termination or lay-off if that person:</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<ul style=\"margin-left: 120px;\">\r\n	<li>\r\n		is laid off where there is a lack of work, due to any reason unforeseen by the employer at the time notice would have otherwise have been given, for such period as the</li>\r\n	<li>\r\n		lack of work continues due to that reason;</li>\r\n	<li>\r\n		is placed on lay-off for up to six days;</li>\r\n	<li>\r\n		is laid off after refusing an offer of reasonable alternate work;</li>\r\n	<li>\r\n		has completed a term of employment that was fixed in the employment contract, unless the employee is employed for a period of three months beyond that period;</li>\r\n	<li>\r\n		has completed a definite assignment that the employee was hired to perform over a period not exceeding 12 months, whether or not the exact period was stated in the employment contract;</li>\r\n	<li>\r\n		having reached age of retirement is retired under a bona fide retirement plan;</li>\r\n	<li>\r\n		is doing construction work in the construction industry;</li>\r\n	<li>\r\n		is laid off as a result of normal seasonal reduction, closure or suspension of an operation;</li>\r\n	<li>\r\n		is terminated as a result of just cause.</li>\r\n</ul>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"termination-cont5\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				7.05</td>\r\n			<td>\r\n				In the event termination arrangements are offered to an employee and the total of those arrangements exceed the statutory requirements of the Employment Standards Act, those arrangements which are in excess of the statutory requirements shall not be made available unless the residence receives a General Release and Settlement Agreement properly signed by the employee.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.06</td>\r\n			<td>\r\n				A terminated employee shall be paid any monies owing on the next normal pay day and in no case shall the payment of outstanding wages or benefits be later than 21 days after the last date of employment.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>8</strong></td>\r\n			<td>\r\n				<strong>FORMS</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				<span style=\"color:#0000cd;\"><u>Attachment A &mdash; Termination Check Chart</u></span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', 'en'),
(81, 80, 'Temporary Lay-Offs', '<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"table-lay\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>1</strong></td>\r\n			<td>\r\n				<strong>POLICY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.01</td>\r\n			<td>\r\n				The residence reserves the right to temporarily lay off employees when there is a shortage of work or demand for the company&rsquo;s goods and services, when the company is going through financial difficulties and a restructuring of the organization is needed, to cut costs and increase efficiency, or when the country or province is going through a declared emergency or economic crisis.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.02</td>\r\n			<td>\r\n				The residence will comply with the requirements and temporary layoff provisions under the New Brunswick Employment Standards Act and the employee&rsquo;s employment contract, if applicable.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.03</td>\r\n			<td>\r\n				This policy is intended to provide direction to management and employees when a decision is made to temporarily lay off an employee and suspend the employment relationship.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.04</td>\r\n			<td>\r\n				In all cases where an employee is temporarily laid off, approval must first be obtained at the General Manager level.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>2</strong></td>\r\n			<td>\r\n				<strong>PURPOSE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				2.01</td>\r\n			<td>\r\n				The purpose of this Statement of Policy and Procedure is to ensure that when employees are being laid off, they are treated in a fair and consistent manner and within the provisions of the law.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>3</strong></td>\r\n			<td>\r\n				<strong>SCOPE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				3.01</td>\r\n			<td>\r\n				This Statement of Policy and Procedure applies to all full-time and part-time employees and dependent contractors in New Brunswick. It does not apply to employees who are covered by a collective agreement in which specific temporary layoff provisions have been negotiated between the employer and the union.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>4</strong></td>\r\n			<td>\r\n				<strong>RESPONSIBILITY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.01</td>\r\n			<td>\r\n				Managers are responsible for:</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<ul style=\"margin-left: 80px;\">\r\n	<li>\r\n		Consulting with Human Resources or the General Manager or both regarding temporary layoff matters and following instructions provided by them;</li>\r\n	<li>\r\n		Following the temporary layoff procedures found in this policy;</li>\r\n	<li>\r\n		Preparing written documentation for employees related to the temporary stoppage of employment and maintaining copies of official correspondence in the employees&rsquo; personal files;</li>\r\n	<li>\r\n		Notifying employees selected for temporary layoff through an official letter explaining the reasons for the layoff and whether they should expect to be recalled;</li>\r\n	<li>\r\n		Discussing the temporary layoff terms with affected employees and addressing any outstanding payments and documentation;</li>\r\n	<li>\r\n		Providing employees with information about the applicable accrued benefits that are due, such as group insurance and vacation entitlements, and directing employees to the appropriate department (i.e., Payroll to address specific questions concerning insurance or pension issues, including benefits, options, etc.);</li>\r\n	<li>\r\n		Diarizing and tracking the period of layoff and ensuring the employee(s) are recalled before the period expires;</li>\r\n	<li>\r\n		Recalling the employee(s) to work as per the recall procedures found in this policy; and</li>\r\n	<li>\r\n		Informing the employee(s) if they are not being recalled and proceeding to the termination process as per the residence&rsquo;s termination policy.</li>\r\n</ul>\r\n<p>\r\n	<br />\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"lay-cont\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				4.02</td>\r\n			<td>\r\n				Human Resources or the General Manager is responsible for:&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<ul style=\"margin-left: 80px;\">\r\n	<li>\r\n		Administering this temporary layoff from employment policy;</li>\r\n	<li>\r\n		Complying with collective agreement provisions for bargaining unit employees;</li>\r\n	<li>\r\n		Following the temporary layoff procedures;</li>\r\n	<li>\r\n		Identifying the need for a temporary layoff;</li>\r\n	<li>\r\n		Ensuring proper assessment of applicable legal and collective agreement implications;</li>\r\n	<li>\r\n		Selecting the positions, teams or departments that will be affected by the temporary layoff according to established criteria;</li>\r\n	<li>\r\n		Ensuring that no discrimination has taken place and that there won&rsquo;t be an adverse impact on protected groups;</li>\r\n	<li>\r\n		Consulting with legal counsel to ensure the legality of the process;</li>\r\n	<li>\r\n		Reviewing and revising this policy as appropriate; and</li>\r\n	<li>\r\n		Providing policy interpretations and advice when required.<br />\r\n		<br />\r\n		&nbsp;</li>\r\n</ul>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"lay-cont1\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>5</strong></td>\r\n			<td>\r\n				<strong>DEFINITIONS</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.01</td>\r\n			<td>\r\n				<strong>&ldquo;Layoff&rdquo;</strong> means a temporary interruption of the employment relationship at the direction of the employer because of a lack of work.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.02</td>\r\n			<td>\r\n				<strong>&ldquo;Pay in lieu of notice&rdquo;</strong> means payment made to an employee in lieu of having the employee work during the notice period.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.03</td>\r\n			<td>\r\n				<strong>&ldquo;Personal file&rdquo;</strong> means the official employer file that contains information relevant to individual employees and their employment. There is only one personal file.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.04</td>\r\n			<td>\r\n				&ldquo;<strong>Resignation&rdquo;</strong> means a cessation of employment that is initiated by an employee, without duress, and includes voluntary retirement.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.05</td>\r\n			<td>\r\n				<strong>&ldquo;Temporary layoff&rdquo;</strong> under the Employment Standards Act means:</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<ul style=\"margin-left: 80px;\">\r\n	<li>\r\n		A period not exceeding six consecutive days; or</li>\r\n	<li>\r\n		Where there is lack of work due to any reason unforeseen by the employer and the lack of work continues due to that same reason.<br />\r\n		<br />\r\n		&nbsp;</li>\r\n</ul>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"lay-cont2\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>6</strong></td>\r\n			<td>\r\n				<strong>REFERENCES and RELATED STATEMENTS of POLICY and PROCEDURE</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<ul style=\"margin-left: 40px;\">\r\n	<li>\r\n		Employment Standards Act and Regulations (New Brunswick)</li>\r\n	<li>\r\n		SPP HR 2.05.NB &mdash; Employment Classifications</li>\r\n	<li>\r\n		SPP HR 2.09.NB &mdash; Termination of Employment<br />\r\n		<br />\r\n		&nbsp;</li>\r\n</ul>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"lay-cont3\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>7</strong></td>\r\n			<td>\r\n				<strong>PROCEDURE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.01</td>\r\n			<td>\r\n				Temporary layoff may be for a duration of up to six consecutive days, or where there is lack of work due to any reason unforeseen by the employer and the lack of work continues due to that same reason. <em>[If the employee is covered by a collective agreement, the maximum length of a layoff is the period of time during which the employee has the right to be recalled; consult your collective agreement.]</em><br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.02</td>\r\n			<td>\r\n				During the period of temporary layoff, employees may continue to receive the residence&rsquo;s benefits. The affected benefits and how costs will be assumed will be communicated to affected employees before the layoff commences (see paragraph 7.04).<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.03</td>\r\n			<td>\r\n				Although the <em>Employment Standards </em>Act does not require written notice if the layoff does not exceed six days or if there is a lack of work due to any unforeseen reason, the residence will give employees written notice of a temporary layoff as follows:</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"1\" cellpadding=\"1\" cellspacing=\"1\" class=\"lay-cont4\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>Employment Period</strong></td>\r\n			<td>\r\n				<strong>Notice Required</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Less than 6 months</td>\r\n			<td>\r\n				No notice required</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				6 months but less than 5 years</td>\r\n			<td>\r\n				2 weeks</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5 years or more</td>\r\n			<td>\r\n				4 weeks</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	<em>[Notice is not required where the layoff results from the normal seasonal reduction, closure or suspension of an operation.]</em></p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"lay-cont5\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				7.04</td>\r\n			<td>\r\n				The temporary layoff notice will explicitly state that the layoff will be temporary, explain why the layoff is happening, set the duration of the temporary layoff period as well as the period during which the employee may be recalled to resume work, and outline any other rights (i.e., access to SUB plan or vacation pay), obligations and benefits allotted to the employee during the layoff period.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.05</td>\r\n			<td>\r\n				Human Resources or the Manager will ask the employee to return to work on or before the end of the six-day period, or whatever other period is allowed as per paragraph 7.01 herein if certain conditions are met (see definition of temporary layoff), and within 7 days from the date the recall notice was served to the employee. The employee will receive a recall letter indicating when the employee is being recalled to work and the period to access or refuse such recall. Employees will be recalled according to the needs of the residence, the employee&rsquo;s classification and his or her ability to perform the job. Notice of recall will be sent by email or registered mail or both, with a return receipt requested, to the employee&rsquo;s home address on record. Unless an employee responds to the recall notice within 7 days following receipt of the notice or its attempted delivery, the employee&rsquo;s name will be removed from the recall list and the employee will no longer have any job rights with the residence.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.06</td>\r\n			<td>\r\n				If the residence recalls a laid off employee within the layoff period, there are three possibilities:</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<ul style=\"margin-left: 80px;\">\r\n	<li>\r\n		The employee returns to work for the residence in the same or a similar position;</li>\r\n	<li>\r\n		The employee does not wish to return and officially resigns by providing a written resignation letter; or</li>\r\n	<li>\r\n		The employee does not respond to the recall notice and is considered to have resigned.</li>\r\n</ul>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"lay-cont6\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				In any case, employees should formally accept or refuse to return to work within 7 days after receiving a recall request. If an employee finds another permanent job during the temporary layoff [or recall] period, he or she must notify his or her Manager or Human Resources as soon as reasonably possible. In this case, the employee loses the right to be recalled to resume work.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				In cases where the laid off employee resumes work, his or her temporary layoff period will end and the employee won&rsquo;t be eligible for termination pay.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	<br />\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"lay-cont7\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				7.07</td>\r\n			<td>\r\n				If the residence does not recall a laid off employee within the temporary layoff [or recall] period, the residence will inform the employee with a letter that his or her employment is being terminated. The termination process will begin as per the termination policy. The residence will pay the employee what the law requires (e.g., notice or pay in lieu of notice in accordance with employment standards) from the first day of termination. The first day of an employee&rsquo;s termination is the first day of his or her layoff, unless applicable law dictates otherwise.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.08</td>\r\n			<td>\r\n				If employees have any questions, they should contact their Manager or Human Resources.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', 'en'),
(82, 81, 'Exit Interviews', '<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"table-inteview\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>1</strong></td>\r\n			<td>\r\n				<strong>POLICY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.01</td>\r\n			<td>\r\n				The residence will conduct a confidential exit interview with every employee voluntarily leaving the employ of the Company or retiring. Employees who are being terminated involuntarily will have exit interviews only if they request one.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				1.02</td>\r\n			<td>\r\n				Whenever possible, exit interviews will be held during the employee&rsquo;s final week of employment with a member of the management staff.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>2</strong></td>\r\n			<td>\r\n				<strong>PURPOSE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				2.01</td>\r\n			<td>\r\n				This policy is designed to assist employees to obtain information about the separation and to assist the residence to gain insight and data about employees&rsquo; work experiences.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>3</strong></td>\r\n			<td>\r\n				<strong>SCOPE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				3.01</td>\r\n			<td>\r\n				This policy applies to all employees.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>4</strong></td>\r\n			<td>\r\n				<strong>RESPONSIBILITY</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.01</td>\r\n			<td>\r\n				Managers are responsible for scheduling an exit interview for employees who are voluntarily leaving or retiring from employment with the residence, or those being terminated who request an exit interview.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				4.02</td>\r\n			<td>\r\n				Employees who are terminating their employment with the residence are responsible for returning all company supplied equipment, customer lists or files, and other company property in their possession to their Supervisors on or prior to the last day of work.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>5</strong></td>\r\n			<td>\r\n				<strong>DEFINITIONS</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.01</td>\r\n			<td>\r\n				&ldquo;<strong>Voluntary termination&rdquo; </strong>means a cessation of employment which is initiated by an employee, without duress, and includes resignation and retirement.<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				5.02</td>\r\n			<td>\r\n				<strong>&ldquo;Involuntary termination&rdquo;</strong> means a cessation of employment initiated by the residence, at any time, in its sole discretion.&nbsp;<br />\r\n				<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>6</strong></td>\r\n			<td>\r\n				<strong>REFERENCES and RELATED STATEMENTS of POLICY and PROCEDURE</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<ul style=\"margin-left: 80px;\">\r\n	<li>\r\n		SPP HR 2.09.NB &mdash; Termination of Employment</li>\r\n	<li>\r\n		SPP HR 2.10.NB &mdash; Third Party Reference Requests</li>\r\n	<li>\r\n		SPP HR 5.07.NB &mdash; Personal Information Protection</li>\r\n</ul>\r\n<p>\r\n	<br />\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"interview-cont\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<strong>7</strong></td>\r\n			<td>\r\n				<strong>PROCEDURE</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.01</td>\r\n			<td>\r\n				An exit interview should be scheduled during the employee&rsquo;s final week of employment, whenever possible, in conjunction with a member of the Human Resources department.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.02</td>\r\n			<td>\r\n				The person conducting the exit interview shall,</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"interview-cont1\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(a)</td>\r\n			<td>\r\n				conduct the interview in an open and honest manner and shall prepare a confidential summary of the employee&rsquo;s comments related to the employee&rsquo;s experiences with the residence,</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(b)</td>\r\n			<td>\r\n				confirm the employee&rsquo;s address or forwarding address and telephone number and provide the exiting employee with information regarding final pay, continuation of benefits, if any, company policies on confidentiality, non-competition, references and re-hiring, and</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				(c)</td>\r\n			<td>\r\n				reclaim any company property in the employee&rsquo;s possession or establish a schedule for the return of company property on or prior to the employee&rsquo;s last day at the workplace.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" class=\"interview-cont2\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				7.03</td>\r\n			<td>\r\n				Data obtained from an employee during an exit interview and any written record of the interview is confidential. No data may be disclosed to any person unless the data is in a form that does not identify the employee.<br />\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				7.04</td>\r\n			<td>\r\n				Human Resources staff shall analyse data from exit interviews [<span style=\"color:#ff8c00;\">quarterly</span>], and shall report the findings to the General Manager.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', 'en'),
(84, 83, ' Sick Days', '<p>\r\n	New Brunswick: Under the<span style=\"color:#0000cd;\"><u><em> New Brunswick Employment Standards Act</em></u></span>, 5 unpaid days of leave in a calendar year for sick leave after 90 days of employment and 3 unpaid days of leave in a calendar year for family care responsibility leave.</p>\r\n', 'en'),
(86, 85, 'Employee Wellness', '<p>\r\n	<strong>Policy brief &amp; purpose</strong></p>\r\n<p>\r\n	Our employee wellness program policy describes our company&rsquo;s wellness initiatives that promote employee health. We want our employees to have access to wellness resources and a personalized wellness plan.</p>\r\n<p>\r\n	Employee wellness programs have many benefits. Wellness helps people become more productive. It can reduce the number of sick days employees take and various sickness-related expenses and accidents. But, most importantly, employee wellness is a vital aspect of building a happier workplace.<br />\r\n	&nbsp;</p>\r\n<p>\r\n	<strong>Scope</strong><br />\r\n	<br />\r\n	This employee wellness program policy applies to all our employees. We may offer our wellness program as part of a group health plan or separately.<br />\r\n	&nbsp;</p>\r\n<p>\r\n	<strong>Policy elements</strong><br />\r\n	<br />\r\n	Our company provides a wellness program that promotes employee health and disease prevention. Each employee can have a personalized wellness plan and a variety of wellness resources.</p>\r\n<p>\r\n	<strong>What is a wellness program for employees?</strong><br />\r\n	Our wellness program has several goals. For example, we want to help our employees:</p>\r\n<ul>\r\n	<li>\r\n		Lose weight<br />\r\n		&nbsp;</li>\r\n	<li>\r\n		Quit smoking<br />\r\n		&nbsp;</li>\r\n	<li>\r\n		Manage stress<br />\r\n		&nbsp;</li>\r\n	<li>\r\n		Lower blood pressure or cholesterol levels<br />\r\n		&nbsp;</li>\r\n	<li>\r\n		Improve their physical strength and stamina</li>\r\n</ul>\r\n<p>\r\n	This list isn&rsquo;t exhaustive. We aim to promote every outcome that will make our employees healthier. Some training or courses aim to prevent occupational accidents and promote correct use of equipment and material on the job. These fall under the purview of our workplace health and safety policy.<br />\r\n	<br />\r\n	Wellness resources include any kind of information, advice, activity, facility, equipment and membership that promotes employee health (physical, emotional and psychological) and fitness.</p>\r\n<p>\r\n	As part of our wellness program, we may use third-party vendors such as gyms, wellness centers, coaches, physicians and health education providers. Physicians or health experts may ask employees to answer assessment questions and take biometric screenings to determine their health risk and help them follow a suitable program. Questions and screenings are voluntary and regulated.</p>\r\n<p>\r\n	We may also host competitions among our employees, like 8-week walking challenge. Participating in these contests is also voluntary.<br />\r\n	&nbsp;</p>\r\n<p>\r\n	<strong>Team leaders&rsquo; responsibilities</strong><br />\r\n	Team leaders should work with the HR department to communicate our wellness initiatives to their team members. They should tell their team members:</p>\r\n<ul>\r\n	<li>\r\n		That our company offers a wellness program<br />\r\n		&nbsp;</li>\r\n	<li>\r\n		How and when they can use our wellness resources<br />\r\n		&nbsp;</li>\r\n	<li>\r\n		That they can get personalized wellness plans<br />\r\n		&nbsp;</li>\r\n	<li>\r\n		Who employees can refer to for more details</li>\r\n</ul>\r\n<p>\r\n	We advise team leaders to encourage their team members to participate and be open to discuss any concerns when needed.</p>\r\n<p>\r\n	<strong>Wellness Incentives</strong><br />\r\n	We want to encourage our employees to participate in our wellness programs, so we&rsquo;ll provide employee wellness program incentives. We&rsquo;ll also offer rewards for employees who achieve their wellness objectives, as they&rsquo;re formed in their personalized wellness plan. Incentives and rewards may be:</p>\r\n<ul>\r\n	<li>\r\n		Cash<br />\r\n		&nbsp;</li>\r\n	<li>\r\n		Time off<br />\r\n		&nbsp;</li>\r\n	<li>\r\n		Reductions in insurance premiums<br />\r\n		&nbsp;</li>\r\n	<li>\r\n		Other gifts and awards</li>\r\n</ul>\r\n<p>\r\n	Incentives and rewards may be regulated by law. We&rsquo;ll follow legal limitations at all times.</p>\r\n<p>\r\n	<strong>Employees with Disabilities</strong><br />\r\n	We can make reasonable accommodations for our employees with disabilities to help them have equal access to our wellness program. Our HR department will consult with physicians and wellness experts to help our employees with disabilities have a suitable wellness plan.</p>\r\n<p>\r\n	The same applies to employees who can&rsquo;t participate in certain wellness activities due to age, pregnancy or other reason. We want everyone to have access to wellness plans and resources they can use. We&rsquo;ll also pay any wellness incentives to all our employees who participate in our wellness programs without discriminating against protected characteristics.</p>\r\n<p>\r\n	We encourage our employees to reach out to our HR department. They can explain their situation and discuss options. These discussions and any relevant information will be kept confidential.</p>\r\n<p>\r\n	<strong>Legality of wellness program</strong><br />\r\n	Our company will handle any health insurance and wellness plans with attention to relevant legal guidelines.</p>\r\n<p>\r\n	<strong>Genetic information and disability</strong><br />\r\n	Confidentiality and respect to our employees&rsquo; rights are important to us. We won&rsquo;t:</p>\r\n<ul>\r\n	<li>\r\n		use any genetic information and disability status to disadvantage our employees in any way.</li>\r\n	<li>\r\n		use wellness incentives in exchange for information on our employees&rsquo; health condition or that of their family.</li>\r\n	<li>\r\n		try to coerce employees into providing health information or taking medical examinations.</li>\r\n</ul>\r\n<p>\r\n	We will let employees know what health information we need for our wellness program, who will be able to see it and why. All data will be kept confidential and our company will be able to access aggregated data.<br />\r\n	<br />\r\n	<br />\r\n	<strong>Voluntary participation</strong><br />\r\n	We encourage employees to participate in our wellness program but their participation is voluntary . There won&rsquo;t be any punishment or adverse action for employees who choose not to use our wellness resources and program.</p>\r\n<p>\r\n	<strong>Incentives</strong><br />\r\n	Any incentives that encourage employees to participate in our wellness programs will always be within legal guidelines. We&rsquo;ll give the same incentive to all employees who participate in our wellness program, regardless of disability or health risk</p>\r\n<p>\r\n	<strong>Design</strong><br />\r\n	Our wellness program will be designed with employee health in mind. It won&rsquo;t be unpleasant, too time-consuming or require heavy spending by our employees. We can create personalized wellness plans for each employee. We always welcome ideas and suggestions for our employee wellness program.<br />\r\n	.</p>\r\n<p>\r\n	&nbsp;</p>\r\n', 'en'),
(87, 86, 'Form-ORIENTATION GUIDE (PSW) - MEDICATIONS', '<p>\r\n	&nbsp;</p>\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" style=\"width: 500px\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				Employee Name:</td>\r\n			<td class=\"employee0\">\r\n				&nbsp;</td>\r\n			<td>\r\n				Training Date:</td>\r\n			<td class=\"employee01\">\r\n				&nbsp;</td>\r\n			<td>\r\n				20</td>\r\n			<td class=\"emp02\">\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Name of Trainer:</td>\r\n			<td class=\"employee03\">\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n<table border=\"1\" cellpadding=\"1\" cellspacing=\"1\" class=\"table-medication\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				Employee</td>\r\n			<td>\r\n				Trainer</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Only PSW Medications staff can administer medications</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Follow the policies and procedures for administering medications</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<span avenir=\"\" data-doc-id=\"1067983000000026001\" data-doc-type=\"writer\" font-size:=\"\" lt=\"\" next=\"\" style=\"font-family: \">Distribution of the medications with the bubble packs</span></td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Medications must be prescribed by a doctor</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Administering medications-Oral (mouth)</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Administering medication- Nasal (nose)</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Administering medication -Eyes&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Administering medication-Auditory (ears)</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Administering medication-Rectum (suppository)</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Administering medication-Topical (on the skin)</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				L&rsquo;administration des m&eacute;dicaments en suspension (liquide)</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Administering Coumadin</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Administering Fosamax/Actonel&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Preparing injections</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Utilisation des inhalateurs (puffers)</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Revise and use the sheet &laquo; Day List or Night List &raquo;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				PRN medication administration (Tylenol, Gravol, Immodium, etc.)</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Administer PRN medications (as needed), verify last dosage</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Required signatures when you administer PRN (date, time, signature)</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Follow narcotic rules and regulations</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Antibiotics must be started as soon as possible</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>File Documentation</strong></td>\r\n			<td class=\"file-doccumentation\" style=\"text-align: center;\">\r\n				-------</td>\r\n			<td style=\"text-align: center;\">\r\n				-------</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Write pertinent notes in the communication book</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Sign for medications administered as well as PRN (date, time and signature)</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				If nothing is written, nothing was done!</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Resident book- know how it is organized</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<strong>Minor treatment and sampling</strong></td>\r\n			<td style=\"text-align: center;\">\r\n				-------</td>\r\n			<td style=\"text-align: center;\">\r\n				-------</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Vital signs (BP, Pulse, SpO2, Temp)</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				How the saturometer works and when to use it</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Nasal canula installation&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n			<td>\r\n				&nbsp;</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				Use of